<script>
/* --------------- Global constants and data --------------- */
const nu0 = 1000.0;               // band origin (cm-1)
const J_MAX = 60;                // maximum J
const K_MAX = 5;                 // maximum K
const zeta = 0.2;                // Coriolis coupling (cm-1)
const T = 300;                   // Temperature (K)
const k_B = 0.695;               // Boltzmann constant in cm-1/K

// Pre-generate all possible lines
let allLines = []; // each line: {x, y, K, subBand, branch, label}
function generateLines() {
    allLines = [];
    for (let K=0; K<=K_MAX; K++) {
        ['positive', 'negative'].forEach(sub => {
            ['P','Q','R'].forEach(branch => {
                for (let J=0; J<=J_MAX; J++) {
                    // simple frequency model
                    let deltaK = sub==='positive'?1:-1;
                    let freq = nu0 + 0.2*J + 0.05*K*deltaK + (Math.random()-0.5)*0.05;
                    // intensity: rotational weighting + Boltzmann
                    let intensity = (J+1) * Math.exp(-k_B*0.05*J*(J+1)/T);
                    intensity = Math.min(intensity,1); // keep within 0-1
                    allLines.push({
                        x:freq,
                        y:intensity,
                        K:K,
                        subBand:sub,
                        branch:branch,
                        label:`K=${K}, ${sub} K, branch ${branch}`
                    });
                }
            });
        });
    }
}
generateLines();

/* --------------- Helper functions --------------- */
// Update displayed slider values and ensure logical bounds
function updateSliderValues() {
    const lower = parseFloat(sliderXLower.value);
    const upper = parseFloat(sliderXUpper.value);
    if (lower >= upper) {
        sliderXUpper.value = lower + 1;
    } else if (upper <= lower) {
        sliderXLower.value = upper - 1;
    }
    spanXLowerValue.textContent = sliderXLower.value;
    spanXUpperValue.textContent = sliderXUpper.value;
}

// Filter lines based on current UI selections
function getFilteredLines() {
    const view = viewFull.checked ? 'full' : 'sub';
    let filtered = allLines;
    if (view === 'full') {
        // all lines, ignore K/subband, color black
        filtered = allLines;
    } else {
        const Ksel = parseInt(kSelectorGroup.querySelector('input:checked').value);
        const subsel = subbandTypeGroup.querySelector('input:checked').value;
        filtered = allLines.filter(l=>l.K===Ksel && l.subBand===subsel);
        if (subsel !== 'full') {
            const branSel = branchSelectorGroup.querySelector('input:checked').value;
            if (branSel !== 'all') {
                filtered = filtered.filter(l=>l.branch===branSel);
            }
        }
    }
    return filtered;
}

// Build Plotly data/traces
function buildPlotData() {
    const lines = getFilteredLines();
    const view = viewFull.checked ? 'full' : 'sub';
    const data = [];
    if (view === 'full') {
        // single trace, all black
        data.push({
            x: lines.map(l=>l.x),
            y: lines.map(l=>l.y),
            type:'bar',
            width: 0.1,
            marker:{color: 'black'},
            showlegend:false
        });
    } else {
        // separate traces for positive (red) and negative (blue)
        const posLines = lines.filter(l=>l.subBand==='positive');
        const negLines = lines.filter(l=>l.subBand==='negative');
        if (posLines.length>0) {
            data.push({
                x: posLines.map(l=>l.x),
                y: posLines.map(l=>l.y),
                type:'bar',
                width: 0.1,
                marker:{color: 'red'},
                showlegend:false
            });
        }
        if (negLines.length>0) {
            data.push({
                x: negLines.map(l=>l.x),
                y: negLines.map(l=>l.y),
                type:'bar',
                width: 0.1,
                marker:{color: 'blue'},
                showlegend:false
            });
        }
    }
    return data;
}

function setPlotTitle() {
    const view = viewFull.checked ? 'full' : 'sub';
    if (view==='full') {
        plotLayout.title.text='Rovibronic Spectrum';
    } else {
        const Ksel = parseInt(kSelectorGroup.querySelector('input:checked').value);
        const subsel = subbandTypeGroup.querySelector('input:checked').value;
        let title = `K = ${Ksel} sub-band`;
        if (subsel!=='full') {
            title += `\n"${subsel.charAt(0).toUpperCase()+subsel.slice(1)}" sub-band`;
        }
        plotLayout.title.text=title;
    }
}

// Draw the plot
let plotLayout = {
    title: {text:'Rovibronic Spectrum'},
    xaxis: {title:'wavenumber (cm⁻¹)', range:[902,1091]},
    yaxis: {title:'absorbance', range:[0,1.2]},
    showlegend:false,
    bargap:0
    // more layout options could be added
};
Plotly.newPlot('plot-div', buildPlotData(), plotLayout, {displayModeBar:false});

/* --------------- Event listeners --------------- */
const viewFull = document.getElementById('radio-view-full');
const viewSub = document.getElementById('radio-view-sub');
const kSelectorGroup = document.getElementById('k-selector-group');
const subbandTypeGroup = document.getElementById('subband-type-group');
const branchSelectorGroup = document.getElementById('branch-selector-group');
const branchInputs = branchSelectorGroup.querySelectorAll('input[name="branch-selector"]');
const subbandInputs = subbandTypeGroup.querySelectorAll('input[name="subband-type"]');
const sliderXLower = document.getElementById('slider-x-lower');
const sliderXUpper = document.getElementById('slider-x-upper');
const spanXLowerValue = document.getElementById('span-x-lower-value');
const spanXUpperValue = document.getElementById('span-x-upper-value');
const kRadioButtons = kSelectorGroup.querySelectorAll('input[name="k-selector"]');

// View selector changes
viewFull.addEventListener('change', ()=>{
    if (!viewFull.checked) return;
    // disable subband controls
    kSelectorGroup.classList.add('disabled');
    subbandTypeGroup.classList.add('disabled');
    branchSelectorGroup.style.display = 'none';
    Plotly.react('plot-div', buildPlotData(), plotLayout);
    setPlotTitle();
});
viewSub.addEventListener('change', ()=>{
    if (!viewSub.checked) return;
    // enable subband controls
    kSelectorGroup.classList.remove('disabled');
    subbandTypeGroup.classList.remove('disabled');
    // branch selector visibility depends on subband type
    const subsel = subbandTypeGroup.querySelector('input:checked').value;
    branchSelectorGroup.style.display = (subsel==='full'?'none':'flex');
    Plotly.react('plot-div', buildPlotData(), plotLayout);
    setPlotTitle();
});

// K selection changes
kRadioButtons.forEach(rb=>{
    rb.addEventListener('change', ()=>{
        const Ksel = parseInt(kSelectorGroup.querySelector('input:checked').value);
        // disable negative subband option when K=0
        const negInput = subbandTypeGroup.querySelector('#radio-subband-negative');
        if (Ksel===0) {
            negInput.disabled = true;
            if (negInput.checked) negInput.checked=false;
        } else {
            negInput.disabled = false;
        }
        Plotly.react('plot-div', buildPlotData(), plotLayout);
        setPlotTitle();
    });
});

// Subband type changes
subbandInputs.forEach(rb=>{
    rb.addEventListener('change', ()=>{
        const subsel = subbandTypeGroup.querySelector('input:checked').value;
        branchSelectorGroup.style.display = (subsel==='full'?'none':'flex');
        Plotly.react('plot-div', buildPlotData(), plotLayout);
        setPlotTitle();
    });
});

// Branch selection changes
branchInputs.forEach(rb=>{
    rb.addEventListener('change', ()=>{
        Plotly.react('plot-div', buildPlotData(), plotLayout);
        setPlotTitle();
    });
});

// Slider changes
function redrawWithNewRange() {
    const lower = parseFloat(sliderXLower.value);
    const upper = parseFloat(sliderXUpper.value);
    Plotly.relayout('plot-div', {'xaxis.range':[lower,upper]});
}
sliderXLower.addEventListener('input', ()=>{
    updateSliderValues();
    redrawWithNewRange();
});
sliderXUpper.addEventListener('input', ()=>{
    updateSliderValues();
    redrawWithNewRange();
});
</script>
</body>
