  const btnP = document.getElementById('btn-p');
  const btnSV = document.getElementById('btn-sv');
  const btnSH = document.getElementById('btn-sh');
  const checkboxBox = document.getElementById('checkbox-box');

  // State
  let currentWaveType = 'P';
  const degToRad = Math.PI / 180;

  // Scene setup
  const scene = new THREE.Scene();
  scene.background = new THREE.Color(0xf0f0f0);

  const camera = new THREE.PerspectiveCamera(45, canvasContainer.clientWidth / canvasContainer.clientHeight, 0.1, 1000);
  camera.position.set(0, 0, 5);
  camera.lookAt(0, 0, 0);

  const renderer = new THREE.WebGLRenderer({ antialias: true});
  renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
  canvasContainer.appendChild(renderer.domElement);

  const controls = new OrbitControls(camera, renderer.domElement);
  controls.enableDamping = true;
  controls.dampingFactor = 0.05;

  // Lighting
  const ambient = new THREE.AmbientLight(0x404040);
  scene.add(ambient);
  const dirLight1 = new THREE.DirectionalLight(0xffffff, 0.6);
  dirLight1.position.set(1,1,1);
  scene.add(dirLight1);
  const dirLight2 = new THREE.DirectionalLight(0xffffff, 0.4);
  dirLight2.position.set(-1,-1,-1);
  scene.add(dirLight2);

  // Parent object for rotations
  const radiationParent = new THREE.Object3D();
  scene.add(radiationParent);

  // Bounding box helper
  let boxHelper = null;

  // Arrow helper (U)
  const arrowHelper = new THREE.ArrowHelper(new THREE.Vector3(0,0,1), new THREE.Vector3(0,0,0), 1.5, 0x000000);
  scene.add(arrowHelper);
  const uSprite = makeTextSprite('U', { fontsize: 24, borderColor: {r:0,g:0,b:0,a:1}, bgColor: {r:255,g:255,b:255,a:0} });
  uSprite.position.copy(arrowHelper.position.clone().add(arrowHelper.getDirection().clone().multiplyScalar(arrowHelper.length + 0.2)));
  scene.add(uSprite);

  // Compass
  const compass = createCompass();
  scene.add(compass);

  // Current radiation mesh
  let radiationMesh = null;
  function createRadiationMesh(type) {
    const paramFunc = function (u, v, target) {
      const phi = u * Math.PI * 2;      // [0, 2π]
      const theta = v * Math.PI;        // [0, π]
      let r;
      if (type === 'P') {
        r = Math.abs(Math.sin(2 * theta) * Math.sin(phi));
      } else if (type === 'SV') {
        r = Math.abs(Math.cos(2 * theta) * Math.sin(phi));
      } else { // SH
        r = Math.abs(Math.cos(theta) * Math.cos(phi));
      }
      const x = r * Math.sin(theta) * Math.cos(phi);
      const y = r * Math.sin(theta) * Math.sin(phi);
      const z = r * Math.cos(theta);
      target.set(x, y, z);
    };
    const geometry = new THREE.ParametricGeometry(paramFunc, 100, 60);
    const material = new THREE.MeshStandardMaterial({ color: 0xD2B48C, side: THREE.DoubleSide, roughness: 0.5, metalness: 0.1});
    const mesh = new THREE.Mesh(geometry, material);
    mesh.scale.set(1.5, 1.5, 1.5); // enlarge for visibility
    return mesh;
  }

  function updateRadiationMesh() {
    if (radiationMesh) {
      radiationParent.remove(radiationMesh);
      radiationMesh.geometry.dispose();
      radiationMesh.material.dispose();
      radiationMesh = null;
    }
    radiationMesh = createRadiationMesh(currentWaveType);
    radiationParent.add(radiationMesh);

    if (boxHelper) {
      scene.remove(boxHelper);
      boxHelper.dispose(); // BoxHelper inherits from Mesh, will dispose geometry and material
    }
    boxHelper = new THREE.BoxHelper(radiationParent, 0x000000);
    boxHelper.visible = checkboxBox.checked;
    scene.add(boxHelper);
  }

  // initial create
  updateRadiationMesh();
  updateRotation();

  // Event listeners
  function updateRotation() {
    const strike = parseFloat(strikeSlider.value) * degToRad;
    const dip = parseFloat(dipSlider.value) * degToRad;
    const rake = parseFloat(rakeSlider.value) * degToRad;
    radiationParent.rotation.set(0,0,0);
    radiationParent.rotateZ(strike);
    radiationParent.rotateX(dip);
    radiationParent.rotateY(rake);
  }

  strikeSlider.addEventListener('input', () => {
    strikeVal.textContent = strikeSlider.value;
    updateRotation();
  });
  dipSlider.addEventListener('input', () => {
    dipVal.textContent = dipSlider.value;
    updateRotation();
  });
  rakeSlider.addEventListener('input', () => {
    rakeVal.textContent = rakeSlider.value;
    updateRotation();
  });

  function setActiveButton(btn) {
    [btnP, btnSV, btnSH].forEach(b => b.classList.remove('active'));
    btn.classList.add('active');
  }
  btnP.addEventListener('click', () => { currentWaveType='P'; setActiveButton(btnP); updateRadiationMesh(); updateRotation(); });
  btnSV.addEventListener('click', () => { currentWaveType='SV'; setActiveButton(btnSV); updateRadiationMesh(); updateRotation(); });
  btnSH.addEventListener('click', () => { currentWaveType='SH'; setActiveButton(btnSH); updateRadiationMesh(); updateRotation(); });

  checkboxBox.addEventListener('change', () => {
    if (boxHelper) boxHelper.visible = checkboxBox.checked;
  });

  window.addEventListener('resize', () => {
    const width = canvasContainer.clientWidth;
    const height = canvasContainer.clientHeight;
    renderer.setSize(width, height);
    camera.aspect = width / height;
    camera.updateProjectionMatrix();
  });

  // animation loop
  function animate() {
    requestAnimationFrame(animate);
    controls.update();
    renderer.render(scene, camera);
  }
  animate();

  // Helper functions
  function makeTextSprite(message, parameters) {
    const fontface = parameters?.fontface || 'Arial';
    const fontsize = parameters?.fontsize || 18;
    const borderThickness = parameters?.borderThickness || 4;
    const borderColor = parameters?.borderColor || { r:0, g:0, b:0, a:1 };
    const bgColor = parameters?.bgColor || { r:255, g:255, b:255, a:0.8 };

    const canvas = document.createElement('canvas');
    const context = canvas.getContext('2d');
    context.font = fontsize + 'px ' + fontface;

    const metrics = context.measureText(message);
    const textWidth = metrics.width;

    context.fillStyle = 'rgba(' + bgColor.r + ',' + bgColor.g + ',' + bgColor.b + ',' + bgColor.a + ')';
    context.fillRect(0, 0, textWidth + borderThickness * 2, fontsize + borderThickness * 2);
    context.strokeStyle = 'rgba(' + borderColor.r + ',' + borderColor.g + ',' + borderColor.b + ',' + borderColor.a + ')';
    context.lineWidth = borderThickness;
    context.strokeRect(0, 0, textWidth + borderThickness * 2, fontsize + borderThickness * 2);

    context.fillStyle = 'rgba(0,0,0,1)';
    context.textAlign = 'center';
    context.textBaseline = 'middle';
    context.fillText(message, textWidth / 2 + borderThickness, fontsize / 2 + borderThickness);

    const texture = new THREE.Texture(canvas);
    texture.needsUpdate = true;
    const spriteMaterial = new THREE.SpriteMaterial({ map: texture, transparent: true });
    const sprite = new THREE.Sprite(spriteMaterial);
    sprite.scale.set(0.5, 0.25, 1);
    return sprite;
  }

  function createCompass() {
    const compassGroup = new THREE.Group();

    // circle
    const circleGeometry = new THREE.RingGeometry(1.5, 1.6, 64);
    const circleMaterial = new THREE.MeshBasicMaterial({ color: 0x777777, side: THREE.DoubleSide, transparent: true, opacity: 0.3 });
    const circleMesh = new THREE.Mesh(circleGeometry, circleMaterial);
    compassGroup.add(circleMesh);

    // cardinal points
    const positions = [
      { angle: 0,   label: 'N' },
      { angle: Math.PI / 2, label: 'E' },
      { angle: Math.PI, label: 'S' },
      { angle: -Math.PI / 2, label: 'W' }
    ];
    positions.forEach(pos => {
      const sprite = makeTextSprite(pos.label, { fontsize: 18, borderColor: {r:0,g:0,b:0,a:1}, bgColor: {r:255,g:255,b:255,a:0.8} });
      const radius = 1.7;
      const x = radius * Math.sin(pos.angle);
      const y = radius * Math.cos(pos.angle);
      sprite.position.set(x, y, 0.01);
      compassGroup.add(sprite);
    });

    // degree markings
    for (let deg = 30; deg < 360; deg += 30) {
      const rad = deg * Math.PI / 180;
      const sprite = makeTextSprite(`${deg}°`, { fontsize: 12, borderColor:{r:0,g:0,b:0,a:1}, bgColor:{r:255,g:255,b:255,a:0.8} });
      const radius = 1.8;
      const x = radius * Math.sin(rad);
      const y = radius * Math.cos(rad);
      sprite.position.set(x, y, 0.01);
      compassGroup.add(sprite);
    }

    compassGroup.position.set(0, 0, 0);
    return compassGroup;
  }
}
</script>
</body>
</html>
