      <input type="range" id="slider-terms" min="2" max="50" step="1" value="10">
      <span id="span-terms-value">10</span>
    </div>

    <div class="control-group">
      <label>Repeated Euler transformations</label>
      <div>
        <input type="radio" id="radio-euler-0" name="euler-transformations" value="0" checked>
        <label for="radio-euler-0">0</label>
        <input type="radio" id="radio-euler-1" name="euler-transformations" value="1">
        <label for="radio-euler-1">1</label>
        <input type="radio" id="radio-euler-2" name="euler-transformations" value="2">
        <label for="radio-euler-2">2</label>
        <input type="radio" id="radio-euler-3" name="euler-transformations" value="3">
        <label for="radio-euler-3">3</label>
      </div>
    </div>

