<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
<script>
/* =========================
   Global variables
   ========================= */
const canvasSize = 512;
const secretSize = 64;           // size of secret image
const shareSize = secretSize * 2; // each share is 2x size
let share1 = null, share2 = null;

// Current offsets of the shares on canvas
let shareX1 = 0, shareY1 = 0, shareX2 = 0, shareY2 = 0;

// DOM references
const handle1 = document.getElementById('joystick-handle1');
const handle2 = document.getElementById('joystick-handle2');
const resetBtn = document.getElementById('btn-reset');

/* =========================
   Helper functions
   ========================= */
function setHandlePercent(handle, percentX, percentY) {
  const container = handle.parentNode;
  const rect = container.getBoundingClientRect();
  const w = rect.width;
  const h = rect.height;
  const halfHandle = handle.offsetWidth / 2;
  const centerX = percentX * w;
  const centerY = percentY * h;
  handle.style.left = (centerX - halfHandle) + 'px';
  handle.style.top = (centerY - halfHandle) + 'px';
}

function getPercentFromHandle(handle) {
  const container = handle.parentNode;
  const rect = container.getBoundingClientRect();
  const w = rect.width;
  const h = rect.height;
  const halfHandle = handle.offsetWidth / 2;
  const left = parseFloat(handle.style.left) || 0;
  const top = parseFloat(handle.style.top) || 0;
  const centerX = left + halfHandle;
  const centerY = top + halfHandle;
  return {x: centerX / w, y: centerY / h};
}

function updateShareFromHandles() {
  const p1 = getPercentFromHandle(handle1);
  const p2 = getPercentFromHandle(handle2);
  const maxOffset = canvasSize - shareSize;
  shareX1 = p1.x * maxOffset;
  shareY1 = p1.y * maxOffset;
  shareX2 = p2.x * maxOffset;
  shareY2 = p2.y * maxOffset;
}

/* =========================
   Drag handling
   ========================= */
let activeHandle = null;

function onMouseDown(e) {
  if (e.target === handle1 || e.target === handle2) {
    activeHandle = e.target;
    e.preventDefault();
  }
}

function onMouseMove(e) {
  if (!activeHandle) return;
  const container = activeHandle.parentNode;
  const rect = container.getBoundingClientRect();
  const w = rect.width;
  const h = rect.height;
  const halfHandle = activeHandle.offsetWidth / 2;
  let localX = e.clientX - rect.left - halfHandle;
  let localY = e.clientY - rect.top - halfHandle;
  localX = Math.max(0, Math.min(localX, w - halfHandle));
  localY = Math.max(0, Math.min(localY, h - halfHandle));
  activeHandle.style.left = (localX - halfHandle) + 'px';
  activeHandle.style.top  = (localY - halfHandle) + 'px';
  updateShareFromHandles();
}

function onMouseUp() {
  activeHandle = null;
}

/* =========================
   Reset button
   ========================= */
resetBtn.addEventListener('click', () => {
  setHandlePercent(handle1, 0.75, 0.25); // top=25%, left=75%
  setHandlePercent(handle2, 0.25, 0.75); // top=75%, left=25%
  updateShareFromHandles();
});

/* =========================
   Initialize after DOM content
   ========================= */
document.addEventListener('DOMContentLoaded', () => {
  // Setup initial handle positions
  setHandlePercent(handle1, 0.75, 0.25);
  setHandlePercent(handle2, 0.25, 0.75);
  updateShareFromHandles();

  // Attach event listeners
  document.addEventListener('mousedown', onMouseDown);
  document.addEventListener('mousemove', onMouseMove);
  document.addEventListener('mouseup', onMouseUp);
});

/* =========================
   p5.js Sketch
   ========================= */
const sketch = function(p) {
  let canvas;

  p.setup = function() {
    canvas = p.createCanvas(canvasSize, canvasSize);
    canvas.parent('visualization-area');

    // Generate shares
    generateShares();
    // Update offset after shares are created
    updateShareFromHandles();
  };

  p.draw = function() {
    p.background(200);
    if (share1 && share2) {
      p.image(share1, shareX1, shareY1);
      p.image(share2, shareX2, shareY2);
    }
  };

  function generateShares() {
    // Create secret image
    const secret = p.createGraphics(secretSize, secretSize);
    secret.pixelDensity(1);
    secret.noStroke();
    secret.background(255);

    // Draw simple smiley face
    const faceRadius = secretSize * 0.8;
    secret.fill(0);
    secret.ellipse(secretSize/2, secretSize/2, faceRadius, faceRadius);
    // Eyes
    const eyeRadius = secretSize * 0.07;
    const eyeOffsetX = secretSize * 0.2;
    const eyeOffsetY = secretSize * 0.2;
    secret.fill(255);
    secret.ellipse(secretSize/2 - eyeOffsetX, secretSize/2 - eyeOffsetY, eyeRadius*2, eyeRadius*2);
    secret.ellipse(secretSize/2 + eyeOffsetX, secretSize/2 - eyeOffsetY, eyeRadius*2, eyeRadius*2);
    secret.fill(0);
    secret.ellipse(secretSize/2 - eyeOffsetX, secretSize/2 - eyeOffsetY, eyeRadius, eyeRadius);
    secret.ellipse(secretSize/2 + eyeOffsetX, secretSize/2 - eyeOffsetY, eyeRadius, eyeRadius);
    // Mouth
    secret.stroke(0);
    secret.strokeWeight(secretSize * 0.02);
    secret.noFill();
    secret.arc(secretSize/2, secretSize/2 + secretSize*0.15,
               secretSize*0.5, secretSize*0.3, 0, Math.PI);

    secret.stroke(255, 0, 0, 0); // hide stroke

    secret.loadPixels(); // prepare pixel array for reading

    // Create share buffers
    share1 = p.createGraphics(shareSize, shareSize);
    share2 = p.createGraphics(shareSize, shareSize);
    share1.pixelDensity(1);
    share2.pixelDensity(1);
    share1.clear();
    share2.clear();

    // Patterns: [black, transparent]
    const patterns = [
      [[0,1],[1,0]],
      [[1,0],[0,1]]
    ];

    for (let i = 0; i < secretSize; i++) {
      for (let j = 0; j < secretSize; j++) {
        const c = secret.get(i, j); // returns p5.Color
        const isBlack = c.levels[0] < 128; // threshold
        const idx = Math.floor(Math.random() * 2);
        let pat1, pat2;
        if (isBlack) {
          pat1 = patterns[idx];
          pat2 = patterns[1 - idx];
        } else {
          pat1 = pat2 = patterns[idx];
        }
        const x = i * 2;
        const y = j * 2;
        // Draw share1
        for (let dx = 0; dx < 2; dx++) {
          for (let dy = 0; dy < 2; dy++) {
            if (pat1[dy][dx] === 0) {
              share1.noStroke();
              share1.fill(0);
              share1.rect(x + dx, y + dy, 1, 1);
            }
          }
        }
        // Draw share2
        for (let dx = 0; dx < 2; dx++) {
          for (let dy = 0; dy < 2; dy++) {
            if (pat2[dy][dx] === 0) {
              share2.noStroke();
              share2.fill(0);
              share2.rect(x + dx, y + dy, 1, 1);
            }
          }
        }
      }
    }
  }
};

new p5(sketch);
</script>
</body>
</html>
