<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Concentric Circles Inversion Demo</title>
<style>
  body {font-family: Arial, sans-serif; text-align:center; margin:20px;}
  #main-container {display:inline-block; border:1px solid #999; padding:10px; background:#fff;}
  #control-panel {display:flex; justify-content:space-around; margin-bottom:10px;}
  .control-group {display:flex; align-items:center;}
  .control-group label {margin-right:5px; font-weight:bold;}
  .control-group input[type=radio] {margin-left:4px;}
  .control-group input[type=checkbox] {margin-left:4px;}
  #canvas-container {border:1px solid #999; width:550px; height:550px;}
</style>
</head>
<body>
<div id="main-container">
  <div id="control-panel">
    <div id="rings-control-group" class="control-group">
      <label for="rings-radios">rings</label>
      <div id="rings-radios">
        <input type="radio" name="rings" value="1" id="rings-1" checked>
        <label for="rings-1">1</label>
        <input type="radio" name="rings" value="2" id="rings-2">
        <label for="rings-2">2</label>
        <input type="radio" name="rings" value="3" id="rings-3">
        <label for="rings-3">3</label>
        <input type="radio" name="rings" value="4" id="rings-4">
        <label for="rings-4">4</label>
        <input type="radio" name="rings" value="5" id="rings-5">
        <label for="rings-5">5</label>
        <input type="radio" name="rings" value="6" id="rings-6">
        <label for="rings-6">6</label>
        <input type="radio" name="rings" value="7" id="rings-7">
        <label for="rings-7">7</label>
      </div>
    </div>
    <div id="circles-per-ring-control-group" class="control-group">
      <label for="cpr-radios">circles per ring</label>
      <div id="cpr-radios">
        <input type="radio" name="cpr" value="4" id="cpr-4">
        <label for="cpr-4">4</label>
        <input type="radio" name="cpr" value="6" id="cpr-6">
        <label for="cpr-6">6</label>
        <input type="radio" name="cpr" value="8" id="cpr-8" checked>
        <label for="cpr-8">8</label>
        <input type="radio" name="cpr" value="10" id="cpr-10">
        <label for="cpr-10">10</label>
        <input type="radio" name="cpr" value="12" id="cpr-12">
        <label for="cpr-12">12</label>
        <input type="radio" name="cpr" value="14" id="cpr-14">
        <label for="cpr-14">14</label>
      </div>
    </div>
    <div id="inverse-control-group" class="control-group">
      <label for="checkbox-inverse">inverse</label>
      <input type="checkbox" id="checkbox-inverse">
    </div>
  </div>
  <div id="canvas-container">
    <!-- placeholder canvas to be replaced by p5 -->
    <canvas id="p5-canvas"></canvas>
  </div>
</div>

<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
<script>
let numRings = 1;
let numCirclesPerRing = 8;
let inverse = false;
let locators = [];      // two p5.Vector objects
let draggedLocator = null;

// ------- P5 Sketch -------
function setup() {
  // Remove placeholder canvas so we don't have duplicate elements
  const placeholder = document.getElementById('p5-canvas');
  if (placeholder) placeholder.parentNode.removeChild(placeholder);

  createCanvas(550, 550);
  angleMode(RADIANS);
  // Place the newly created canvas inside the container
  canvas.parent('canvas-container');
  canvas.elt.id = 'p5-canvas';

  // Initialise locators relative to canvas centre
  locators = [createVector(-50, 0), createVector(50, 0)];
  noLoop();
}

function draw() {
  background('#C1D4E6');
  translate(width / 2, height / 2);   // move origin to centre

  const layout = computeCircleLayout();
  const circles = layout.circles;
  const outerCenters = layout.outerCenters;

  if (!inverse) {
    stroke(0);
    strokeWeight(1);
    fill('#8C929D');
    for (const c of circles) {
      ellipse(c.x, c.y, c.r * 2);
    }
    // Guide lines from centre to outermost ring
    stroke('#A0A0A0');
    strokeWeight(0.5);
    for (const oc of outerCenters) {
      line(0, 0, oc.x, oc.y);
    }
  } else {
    // Inversion circle
    const ax = locators[0].x, ay = locators[0].y;
    const bx = locators[1].x, by = locators[1].y;
    const C_inv = createVector((ax + bx) / 2, (ay + by) / 2);
    const R_inv = dist(ax, ay, bx, by) / 2;

    // Draw locators
    noStroke();
    fill('#FF0000');
    for (const loc of locators) ellipse(loc.x, loc.y, 8);

    // Connecting line
    stroke('#FF0000');
    strokeWeight(1);
    line(ax, ay, bx, by);

    // Inversion circle
    stroke(0);
    strokeWeight(1);
    fill('rgba(255,0,0,0.3)');
    ellipse(C_inv.x, C_inv.y, R_inv * 2);

    // Inverted shapes
    for (const c of circles) {
      const C_orig = createVector(c.x, c.y);
      const r_orig = c.r;
      const d = dist(C_inv.x, C_inv.y, C_orig.x, C_orig.y);

      if (d < 1e-5 || abs(d - r_orig) < 1e-5) {
        // Circle passes through inversion centre -> line
        const u = p5.Vector.sub(C_orig, C_inv).normalize();
        const v = createVector(-u.y, u.x);
        const D = (R_inv * R_inv) / (2 * d);
        const L0 = p5.Vector.add(C_inv, p5.Vector.mult(v, D));
        const far = 2000; // big enough to cover canvas
        const p1 = p5.Vector.add(L0, p5.Vector.mult(v, far));
        const p2 = p5.Vector.sub(L0, p5.Vector.mult(v, far));
        stroke(0);
        strokeWeight(1);
        line(p1.x, p1.y, p2.x, p2.y);
      } else {
        const factor = (R_inv * R_inv) / (d * d - r_orig * r_orig);
        const newCenter = p5.Vector.add(C_inv, p5.Vector.mult(p5.Vector.sub(C_orig, C_inv), factor));
        const newRadius = abs(factor) * r_orig;
        noStroke();
        fill('rgba(220,100,220,0.5)');
        ellipse(newCenter.x, newCenter.y, newRadius * 2);
      }
    }
  }
}

function computeCircleLayout() {
  const N = numCirclesPerRing;
  const M = numRings;
  const R_max = width / 2 * 0.9;
  const k = (1 - sin(PI / N)) / (1 + sin(PI / N));
  const R_c = R_max / (1 + sin(PI / N));
  const r = R_c * sin(PI / N);

  const circles = [];
  const outerCenters = [];
  for (let i = 0; i < M; i++) {
    const centerRadius = R_c * pow(k, i);
    const circleRadius = r * pow(k, i);
    for (let j = 0; j < N; j++) {
      const angle = j * TWO_PI / N;
      const cx = centerRadius * cos(angle);
      const cy = centerRadius * sin(angle);
      circles.push({x: cx, y: cy, r: circleRadius});
    }
  }
  // outer ring centers for guide lines
  for (let j = 0; j < N; j++) {
    const angle = j * TWO_PI / N;
    outerCenters.push({
      x: R_c * cos(angle),
      y: R_c * sin(angle)
    });
  }
  return {circles, outerCenters};
}

// ------- Interaction Handlers -------
function mousePressed() {
  if (!inverse) return;
  const mx = mouseX - width / 2;
  const my = mouseY - height / 2;
  for (let i = 0; i < locators.length; i++) {
    if (dist(mx, my, locators[i].x, locators[i].y) <= 4) {
      draggedLocator = i;
      break;
    }
  }
}

function mouseDragged() {
  if (!inverse || draggedLocator === null) return;
  const mx = mouseX - width / 2;
  const my = mouseY - height / 2;
  locators[draggedLocator].set(mx, my);
  redraw();
}

function mouseReleased() {
  draggedLocator = null;
}

// ------- Control Panel Listeners -------
function updateParams() {
  numRings = parseInt(document.querySelector('input[name="rings"]:checked').value);
  numCirclesPerRing = parseInt(document.querySelector('input[name="cpr"]:checked').value);
  inverse = document.getElementById('checkbox-inverse').checked;
  redraw();
}

function setupControls() {
  const ringRadios = document.querySelector('input[name="rings"]');
  const cprRadios = document.querySelectorAll('input[name="cpr"]');
  const invCheck = document.getElementById('checkbox-inverse');
  ringRadios.forEach(r => r.addEventListener('change', updateParams));
  cprRadios.forEach(r => r.addEventListener('change', updateParams));
  invCheck.addEventListener('change', updateParams);
}
setupControls();
</script>
</body>
</html>
