<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="utf-8">
<title>pH vs pKa Demo</title>
<style>
    html,body{margin:0;height:100%;font-family:sans-serif}
    #app{display:flex;flex-direction:column;height:100%}
    #control-panel{padding:10px;background:#f0f0f0;border-bottom:1px solid #ccc}
    .slider-group{margin-bottom:10px}
    .slider-group label{margin-right:10px}
    #viz-area{flex:1;position:relative}
    #plotCanvas{display:block;width:100%;height:100%}
    #formula-strong,#formula-weak{
        position:absolute;
        background:rgba(255,255,255,0.8);
        padding:3px 6px;
        border-radius:4px;
        font-size:0.9em;
    }
    #formula-strong{left:10%;top:20%;color:maroon}
    #formula-weak{right:10%;top:50%;color:green}
</style>

<!-- Load p5.js -->
<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.6.0/p5.min.js"></script>
<!-- Load MathJax -->
<script src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
</head>

<body>
<div id="app">
    <!-- Control Panel -->
    <div id="control-panel">
        <div class="slider-group">
            <label id="label-logCa" for="slider-logCa">\(C_a\)</label>
            <input id="slider-logCa" type="range" min="-4" max="0" step="0.01" value="-1">
        </div>
        <div class="slider-group">
            <label id="label-pKa" for="slider-pka">\(pK_a\)</label>
            <input id="slider-pka" type="range" min="-4" max="7" step="0.1" value="1.2">
        </div>
    </div>

    <!-- Visualization Area -->
    <div id="viz-area">
        <canvas id="plotCanvas"></canvas>
        <div id="formula-strong">\(\color{maroon}{pH = - \log C_a}\)</div>
        <div id="formula-weak">\(\color{green}{pH = \frac{1}{2} (pK_a - \log C_a)}\)</div>
    </div>
</div>

<script>
// Global variables
let logCa = -1;   // log10(Ca)
let pKa = 1.2;    // pKa
const xMin = -4, xMax = 7;  // pKa axis
const yMin = 0, yMax = 7;   // pH axis

const sliderLogCa = document.getElementById('slider-logCa');
const sliderPka = document.getElementById('slider-pka');

// Update global state on slider changes
sliderLogCa.addEventListener('input', () => {
    logCa = parseFloat(sliderLogCa.value);
    redraw();           // p5.js redraws
});

sliderPka.addEventListener('input', () => {
    pKa = parseFloat(sliderPka.value);
    redraw();
});

// MathJax typeset on load
document.addEventListener('DOMContentLoaded', () => {
    MathJax.typesetPromise();
});

/* p5.js Sketch */
let canvas;
function setup() {
    canvas = createCanvas(800, 600);
    canvas.parent('viz-area');
    noLoop();  // We'll call redraw() only when sliders change
    // Adjust canvas to container size
    resizeCanvasToContainer();
    window.addEventListener('resize', resizeCanvasToContainer);
}

function resizeCanvasToContainer() {
    const parent = document.getElementById('viz-area');
    const w = parent.clientWidth;
    const h = parent.clientHeight;
    resizeCanvas(w, h);
}

function draw() {
    background(255);
    stroke(0); strokeWeight(1);
    drawAxes();

    // Strong acid horizontal line
    const pHStrong = -logCa;
    drawColoredLine(pKa, pHStrong, xMin, pHStrong, xMax, yMax, {color:'maroon', weight:2});

    // Weak acid slanted line
    const strongSlope = function(x){ return 0.5 * (x - logCa); };
    drawColoredLine(xMin, strongSlope(xMin), xMax, strongSlope(xMax), {color:'green', weight:2});

    // Exact pH curve (blue)
    drawExactPHCurve();

    // Indicator point
    drawIndicatorPoint();
}

function dataToPixelX(x) {
    return map(x, xMin, xMax, margin() , width - margin());
}
function dataToPixelY(y) {
    return map(y, yMin, yMax, height - margin(), margin());
}
function margin(){return 60;} // padding around plot area

function drawAxes() {
    push();
    stroke(70);
    strokeWeight(1);
    // Y axis
    line(margin(), margin(), margin(), height - margin());
    // X axis
    line(margin(), height - margin(), width - margin(), height - margin());

    // Y ticks
    for(let y=yMin;y<=yMax;y++){
        const cx = margin();
        const cy = dataToPixelY(y);
        line(cx-5, cy, cx+5, cy);
        noStroke(); fill(0); textAlign(RIGHT, CENTER);
        text(y, cx-8, cy);
    }
    // X ticks
    for(let x=xMin;x<=xMax;x++){
        const cx = dataToPixelX(x);
        const cy = height - margin();
        line(cx, cy-5, cx, cy+5);
        noStroke(); fill(0); textAlign(CENTER, TOP);
        text(x, cx, cy+8);
    }
    pop();
}

function drawColoredLine(x1, y1, x2, y2, opts){
    const {color='#000', weight=1} = opts || {};
    stroke(color); strokeWeight(weight);
    line(dataToPixelX(x1), dataToPixelY(y1), dataToPixelX(x2), dataToPixelY(y2));
}

function drawExactPHCurve(){
    stroke('blue'); strokeWeight(3);
    beginShape();
    for(let x=xMin; x<=xMax; x+=0.05){
        const y = exactPH(x);
        vertex(dataToPixelX(x), dataToPixelY(y));
    }
    endShape();
}

function exactPH(pKaVal){
    const Ka = pow(10, -pKaVal);
    const Ca = pow(10, logCa);
    const disc = sqrt(Ka*Ka + 4*Ka*Ca);
    const H = (-Ka + disc) / 2;  // positive root
    return -log10(H);
}

function drawIndicatorPoint(){
    const y = exactPH(pKa);
    push();
    noStroke();
    fill('orange'); strokeWeight(1); stroke('black');
    ellipse(dataToPixelX(pKa), dataToPixelY(y), 10, 10);
    pop();
}
</script>
</body>
</html>
