<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Electric Field & Force Simulation</title>
<style>
    body{margin:0;font-family:Arial,Helvetica,sans-serif;line-height:1.4;}
    #app-container{display:flex;flex-direction:column;height:100vh;overflow:hidden;}
    #controls-container{display:flex;gap:30px;padding:10px 20px;background:#f8f8f8;}
    .slider-group{display:flex;flex-direction:column;align-items:center;gap:5px;}
    .slider-group label{font-weight:bold;}
    .slider-group input{width:200px;}
    #main-content{display:flex;flex:1;padding:10px;gap:10px;overflow:hidden;}
    #canvas-container{flex:1;background:#ffffff;border:1px solid #ddd;}
    #info-panel{flex:0 0 240px;padding:10px 15px;background:#fefefe;border:1px solid #ddd;overflow:auto;}
    #info-panel p{margin:8px 0;}
</style>
</head>
<body>
<div id="app-container">
    <div id="controls-container">
        <div class="slider-group">
            <label for="slider-source-charge">source charge (Q)</label>
            <input type="range" id="slider-source-charge">
            <span id="label-source-charge"></span>
        </div>
        <div class="slider-group">
            <label for="slider-test-charge">test charge (q)</label>
            <input type="range" id="slider-test-charge">
            <span id="label-test-charge"></span>
        </div>
    </div>

    <div id="main-content">
        <div id="canvas-container"></div>
        <div id="info-panel">
            <p id="display-E-mag">|E| = 0.000 N/C</p>
            <p id="display-Ex">E<sub>x</sub> component = 0.000 N/C</p>
            <p id="display-Ey">E<sub>y</sub> component = 0.000 N/C</p>
            <p id="display-x-pos">x position = 0.00 m</p>
            <p id="display-y-pos">y position = 0.00 m</p>
            <p id="display-force">Electric force = 0.000 N</p>
        </div>
    </div>
</div>

<script src="https://cdn.jsdelivr.net/npm/p5@1.9.0/lib/p5.js"></script>
<script>
/* ---- Global State ---- */
let Q = 2.0;         // source charge
let q = -0.0845;     // test charge
let testX = 2;       // test charge position x (m)
let testY = 3;       // test charge position y (m)
let dragging = false;
const scale = 40;           // 1 meter = 40 pixels
const arrowScale = 200;     // scaling factor for visual vectors
const canvasApp = {
    width: 600,
    height: 500
};

/* ---- Setup Slider Panels ---- */
function initSliders() {
    const srcSlider = document.getElementById('slider-source-charge');
    srcSlider.min = -5; srcSlider.max = 5; srcSlider.step = 0.1; srcSlider.value = Q;
    document.getElementById('label-source-charge').innerText = Q.toFixed(1);

    const testSlider = document.getElementById('slider-test-charge');
    testSlider.min = -0.2; testSlider.max = 0.2; testSlider.step = 0.0005; testSlider.value = q;
    document.getElementById('label-test-charge').innerText = q.toFixed(4);

    srcSlider.addEventListener('input', () => {
        Q = parseFloat(srcSlider.value);
        document.getElementById('label-source-charge').innerText = Q.toFixed(1);
    });

    testSlider.addEventListener('input', () => {
        q = parseFloat(testSlider.value);
        document.getElementById('label-test-charge').innerText = q.toFixed(4);
    });
}

/* ---- p5.js Sketch ---- */
function setup() {
    createCanvas(canvasApp.width, canvasApp.height).parent('canvas-container');
    initSliders();
}
function draw() {
    background(255);

    /* ---- Transform to World Coordinates ---- */
    translate(width / 2, height / 2);
    scale(1, -1);                          // Invert Y for conventional coords
    const rVec = createVector(testX, testY);
    const r = rVec.mag();
    let Evec = createVector(0, 0);
    if (r > 1e-6) {
        const factor = Q / (r * r);
        Evec = rVec.copy().mult(factor / r); // Q * r_hat / r^2
    }
    const Fvec = Evec.copy().mult(q);

    /* ---- Update Info Panel ---- */
    document.getElementById('display-E-mag').innerText = `|E| = ${Evec.mag().toFixed(3)} N/C`;
    document.getElementById('display-Ex').innerText = `E<sub>x</sub> component = ${Evec.x.toFixed(3)} N/C`;
    document.getElementById('display-Ey').innerText = `E<sub>y</sub> component = ${Evec.y.toFixed(3)} N/C`;
    document.getElementById('display-x-pos').innerText = `x position = ${testX.toFixed(2)} m`;
    document.getElementById('display-y-pos').innerText = `y position = ${testY.toFixed(2)} m`;
    document.getElementById('display-force').innerText = `Electric force = ${Fvec.mag().toFixed(3)} N`;

    /* ---- Draw Grid and Axes ---- */
    stroke(220); strokeWeight(1);
    for (let i = -7; i <= 7; i += 2) {
        line(i * scale, -7 * scale, i * scale, 7 * scale); // vertical lines
        line(-7 * scale, i * scale, 7 * scale, i * scale); // horizontal lines
    }
    // Axis labels
    fill(0); textAlign(CENTER, CENTER); textSize(12);
    for (let i = -7; i <= 7; i += 2) {
        if (i !== 0) {
            text(i.toString(), i * scale, 10);
            text(i.toString(), 10, i * scale);
        }
    }

    /* ---- Draw Source Charge (Blue) ---- */
    fill(0, 0, 255); noStroke();
    ellipse(0, 0, 20, 20);

    /* ---- Draw Test Charge Locator (Black) ---- */
    fill(0); noStroke();
    ellipse(testX * scale, testY * scale, 15, 15);

    /* ---- Draw Vectors ---- */
    const base = createVector(testX * scale, testY * scale);
    if (Evec.mag() > 0) drawArrow(base, Evec, 'red');
    if (q !== 0) drawArrow(base, Fvec, 'blue');
}

/* ---- Draw Arrow Utility ---- */
function drawArrow(base, vector, color) {
    const scaled = p5.Vector.mult(vector, arrowScale);
    const end = p5.Vector.add(base, scaled);
    stroke(color); strokeWeight(2);
    line(base.x, base.y, end.x, end.y);
    // Arrowhead
    const arrowSize = 10;
    const angle = Math.atan2(vector.y, vector.x);
    push();
    translate(end.x, end.y);
    rotate(angle);
    fill(color); noStroke();
    triangle(-arrowSize, arrowSize, -arrowSize, -arrowSize, 0, 0);
    pop();
}

/* ---- Dragging Logic ---- */
function mousePressed() {
    const mx = (mouseX - width / 2) / scale;
    const my = -(mouseY - height / 2) / scale;
    if (dist(mx, my, testX, testY) <= 0.15) {
        dragging = true;
    }
}
function mouseReleased() {
    dragging = false;
}
function mouseDragged() {
    if (dragging) {
        const mx = (mouseX - width / 2) / scale;
        const my = -(mouseY - height / 2) / scale;
        testX = mx;
        testY = my;
    }
}
</script>
</body>
</html>
