  </div>

  <div id="sliders-container">
    <div id="slider-r-control">
      <label for="slider-r">radius r</label>
      <input type="range" id="slider-r" min="0.1" max="5" step="0.1" value="2.5">
      <span id="span-r-value">2.5</span>
    </div>
    <div id="slider-k-control">
      <label for="slider-k">wave number k</label>
      <input type="range" id="slider-k" min="1" max="100" step="0.01" value="50">
      <span id="span-k-value">50</span>
    </div>
  </div>
</div>

<p id="hover-info">(Hover over the curves to see math labels.)</p>

<div id="plots-container">
  <div id="plot-left"></div>
  <div id="plot-right"></div>
</div>

<script>
const sliderR = document.getElementById('slider-r');
const sliderK = document.getElementById('slider-k');
const spanR = document.getElementById('span-r-value');
const spanK = document.getElementById('span-k-value');

const checkboxHertz = document.getElementById('checkbox-hertz');
const checkboxOverlap = document.getElementById('checkbox-overlap');
const checkboxGaussian = document.getElementById('checkbox-gaussian');

const btnLinearLinear = document.getElementById('btn-linear-linear');
const btnLogLinear = document.getElementById('btn-log-linear');
const btnLogLog = document.getElementById('btn-log-log');

const plotLeft = document.getElementById('plot-left');
const plotRight = document.getElementById('plot-right');

// Utility to generate linearly spaced array
function linspace(start, end, points) {
  const arr = [];
  const step = (end - start) / (points - 1);
  for (let i = 0; i < points; i++) arr.push(start + step * i);
  return arr;
}

// Function Definitions
function fH(r) { return r <= 1 ? Math.pow(1 - r * r, 2.5) : 0; }
function fO(r) { return r <= 1 ? Math.pow(1 - r, 2) : 0; }
function fG(r) { return Math.exp(-Math.PI * r * r); }

function FH(k) {
  if (k === 0) return Math.PI;
  const term = ((3 - k * k) * Math.sin(k) - 3 * k * Math.cos(k));
  return 15 * Math.PI * term / Math.pow(k, 5);
}
function FO(k) {
  if (k === 0) return -1e6; // approximate large negative value
  const term = 2 - k * k - 2 * Math.cos(k) - 2 * k * Math.sin(k);
  return 8 * Math.PI * term / Math.pow(k, 4);
}
function FG(k) { return (1 / Math.sqrt(Math.PI)) * Math.exp(-k * k / (4 * Math.PI)); }

// Hover templates
const hoverTemplates = {
  Hertz: 'Hertz<br>f(r) = (1 - r^2)^{5/2}, r \\le 1$<extra></extra>',
  Overlap: 'overlap<br>f(r) = (1 - r)^2$<extra></extra>',
  Gaussian: 'Gaussian<br>f(r) = e^{-\\pi r^2}$<extra></extra>',

  Hertz_k: 'Hertz<br>F(k) = 15\\pi \\frac{(3-k^2)\\sin(k) - 3k\\cos(k)}{k^5}$<extra></extra>',
  Overlap_k: 'overlap<br>F(k) = 8\\pi \\frac{2 - k^2 - 2\\cos(k) - 2k\\sin(k)}{k^4}$<extra></extra>',
  Gaussian_k: 'Gaussian<br>F(k) = \\frac{1}{\\sqrt{\\pi}} e^{-k^2/(4\\pi)}$<extra></extra>'
};

function init() {
  btnLinearLinear.classList.add('active');
  updatePlots();
}

function updatePlots() {
  const showHertz = checkboxHertz.checked;
  const showOverlap = checkboxOverlap.checked;
  const showGaussian = checkboxGaussian.checked;

  const isLinearY = btnLinearLinear.classList.contains('active');
  const rMax = parseFloat(sliderR.value);
  const kMax = parseFloat(sliderK.value);

  spanR.textContent = rMax.toFixed(1);
  spanK.textContent = kMax.toFixed(2);

  // Prepare r values
  const rVals = linspace(0, rMax, 200);
  const fHVals = rVals.map(r => showHertz ? fH(r) : null);
  const fOVals = rVals.map(r => showOverlap ? fO(r) : null);
  const fGVals = rVals.map(r => showGaussian ? fG(r) :);

  const leftTraces = [];
  if (showHertz)
    leftTraces.push({
      x: rVals,
      y: fHVals,
      mode: 'lines',
      line: {color:'#ff0000'},
      name: 'Hertz',
      hovertemplate: hoverTemplates.Hertz
    });
  if (showOverlap)
    leftTraces.push({
      x: rVals,
      y: fOVals,
      mode: 'lines',
      line: {color:'#000000'},
      name: 'overlap',
      hovertemplate: hoverTemplates.Overlap
    });
  if (showGaussian)
    leftTraces.push({
      x: rVals,
      y: fGVals,
      mode: 'lines',
      line: {color:'#0000ff'},
      name: 'Gaussian',
      hovertemplate: hoverTemplates.Gaussian
    });

  const leftLayout = {
    title: {text: 'function', font:{color:'lightgray'}},
    xaxis: {title:{text:'r',font:{color:'lightgray'}}, range:[0, rMax]},
    yaxis: {title:{text:'f(r)',font:{color:'lightgray'}}},
    margin:{l:60,r:20,t:30,b:50}
  };

  Plotly.react(plotLeft, leftTraces, leftLayout);

  // Prepare k values
  const kVals = linspace(0, kMax, 200);
  const FHVals = kVals.map(k => showHertz ? FH(k) : null);
  const FOVals = kVals.map(k => showOverlap ? FO(k) : null);
  const FGVals = kVals.map(k => showGaussian ? FG(k) : null);

  const rightTraces = [];
  if (showHertz)
    rightTraces.push({
      x: kVals,
      y: FHVals,
      mode: 'lines',
      line: {color:'#ff0000'},
      name: 'Hertz',
      hovertemplate: hoverTemplates.Hertz_k
    });
  if (showOverlap)
    rightTraces.push({
      x: kVals,
      y: FOVals,
      mode: 'lines',
      line: {color:'#000000'},
      name: 'overlap',
      hovertemplate: hoverTemplates.Overlap_k
    });
  if (showGaussian)
    rightTraces.push({
      x: kVals,
      y: FGVals,
      mode: 'lines',
      line: {color:'#0000ff'},
      name: 'Gaussian',
      hovertemplate: hoverTemplates.Gaussian_k
    });

  const rightLayout = {
    title: {text: '3D Fourier transform (real part)', font:{color:'lightgray'}},
    xaxis: {title:{text:'k',font:{color:'lightgray'}}, range:[0, kMax]},
    yaxis: {title:{text:'F(k)',font:{color:'lightgray'}},
            type: isLinearY ? 'linear' : 'log'
           },
    margin:{l:60,r:20,t:30,b:50}
  };

  Plotly.react(plotRight, rightTraces, rightLayout);

  MathJax.typesetPromise();
}

// Event listeners
checkboxHertz.addEventListener('change', updatePlots);
checkboxOverlap.addEventListener('change', updatePlots);
checkboxGaussian.addEventListener('change', updatePlots);
sliderR.addEventListener('input', updatePlots);
sliderK.addEventListener('input', updatePlots);

// Scale button handlers
function activateButton(activeBtn, otherBtn1, otherBtn2) {
  activeBtn.classList.add('active');
  otherBtn1.classList.remove('active');
  otherBtn2.classList.remove('active');
  updatePlots();
}
btnLinearLinear.addEventListener('click', () => activateButton(btnLinearLinear, btnLogLinear, btnLogLog));
btnLogLinear.addEventListener('click', () => activateButton(btnLogLinear, btnLinearLinear, btnLogLog));
btnLogLog.addEventListener('click', () => activateButton(btnLogLog, btnLinearLinear, btnLogLinear));

window.onload = init;
</script>
</body>
</html>
