<!-- p5.js library -->
<script src="https://cdn.jsdelivr.net/npm/p5@1.6.0/lib/p5.min.js"></script>
<script>
let neutral_year = 2040; // default
let type = 'emissions';
let scale = 'individual';
let population = 250; // in thousands
let annualEmissions = 14;
let reductionTime = 20;
let annualBudget = 1.5;
let startYear = 2020;
let compensationCost = 15;
let fracCompPercent = 50;
let constCompPercent = 25;

// Slider elements
const sliders = {
  population:   {elem: document.getElementById('slider-population'),      val: document.getElementById('value-population')},
  annualEmissions:    {elem: document.getElementById('slider-annual-emissions'), val: document.getElementById('value-annual-emissions')},
  reductionTime:      {elem: document.getElementById('slider-reduction-time'), val: document.getElementById('value-reduction-time')},
  annualBudget:       {elem: document.getElementById('slider-annual-budget'), val: document.getElementById('value-annual-budget')},
  startYear:          {elem: document.getElementById('slider-start-year'), val: document.getElementById('value-start-year')},
  compensationCost:   {elem: document.getElementById('slider-compensation-cost'), val: document.getElementById('value-compensation-cost')},
  fracCompPercent:    {elem: document.getElementById('slider-fractional-compensation'), val: document.getElementById('value-fractional-compensation')},
  constCompPercent:   {elem: document.getElementById('slider-constant-compensation'), val: document.getElementById('value-constant-compensation')},
};

// Radio groups
const radios = {
  scale: [...document.querySelectorAll('input[name="scale"]')],
  type: [...document.querySelectorAll('input[name="type"]')]
};

// Buttons
const buttons = {
  neutralNow:       document.getElementById('btn-neutral-now'),
  neutral2030:      document.getElementById('btn-neutral-2030'),
  neutral2040:      document.getElementById('btn-neutral-2040'),
  neutral2050:      document.getElementById('btn-neutral-2050'),
  neutralNever:     document.getElementById('btn-neutral-never')
};

// Attach listeners
Object.values(sliders).forEach(s => {
  s.elem.addEventListener('input', e => {
    const id = e.target.id.replace('slider-','');
    const val = e.target.type === 'range' ? parseFloat(e.target.value) : e.target.value;
    sliders[id].val.textContent = val % 1 === 0 ? Math.round(val) : val.toFixed(1);
    updateParams();
    redraw();
  });
});

radios.scale.forEach(r => {
  r.addEventListener('change', e => {
    scale = e.target.value;
    // enable/disable population slider
    if (scale === 'individual') {
      sliders.population.elem.disabled = true;
    } else {
      sliders.population.elem.disabled = false;
    }
    updateParams();
    redraw();
  });
});

radios.type.forEach(r => {
  r.addEventListener('change', e => {
    type = e.target.value;
    updateParams();
    redraw();
  });
});

Object.values(buttons).forEach(btn => {
  btn.addEventListener('click', () => {
    // reset active
    Object.values(buttons).forEach(b=>b.classList.remove('active'));
    if (btn === buttons.neutralNever) {
      neutral_year = null;
    } else {
      neutral_year = parseInt(btn.textContent);
      btn.classList.add('active');
    }
    redraw();
  });
});

function updateParams() {
  population = parseInt(sliders.population.elem.value);
  annualEmissions = parseFloat(sliders.annualEmissions.elem.value);
  reductionTime = parseInt(sliders.reductionTime.elem.value);
  annualBudget = parseFloat(sliders.annualBudget.elem.value);
  startYear = parseInt(sliders.startYear.elem.value);
  compensationCost = parseInt(sliders.compensationCost.elem.value);
  fracCompPercent = parseInt(sliders.fracCompPercent.elem.value);
  constCompPercent = parseInt(sliders.constCompPercent.elem.value);
}

// Data arrays
const years = [];
for (let y = 2020; y <= 2050; y++) years.push(y);

let data = {};

// Compute data
function computeData() {
  data = {
    real: [],
    compensated: [],
    effective: [],
    cumulativeEffective: [],
    annualCosts: [],
    totalCosts: 0,
    effectiveAtYear: {}, // for budget circles
    cumulativeAtYear: {} // for lines
  };

  let cumEff = 0;
  let totalCost = 0;
  for (let t of years) {
    const real = t < startYear ? annualEmissions : annualEmissions * Math.pow(0.5, (t - startYear) / reductionTime);
    const fractional = real * (fracCompPercent / 100);
    const constant = annualEmissions * (constCompPercent / 100);
    const compensated = fractional + constant;
    const effective = real - compensated;

    const annualCost = compensated * compensationCost;
    cumEff += effective;
    totalCost += annualCost;

    data.real.push(real);
    data.compensated.push(compensated);
    data.effective.push(effective);
    data.cumulativeEffective.push(cumEff);
    data.annualCosts.push(annualCost);

    data.effectiveAtYear[t] = effective;
    data.cumulativeAtYear[t] = cumEff;
  }
  data.totalCosts = totalCost;
}

// p5.js sketch
let sketch = new p5((p) => {
  p.setup = function() {
    let canvas = p.createCanvas(900, 600);
    canvas.parent('main-plot-container');
    p.noLoop();
    computeData();
  };

  p.draw = function() {
    p.clear();
    const margin = {left:60, right:20, top:30, bottom:100};
    const plotX0 = margin.left;
    const plotY0 = margin.top;
    const plotW = p.width - margin.left - margin.right;
    const plotH = 400 - margin.top - margin.bottom;

    // Background
    p.background(255);

    // Axis
    p.stroke(0);
    p.strokeWeight(1);
    p.line(plotX0, plotY0, plotX0, plotY0+plotH); // Y axis
    p.line(plotX0, plotY0+plotH, plotX0+plotW, plotY0+plotH); // X axis

    // Y axis ticks and label
    let maxY;
    if (scale === 'individual') {
      maxY = Math.max(...data.real.map(r=>r*10), 0) * 1.1; // scale
      if (maxY < 5) maxY = 5;
      p.noFill();
      p.textAlign(p.RIGHT, p.CENTER);
      p.text('annual CO₂ emissions per person (tons)', plotX0-10, plotY0+plotH/2);
    } else {
      maxY = Math.max(...data.real.map(r=>r * population * 1000 / 1e6),0)*1.1;
      if (maxY < 5) maxY = 5;
      p.noFill();
      p.textAlign(p.RIGHT, p.CENTER);
      p.text('annual city emission of CO₂ (mil tons)', plotX0-10, plotY0+plotH/2);
    }

    // X axis ticks and label
    p.textAlign(p.CENTER, p.BOTTOM);
    for (let i=0;i<years.length;i+=5) {
      const x = plotX0 + i*(plotW/(years.length-1));
      p.line(x, plotY0+plotH, x, plotY0+plotH+5);
      p.noStroke();
      p.fill(0);
      p.text(years[i], x, plotY0+plotH+15);
    }

    const yScale = maxY / plotH;

    // Plot curves and areas
    // Real emissions (red)
    p.stroke('red');
    p.strokeWeight(2);
    p.noFill();
    p.beginShape();
    for (let i=0;i<years.length;i++) {
      const x = plotX0 + i*(plotW/(years.length-1));
      let yVal = data.real[i];
      if (scale === 'individual') {
        yVal = yVal; // tons
      } else {
        yVal = yVal * population * 1000 / 1e6; // mil tons
      }
      const y = plotY0 + plotH - yVal / yScale;
      p.vertex(x,y);
    }
    p.endShape();

    // Effective emissions (black)
    p.stroke(0);
    p.strokeWeight(2);
    p.beginShape();
    for (let i=0;i<years.length;i++) {
      const x = plotX0 + i*(plotW/(years.length-1));
      let yVal = data.effective[i];
      if (scale === 'individual') {
        yVal = yVal;
      } else {
        yVal = yVal * population * 1000 / 1e6;
      }
      const y = plotY0 + plotH - yVal / yScale;
      p.vertex(x,y);
    }
    p.endShape();

    // Area between real and effective (blue)
    p.noStroke();
    p.fill(0, 114, 189, 80);
    p.beginShape();
    for (let i=0;i<years.length;i++) {
      const x = plotX0 + i*(plotW/(years.length-1));
      let yReal, yEff;
      if (scale==='individual') {
        yReal = data.real[i];
        yEff = data.effective[i];
      } else {
        yReal = data.real[i]*population*1000/1e6;
        yEff = data.effective[i]*population*1000/1e6;
      }
      const yRealPos = plotY0 + plotH - yReal / yScale;
      const yEffPos = plotY0 + plotH - yEff / yScale;
      p.vertex(x,yRealPos);
    }
    for (let i=years.length-1;i>=0;i--) {
      const x = plotX0 + i*(plotW/(years.length-1));
      let yReal, yEff;
      if (scale==='individual') {
        yReal = data.real[i];
        yEff = data.effective[i];
      } else {
        yReal = data.real[i]*population*1000/1e6;
        yEff = data.effective[i]*population*1000/1e6;
      }
      const yEffPos = plotY0 + plotH - yEff / yScale;
      p.vertex(x,yEffPos);
    }
    p.endShape(p.CLOSE);

    // Area below effective to zero (red or green)
    // Red where effective positive
    p.fill(255,0,0,80);
    p.beginShape();
    for (let i=0;i<years.length;i++) {
      const x = plotX0 + i*(plotW/(years.length-1));
      let yEff;
      if (scale==='individual') {
        yEff = data.effective[i];
      } else {
        yEff = data.effective[i]*population*1000/1e6;
      }
      const yEffPos = plotY0 + plotH - yEff / yScale;
      if (yEff > 0) {
        p.vertex(x, yEffPos);
      } else {
        p.vertex(x, plotY0+plotH);
      }
    }
    p.vertex(plotX0+plotW, plotY0+plotH);
    p.endShape(p.CLOSE);

    // Green where effective negative (overcompensation)
    p.fill(0,128,0,80);
    p.beginShape();
    for (let i=0;i<years.length;i++) {
      const x = plotX0 + i*(plotW/(years.length-1));
      let yEff;
      if (scale==='individual') {
        yEff = data.effective[i];
      } else {
        yEff = data.effective[i]*population*1000/1e6;
      }
      const yEffPos = plotY0 + plotH - yEff / yScale;
      if (yEff < 0) {
        p.vertex(x, yEffPos);
      } else {
        p.vertex(x, plotY0+plotH);
      }
    }
    p.vertex(plotX0+plotW, plotY0+plotH);
    p.endShape(p.CLOSE);

    // Annotations: Neutral line
    if (neutral_year !== null) {
      const idx = neutral_year - 2020;
      const x = plotX0 + idx*(plotW/(years.length-1));
      p.stroke('black');
      p.strokeWeight(1);
      p.line(x, plotY0, x, plotY0+plotH);
      p.noStroke();
      p.fill(0);
      p.textAlign(p.CENTER, p.TOP);
      p.text(`climate neutral ${neutral_year}`, x, plotY0-15);
    }

    // Horizontal dashed lines
    p.stroke('black');
    p.setLineDash([5,5]);
    p.strokeWeight(1);
    if (neutral_year !== null) {
      const cum = data.cumulativeAtYear[neutral_year];
      const y = plotY0 + plotH - cum / (maxY);
      p.line(plotX0, y, plotX0+plotW, y);
      p.noStroke();
      p.fill(0);
      p.textAlign(p.RIGHT, p.BOTTOM);
      p.text(`CO₂ emissions until ${neutral_year} = ${cum.toFixed(1)} tons CO₂`, plotX0-10, y-5);
    }
    const cum2050 = data.cumulativeAtYear[2050];
    const y2050 = plotY0 + plotH - cum2050 / (maxY);
    p.line(plotX0, y2050, plotX0+plotW, y2050);
    p.noStroke();
    p.fill(0);
    p.textAlign(p.RIGHT, p.BOTTOM);
    p.text(`CO₂ emissions until 2050 = ${cum2050.toFixed(1)} tons CO₂`, plotX0-10, y2050-5);
    p.setLineDash([]);

    // If type costs: draw costs plot below
    if (type === 'costs') {
      const costY0 = plotY0 + plotH + 80;
      const costW = p.width - margin.left - margin.right;
      const costH = 150;
      p.stroke('blue');
      p.strokeWeight(2);
      p.noFill();
      p.beginShape();
      for (let i=0;i<years.length;i++) {
        const x = plotX0 + i*(costW/(years.length-1));
        const yVal = data.annualCosts[i];
        const y = costY0 + costH - yVal / (data.totalCosts/(years.length*1.1));
        p.vertex(x,y);
      }
      p.endShape();

      // Filled area
      p.fill(0,114,189,80);
      p.beginShape();
      for (let i=0;i<years.length;i++) {
        const x = plotX0 + i*(costW/(years.length-1));
        const yVal = data.annualCosts[i];
        const y = costY0 + costH - yVal / (data.totalCosts/(years.length*1.1));
        p.vertex(x,y);
      }
      p.vertex(plotX0+costW, costY0+costH);
      p.vertex(plotX0, costY0+costH);
      p.endShape(p.CLOSE);

      // Dashed line for avg cost
      const avgCost = data.totalCosts / (2050-2020+1);
      const yAvg = costY0 + costH - avgCost / (data.totalCosts/(years.length*1.1));
      p.stroke(0);
      p.setLineDash([5,5]);
      p.line(plotX0, yAvg, plotX0+costW, yAvg);
      p.setLineDash([]);
      p.noStroke();
      p.fill(0);
      p.textAlign(p.RIGHT, p.BOTTOM);
      p.text(`annual personal compensation costs (average) = $${avgCost.toFixed(1)} per year`, plotX0-10, yAvg-5);
      // Total cost label
      p.textAlign(p.LEFT, p.TOP);
      p.text(`total personal compensation costs (until 2050) = $${data.totalCosts.toFixed(1)}`, plotX0, costY0-10);
    }

    // Budget is semi-vertical: circles at bottom
    const budgetYBase = plotY0 + plotH + 190;
    const circleXStarts = [plotX0 + plotW*0.1, plotX0 + plotW*0.3, plotX0 + plotW*0.5, plotX0 + plotW*0.7];
    const circleXLabels = [2020, 2030, 2040, 2050];
    for (let i=0;i<4;i++) {
      const year = circleXLabels[i];
      const value = 30 * annualBudget - data.cumulativeAtYear[year];
      const radius = 12 + Math.sqrt(Math.abs(value))*2;
      const styleSolid = data.effectiveAtYear[year] <= 0;
      p.stroke(0);
      p.noFill();
      p.lineCap(p.CENTER);
      if (styleSolid) {
        p.strokeWeight(2);
        p.ellipse(circleXStarts[i], budgetYBase + 50, radius*2, radius*2);
      } else {
        p.drawingContext.setLineDash([4,4]);
        p.strokeWeight(1);
        p.ellipse(circleXStarts[i], budgetYBase + 50, radius*2, radius*2);
        p.drawingContext.setLineDash([]);
      }
      // Text inside
      p.fill(0);
      p.noStroke();
      p.textAlign(p.CENTER, p.CENTER);
      p.text(`${value.toFixed(0)} tons CO₂`, circleXStarts[i], budgetYBase + 50);
      // Year label
      p.textAlign(p.CENTER, p.TOP);
      p.text(year, circleXStarts[i], budgetYBase + 70);
    }
  };
});

function redraw() {
  if (sketch && sketch.canvas) {
    sketch.redraw();
  }
}
</script>
</body>
</html>
