<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Polyhedron Net Folding Demo</title>
<style>
  body, html { margin:0; padding:0; height:100%; font-family:Arial, sans-serif; }
  #main-container { display:flex; flex-direction:column; height:100%; }
  #control-panel { background:#e0e0e0; padding:10px; display:flex; align-items:center; }
  #solid-selector { margin-right:20px; }
  .solid-btn { padding:5px 10px; margin-right:5px; background:#fff; border:1px solid #bbb; cursor:pointer; }
  .solid-btn.active { background:#bbb; }
  #fold-slider-container { margin-right:auto; }
  #slider-fold { width:200px; }
  #btn-reset-view { font-size:18px; padding:5px 10px; background:#fff; border:1px solid #bbb; cursor:pointer; }
  #canvas-container { flex-grow:1; border:1px solid #ccc; }
</style>
</head>
<body>
<div id="main-container">
  <div id="control-panel">
    <div id="solid-selector">
      <label>solid</label>
      <button id="btn-solid-1" class="solid-btn active">1</button>
      <button id="btn-solid-2" class="solid-btn">2</button>
      <button id="btn-solid-3" class="solid-btn">3</button>
    </div>
    <div id="fold-slider-container">
      <label for="slider-fold">fold-unfold</label>
      <input type="range" id="slider-fold" min="0" max="1" step="0.01" value="0">
    </div>
    <button id="btn-reset-view">+</button>
  </div>
  <div id="canvas-container"></div>
</div>

<script src="https://cdn.jsdelivr.net/npm/three@0.152.2/build/three.min.js"></script>
<script src="https://cdn.jsdelivr.net/npm/three@0.152.2/examples/js/controls/OrbitControls.js"></script>
<script>
  // ----- Global Variables -----
  let scene, camera, renderer, controls;
  let currentSolidGroup = null;
  const unfoldedColor = new THREE.Color('#F5E0E0');
  const foldedColors = ['#E47A55', '#A4C6E3', '#F4E0E1', '#F3C76E', '#A0D8EF', '#FFB347']; // for 6 faces
  const solidDefs = {1: null, 2: null, 3: null};

  // ----- Initialization -----
  initScene();
  initControls();
  createSolidDefs();
  loadSolid(1);
  animate();

  // ----- UI Event Listeners -----
  document.querySelectorAll('.solid-btn').forEach(btn=>{
    btn.addEventListener('click', ()=>{
      document.querySelectorAll('.solid-btn').forEach(b=>b.classList.remove('active'));
      btn.classList.add('active');
      const idx = parseInt(btn.id.split('-')[2],10);
      loadSolid(idx);
    });
  });

  document.getElementById('slider-fold').addEventListener('input', ()=>{
    if(currentSolidGroup) updateFold(parseFloat(this.value));
  });

  document.getElementById('btn-reset-view').addEventListener('click', ()=>{
    controls.reset();
    camera.position.set(3,3,3);
    camera.lookAt(0,0,0);
  });

  // ----- Scene Setup -----
  function initScene(){
    const canvasContainer = document.getElementById('canvas-container');
    scene = new THREE.Scene();
    scene.background = new THREE.Color('#f0f0f0');
    camera = new THREE.PerspectiveCamera(45, canvasContainer.clientWidth/canvasContainer.clientHeight, 0.1, 1000);
    renderer = new THREE.WebGLRenderer({antialias:true});
    renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
    canvasContainer.appendChild(renderer.domElement);

    const amb = new THREE.AmbientLight(0xffffff, 0.5);
    scene.add(amb);
    const dir = new THREE.DirectionalLight(0xffffff, 0.8);
    dir.position.set(5,5,5);
    scene.add(dir);

    window.addEventListener('resize', onWindowResize);
  }

  function onWindowResize(){
    const canvasContainer = document.getElementById('canvas-container');
    camera.aspect = canvasContainer.clientWidth/canvasContainer.clientHeight;
    camera.updateProjectionMatrix();
    renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
  }

  // ----- OrbitControls -----
  function initControls(){
    controls = new THREE.OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    camera.position.set(3,3,3);
    camera.lookAt(0,0,0);
  }

  // ----- Solid Definitions -----
  function createSolidDefs(){
    // Solid 1: Cube Net (6 squares)
    solidDefs[1] = createCubeNet();
    // Solid 2: Rectangular prism net (6 squares)
    solidDefs[2] = createPrismNet();
    // Solid 3: Tetrahedron net (4 triangles)
    solidDefs[3] = createTetraNet();
  }

  // Helper to create a square face
  function makeSquare(halfSize){
    const geo = new THREE.PlaneGeometry(halfSize*2, halfSize*2);
    return geo;
  }

  // Helper to create a triangular face
  function makeTriangle(v1,v2,v3){
    const geometry = new THREE.BufferGeometry();
    const vertices = new Float32Array([v1.x,v1.y,v1.z, v2.x,v2.y,v2.z, v3.x,v3.y,v3.z]);
    geometry.setAttribute('position', new THREE.BufferAttribute(vertices,3));
    geometry.computeVertexNormals();
    return geometry;
  }

  // Solid 1: Cube Net
  function createCubeNet(){
    const group = new THREE.Group();
    const half = 1;
    // Base face at origin, lying in XY plane
    const base = new THREE.Mesh(makeSquare(half), new THREE.MeshStandardMaterial({color: unfoldedColor, side:THREE.DoubleSide, flatShading:true}));
    base.userData = {foldedColor: new THREE.Color(foldedColors[0]), unfoldedColor};
    group.add(base);
    // Helper function to add pivoted face
    const addFace = (mesh, hingeEdgeStart, hingeEdgeEnd, parent, parentMesh, finalAngle, colorIdx)=>{
      const hingeVec = new THREE.Vector3().subVectors(hingeEdgeEnd, hingeEdgeStart);
      const hingeMid = new THREE.Vector3().addVectors(hingeEdgeStart, hingeEdgeEnd).multiplyScalar(0.5);
      // Create pivot
      const pivot = new THREE.Group();
      pivot.position.copy(hingeMid);
      // Compute rotation axis (normalized)
      const axis = hingeVec.clone().normalize();
      pivot.userData = {axis, finalAngle};
      // Position mesh relative to pivot
      mesh.position.copy(parentMesh.position).sub(pivot.position);
      mesh.rotation.copy(parentMesh.rotation);
      pivot.add(mesh);
      parent.add(pivot);
      // Store color
      mesh.userData = {foldedColor: new THREE.Color(foldedColors[colorIdx]), unfoldedColor};
    };
    // Define 5 additional faces
    // Left face
    const left = new THREE.Mesh(makeSquare(half), new THREE.MeshStandardMaterial({color: unfoldedColor, side:THREE.DoubleSide, flatShading:true}));
    addFace(left, new THREE.Vector3(0,0,0), new THREE.Vector3(0,half,0), base, base, Math.PI/2,1);
    // Right face
    const right = new THREE.Mesh(makeSquare(half), new THREE.MeshStandardMaterial({color: unfoldedColor, side:THREE.DoubleSide, flatShading:true}));
    addFace(right, new THREE.Vector3(half,0,0), new THREE.Vector3(half,half,0), base, base, Math.PI/2,2);
    // Top face
    const top = new THREE.Mesh(makeSquare(half), new THREE.MeshStandardMaterial({color: unfoldedColor, side:THREE.DoubleSide, flatShading:true}));
    addFace(top, new THREE.Vector3(0,half,0), new THREE.Vector3(half,half,0), base, base, Math.PI/2,3);
    // Bottom face
    const bottom = new THREE.Mesh(makeSquare(half), new THREE.MeshStandardMaterial({color: unfoldedColor, side:THREE.DoubleSide, flatShading:true}));
    addFace(bottom, new THREE.Vector3(0,0,0), new THREE.Vector3(half,0,0), base, base, Math.PI/2,4);
    // Front face (attached to right)
    const front = new THREE.Mesh(makeSquare(half), new THREE.MeshStandardMaterial({color: unfoldedColor, side:THREE.DoubleSide, flatShading:true}));
    // For front, hinge is the right face's right edge
    addFace(front, new THREE.Vector3(half,0,0), new THREE.Vector3(half,half,0), right, right, Math.PI/2,5);
    return group;
  }

  // Solid 2: Rectangular Prism Net (6 squares)
  function createPrismNet(){
    const group = new THREE.Group();
    const halfW=1, halfH=0.6, halfD=0.4;
    // Base rectangle
    const baseGeo = new THREE.PlaneGeometry(halfW*2, halfH*2);
    const base = new THREE.Mesh(baseGeo, new THREE.MeshStandardMaterial({color:unfoldedColor, side:THREE.DoubleSide, flatShading:true}));
    base.userData = {foldedColor: new THREE.Color(foldedColors[0]), unfoldedColor};
    group.add(base);
    const addFace = (mesh, hingeEdgeStart, hingeEdgeEnd, parent, parentMesh, finalAngle, colorIdx)=>{
      const hingeVec = new THREE.Vector3().subVectors(hingeEdgeEnd, hingeEdgeStart);
      const hingeMid = new THREE.Vector3().addVectors(hingeEdgeStart, hingeEdgeEnd).multiplyScalar(0.5);
      const pivot = new THREE.Group();
      pivot.position.copy(hingeMid);
      const axis = hingeVec.clone().normalize();
      pivot.userData = {axis, finalAngle};
      mesh.position.copy(parentMesh.position).sub(pivot.position);
      mesh.rotation.copy(parentMesh.rotation);
      pivot.add(mesh);
      parent.add(pivot);
      mesh.userData = {foldedColor: new THREE.Color(foldedColors[colorIdx]), unfoldedColor};
    };
    // Left side
    const leftGeo = new THREE.PlaneGeometry(halfD*2, halfH*2);
    const left = new THREE.Mesh(leftGeo, new THREE.MeshStandardMaterial({color:unfoldedColor, side:THREE.DoubleSide, flatShading:true}));
    addFace(left, new THREE.Vector3(-halfW, -halfH, 0), new THREE.Vector3(-halfW, halfH, 0), base, base, Math.PI/2,1);
    // Right side
    const rightGeo = new THREE.PlaneGeometry(halfD*2, halfH*2);
    const right = new THREE.Mesh(rightGeo, new THREE.MeshStandardMaterial({color:unfoldedColor, side:THREE.DoubleSide, flatShading:true}));
    addFace(right, new THREE.Vector3(halfW, -halfH, 0), new THREE.Vector3(halfW, halfH, 0), base, base, Math.PI/2,2);
    // Top
    const topGeo = new THREE.PlaneGeometry(halfW*2, halfD*2);
    const top = new THREE.Mesh(topGeo, new THREE.MeshStandardMaterial({color:unfoldedColor, side:THREE.DoubleSide, flatShading:true}));
    addFace(top, new THREE.Vector3(-halfW, halfH, 0), new THREE.Vector3(halfW, halfH, 0), base, base, Math.PI/2,3);
    // Bottom
    const bottomGeo = new THREE.PlaneGeometry(halfW*2, halfD*2);
    const bottom = new THREE.Mesh(bottomGeo, new THREE.MeshStandardMaterial({color:unfoldedColor, side:THREE.DoubleSide, flatShading:true}));
    addFace(bottom, new THREE.Vector3(-halfW, -halfH, 0), new THREE.Vector3(halfW, -halfH, 0), base, base, Math.PI/2,4);
    // Front (attached to right)
    const frontGeo = new THREE.PlaneGeometry(halfW*2, halfD*2);
    const front = new THREE.Mesh(frontGeo, new THREE.MeshStandardMaterial({color:unfoldedColor, side:THREE.DoubleSide, flatShading:true}));
    addFace(front, new THREE.Vector3(halfW, -halfH, 0), new THREE.Vector3(halfW, halfH, 0), right, right, Math.PI/2,5);
    return group;
  }

  // Solid 3: Tetrahedron Net (4 triangles)
  function createTetraNet(){
    const group = new THREE.Group();
    // Coordinates for a regular tetrahedron net unfolded as four triangles sharing a central vertex
    const v0 = new THREE.Vector3(0,0,0);
    const v1 = new THREE.Vector3(1,0,0);
    const v2 = new THREE.Vector3(0.5,Math.sqrt(3)/2,0);
    const v3 = new THREE.Vector3(0.5,Math.sqrt(3)/6, Math.sqrt(6)/3); // third vertex out of plane
    const baseMesh = new THREE.Mesh(makeTriangle(v0,v1,v2), new THREE.MeshStandardMaterial({color:unfoldedColor, side:THREE.DoubleSide, flatShading:true}));
    baseMesh.userData = {foldedColor: new THREE.Color(foldedColors[0]), unfoldedColor};
    group.add(baseMesh);
    // Helper to add triangular face with hinge
    const addTriFace = (vA,vB,vC, hingeStart, hingeEnd, parent, finalAngle, colorIdx)=>{
      const tri = new THREE.Mesh(makeTriangle(vA,vB,vC), new THREE.MeshStandardMaterial({color:unfoldedColor, side:THREE.DoubleSide, flatShading:true}));
      tri.userData = {foldedColor: new THREE.Color(foldedColors[colorIdx]), unfoldedColor};
      const hingeVec = new THREE.Vector3().subVectors(hingeEnd, hingeStart);
      const hingeMid = new THREE.Vector3().addVectors(hingeStart, hingeEnd).multiplyScalar(0.5);
      const pivot = new THREE.Group();
      pivot.position.copy(hingeMid);
      const axis = hingeVec.clone().normalize();
      pivot.userData = {axis, finalAngle};
      tri.position.copy(parent.position).sub(pivot.position);
      tri.rotation.copy(parent.rotation);
      pivot.add(tri);
      parent.add(pivot);
    };
    // Face 2
    addTriFace(v1,v2,v3, v1,v2, baseMesh, Math.PI/3,1);
    // Face 3
    addTriFace(v0,v2,v3, v0,v2, baseMesh, Math.PI/3,2);
    // Face 4
    addTriFace(v0,v1,v3, v0,v1, baseMesh, Math.PI/3,3);
    return group;
  }

  // ----- Loading and Updating Solids -----
  function loadSolid(index){
    // Remove old
    if(currentSolidGroup){
      scene.remove(currentSolidGroup);
    }
    currentSolidGroup = solidDefs[index];
    scene.add(currentSolidGroup);
    // Reset camera
    controls.reset();
    camera.position.set(3,3,3);
    camera.lookAt(0,0,0);
    // Apply current slider value
    const t = parseFloat(document.getElementById('slider-fold').value);
    updateFold(t);
  }

  function updateFold(t){
    if(!currentSolidGroup) return;
    // Traverse all pivots
    currentSolidGroup.traverse(node=>{
      if(node.userData && node.userData.axis && node.userData.finalAngle !== undefined){
        const angle = t * node.userData.finalAngle;
        node.quaternion.setFromAxisAngle(node.userData.axis, angle);
      }
      if(node.material && node.userData){
        node.material.color.copy(node.userData.unfoldedColor).lerp(node.userData.foldedColor, t);
        node.material.needsUpdate = true;
      }
    });
  }

  // ----- Animation Loop -----
  function animate(){
    requestAnimationFrame(animate);
    controls.update();
    renderer.render(scene, camera);
  }
</script>
</body>
</html>
