<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.9.0/p5.min.js" integrity="sha512-wmXtZ7Z/NvAtqW8GR0+32kdhDHwgaL2u/k6+Yq3VaHCgoWCnI+G3bwOqHrgC9+fDssadB+mOA7lpHwI/lYcgiQ==" crossorigin="anonymous" referrerpolicy="no-referrer"></script>
</head>
<body>
<div id="main-container">
    <div id="control-panel">
        <h3>relative volatility</h3>
        <button id="btn-reset">+</button>

        <div class="slider-row">
            <label for="slider-ac">between A and C</label>
            <input type="range" id="slider-ac" min="0.1" max="5" step="0.01" value="0.6">
            <span id="value-ac">0.60</span>
        </div>
        <div class="slider-row">
            <label for="slider-bc">between B and C</label>
            <input type="range" id="slider-bc" min="0.1" max="5" step="0.01" value="3.93">
            <span id="value-bc">3.93</span>
        </div>
    </div>
    <div id="visualization-container">
        <canvas id="ternary-plot-canvas"></canvas>
    </div>
</div>

<script>
/* ---------- Utility functions ---------- */
function lerpColor(c1, c2, t) {
    const r = c1[0] + (c2[0] - c1[0]) * t;
    const g = c1[1] + (c2[1] - c1[1]) * t;
    const b = c1[2] + (c2[2] - c1[2]) * t;
    return [r, g, b];
}
function rgbToHex([r, g, b]) {
    return "#" + ((1 << 24) + (Math.round(r) << 16) + (Math.round(g) << 8) + Math.round(b)).toString(16).slice(1);
}
function getColor(v) {
    const stops = [
        {v:0.0, col: [44, 123, 182]}, // #2c7bb6
        {v:0.25, col: [171, 217, 233]}, // #abd9e9
        {v:0.5, col: [255, 255, 191]}, // #ffffbf
        {v:0.75, col: [250, 214, 97]}, // #fdae61
        {v:1.0, col: [215, 25, 28]}   // #d7191c
    ];
    for (let i=0;i<stops.length-1;i++){
        if(v>=stops[i].v && v<=stops[i+1].v){
            const t = (v - stops[i].v)/(stops[i+1].v - stops[i].v);
            return lerpColor(stops[i].col, stops[i+1].col, t);
        }
    }
    return [0,0,0];
}

/* ---------- Barycentric to Cartesian conversion ---------- */
const PAD = 60; // padding for drawing inside canvas
const TRI_WIDTH = 600 - 2*PAD; // width of main triangle
const TRI_HEIGHT = TRI_WIDTH * Math.sqrt(3)/2;

const vertexA = [ PAD, PAD + TRI_HEIGHT ]; // bottom left
const vertexB = [ PAD + TRI_WIDTH, PAD + TRI_HEIGHT ]; // bottom right
const vertexC = [ PAD + TRI_WIDTH/2, PAD ]; // top

function baryToCartesian(b) {
    const [a,bn,c] = b; // avoid name conflict
    const x = a * vertexA[0] + bn * vertexB[0] + c * vertexC[0];
    const y = a * vertexA[1] + bn * vertexB[1] + c * vertexC[1];
    return [x,y];
}

/* ---------- Main sketch ---------- */
let sk;
let alpha_ac = 0.6;
let alpha_bc = 3.93;

function setup() {
    const canvas = createCanvas(600,550);
    canvas.parent('visualization-container');
    canvas.id('ternary-plot-canvas'); // override the id to use existing canvas
    sketchRedraw(); // initial render
}
function draw() {
    // no continuous drawing; updated only on slider change
}
function sketchRedraw() {
    noLoop();
    // read current volatility values
    alpha_ac = parseFloat(document.getElementById('slider-ac').value);
    alpha_bc = parseFloat(document.getElementById('slider-bc').value);

    // update value displays
    document.getElementById('value-ac').textContent = alpha_ac.toFixed(2);
    document.getElementById('value-bc').textContent = alpha_bc.toFixed(2);

    background(255);

    // Draw grid of small triangles (10x10)
    const N = 10;
    // Calculate small triangle size (in barycentric)
    const step = 1 / N;

    // For each small triangle we compute its 3 vertices barycentrically
    for (let i = 0; i < N; i++) {
        for (let j = 0; j < N - i; j++) {
            // Vertex 1
            let v1 = [1 - (i + 1) * step, (i + 1) * step, 0];
            // Vertex 2
            let v2 = [1 - i * step - j * step, i * step + j * step, (j + 1) * step];
            // Vertex 3
            let v3 = [1 - i * step, i * step, 0];
            // Centroid of triangle
            const centroid = [
                (v1[0] + v2[0] + v3[0]) / 3,
                (v1[1] + v2[1] + v3[1]) / 3,
                (v1[2] + v2[2] + v3[2]) / 3,
            ];
            // compute F using alpha values
            const alpha1 = alpha_ac;
            const alpha2 = alpha_bc;
            const alpha3 = 1;
            const D = alpha1 * centroid[0] + alpha2 * centroid[1] + alpha3 * centroid[2];
            const y1 = (alpha1 * centroid[0]) / D;
            const y2 = (alpha2 * centroid[1]) / D;
            const y3 = (alpha3 * centroid[2]) / D;
            const F = Math.pow(centroid[0] - y1,2) + Math.pow(centroid[1] - y2,2) + Math.pow(centroid[2] - y3,2);
            const v = F / 2; // normalize to [0,1]
            const colorRGB = getColor(v);
            fill(rgbToHex(colorRGB));
            stroke(0);
            strokeWeight(0.3);
            // Convert barycentrics to canvas coords
            const [x1, y1c] = baryToCartesian(v1);
            const [x2, y2c] = baryToCartesian(v2);
            const [x3, y3c] = baryToCartesian(v3);
            triangle(x1, y1c, x2, y2c, x3, y3c);
        }
    }

    // Draw main triangle outline
    stroke(0);
    strokeWeight(2);
    noFill();
    line(vertexA[0], vertexA[1], vertexB[0], vertexB[1]);
    line(vertexB[0], vertexB[1], vertexC[0], vertexC[1]);
    line(vertexC[0], vertexC[1], vertexA[0], vertexA[1]);

    // Draw labels A,B,C
    textSize(14);
    fill(0);
    noStroke();
    textAlign(CENTER, CENTER);
    text('A', vertexA[0], vertexA[1] + 12);
    text('B', vertexB[0], vertexB[1] + 12);
    text('C', vertexC[0], vertexC[1] - 12);

    // Draw tick marks on the three sides
    const steps = 10;
    for (let k = 0; k <= steps; k++) {
        const t = k / steps;
        // A-B side
        const [xa, ya] = lerpBetween(vertexA, vertexB, t);
        line(xa, ya, xa + 5, ya - 5);
        if(k===0||k===steps){
            text(k/steps.toFixed(1), xa, ya + 20);
        }
        // B-C side
        const [xb, yb] = lerpBetween(vertexB, vertexC, t);
        line(xb, yb, xb - 5, yb - 5);
        if(k===0||k===steps){
            text(k/steps.toFixed(1), xb, yb - 20);
        }
        // C-A side
        const [xc, yc] = lerpBetween(vertexC, vertexA, t);
        line(xc, yc, xc + 5, yc + 5);
        if(k===0||k===steps){
            text(k/steps.toFixed(1), xc, yc + 20);
        }
    }
}
function lerpBetween(p1, p2, t) {
    return [
        p1[0] + (p2[0] - p1[0]) * t,
        p1[1] + (p2[1] - p1[1]) * t,
    ];
}

/* ---------- Event listeners ---------- */
document.getElementById('slider-ac').addEventListener('input', sketchRedraw);
document.getElementById('slider-bc').addEventListener('input', sketchRedraw);
document.getElementById('btn-reset').addEventListener('click', () => {
    document.getElementById('slider-ac').value = 0.6;
    document.getElementById('slider-bc').value = 3.93;
    sketchRedraw();
});

/* ---------- Start sketch ---------- */
function sketchCallback() {
    // nothing needed here since we use global setup
}
new p5(sketchCallback);
</script>
</body>
