(() => {
    const sliders = {
        wso: document.getElementById('slider-wso'),
        n:   document.getElementById('slider-n'),
        bx:  document.getElementById('slider-bx'),
        by:  document.getElementById('slider-by'),
        bz:  document.getElementById('slider-bz')
    };
    const values = {
        wso: document.getElementById('value-wso'),
        n:   document.getElementById('value-n'),
        bx:  document.getElementById('value-bx'),
        by:  document.getElementById('value-by'),
        bz:  document.getElementById('value-bz')
    };

    // Update display spans with current slider values
    const updateDisplay = () => {
        values.wso.textContent = parseFloat(sliders.wso.value).toFixed(1);
        values.n.textContent   = sliders.n.value; // integer
        values.bx.textContent  = parseFloat(sliders.bx.value).toFixed(1);
        values.by.textContent  = parseFloat(sliders.by.value).toFixed(1);
        values.bz.textContent  = parseFloat(sliders.bz.value).toFixed(1);
    };

    // Calculate range of k
    const kArray = [];
    for (let k = -5; k <= 5 + 1e-6; k += 0.1) {
        kArray.push(+k.toFixed(2));
    }

    const updatePlot = () => {
        const n  = parseInt(sliders.n.value, 10);
        const wso = parseFloat(sliders.wso.value);
        const bx  = parseFloat(sliders.bx.value);
        const by  = parseFloat(sliders.by.value);
        const bz  = parseFloat(sliders.bz.value);

        const baseEnergy = 2*n + 1;
        const traceData = [];
        const sqrtTerm = (k) => Math.sqrt(Math.pow(bz + wso*k, 2) + bx*bx + by*by);

        const allZero = Math.abs(wso) < 1e-6 && Math.abs(bx) < 1e-6 &&
                        Math.abs(by) < 1e-6 && Math.abs(bz) < 1e-6;

        if (allZero) {
            const y = kArray.map(k => baseEnergy + k*k);
            traceData.push({
                x: kArray,
                y: y,
                mode: 'lines',
                line: { color: 'red', dash: 'solid', width: 2 }
            });
        } else {
            const yPlus  = kArray.map(k => baseEnergy + k*k + sqrtTerm(k));
            const yMinus = kArray.map(k => baseEnergy + k*k - sqrtTerm(k));
            traceData.push({
                x: kArray,
                y: yPlus,
                mode: 'lines',
                line: { color: 'red', dash: 'solid', width: 2 }
            });
            traceData.push({
                x: kArray,
                y: yMinus,
                mode: 'lines',
                line: { color: 'red', dash: 'dash', width: 2 }
            });
        }

        // Build dynamic title
        const params = [];
        params.push(`n = ${n}`);
        if (Math.abs(bx) > 1e-6) params.push(`BX = ${bx.toFixed(1)}`);
        if (Math.abs(by) > 1e-6) params.push(`BY = ${by.toFixed(1)}`);
        if (Math.abs(bz) > 1e-6) params.push(`BZ = ${bz.toFixed(1)}`);
        if (Math.abs(wso) > 1e-6) params.push(`wso = ${wso.toFixed(1)}`);
        const title = params.join(', ');

        const layout = {
            title: title,
            xaxis: {
                title: 'normalized k length (lwky)',
                range: [-5, 5],
                zeroline: true
            },
            yaxis: {
                title: '2 E / &#x210F;&omega;',
                range: [0, 40],
                zeroline: true
            }
        };

        Plotly.react('plot-container', traceData, layout);
    };

    // Attach events
    Object.values(sliders).forEach(slider => {
        slider.addEventListener('input', () => {
            updateDisplay();
            updatePlot();
        });
    });

    // Initial rendering
    updateDisplay();
    updatePlot();
})();
</script>
