<script>
const EPS = 1e-6;

// Global arrays for p5.js
let p1Segments = [], p2Segments = [], intersections = [], vertices = [];
let p5Instance = null;

// ---------- Utility Functions ----------
function toFraction(num, tolerance = 1e-6, maxDen = 20) {
  const eps = tolerance;
  const n = Math.round(num);
  if (Math.abs(num - n) < eps) return `${n}`;
  for (let den = 1; den <= maxDen; den++) {
    const numInt = Math.round(num * den);
    if (Math.abs(num - numInt / den) < eps) {
      const g = gcd(Math.abs(numInt), den);
      return `${numInt / g}/${den / g}`;
    }
  }
  return num.toFixed(3);
}
function gcd(a, b) { while (b) { const t = b; b = a % b; a = t; } return a; }

function updateValueDisplay(id, val) {
  document.getElementById(`value-${id}`).textContent = val;
}

function adjustSlider(id, delta) {
  const slider = document.getElementById(`slider-${id}`);
  let newVal = parseInt(slider.value) + delta;
  newVal = Math.min(10, Math.max(-10, newVal));
  slider.value = newVal;
  updateValueDisplay(id, newVal);
  updateAll();
}

// ---------- Payoff Table ----------
function updateTable(a, b) {
  document.getElementById('cell-11').textContent = `${a.a11}, ${b.b11}`;
  document.getElementById('cell-12').textContent = `${a.a12}, ${b.b12}`;
  document.getElementById('cell-21').textContent = `${a.a21}, ${b.b21}`;
  document.getElementById('cell-22').textContent = `${a.a22}, ${b.b22}`;
}

// ---------- Best Response Calculations ----------
function computePlayer1Segments(a) {
  const denom = a.a11 - a.a12 - a.a21 + a.a22;
  if (Math.abs(denom) < EPS) {
    // Dominant strategy
    if (a.a12 - a.a22 > 0) { // prefer strategy 1
      return [{type:'vertical', x:1, y1:0, y2:1}];
    } else {
      return [{type:'vertical', x:0, y1:0, y2:1}];
    }
  }
  const yIndiff = (a.a22 - a.a12) / denom;
  let segs = [];
  segs.push({type:'horizontal', y:yIndiff, x1:0, x2:1});
  if (denom > 0) {
    segs.push({type:'vertical', x:0, y1:0, y2:yIndiff});
    segs.push({type:'vertical', x:1, y1:yIndiff, y2:1});
  } else {
    segs.push({type:'vertical', x:0, y1:yIndiff, y2:1});
    segs.push({type:'vertical', x:1, y1:0, y2:yIndiff});
  }
  return segs;
}

function computePlayer2Segments(b) {
  const denom = b.b11 - b.b12 - b.b21 + b.b22;
  if (Math.abs(denom) < EPS) {
    if (b.b12 - b.b22 > 0) { // prefer strategy1 (y=1)
      return [{type:'horizontal', y:1, x1:0, x2:1}];
    } else {
      return [{type:'horizontal', y:0, x1:0, x2:1}];
    }
  }
  const xIndiff = (b.b22 - b.b21) / denom;
  let segs = [];
  segs.push({type:'vertical', x:xIndiff, y1:0, y2:1});
  if (denom > 0) {
    segs.push({type:'horizontal', y:0, x1:0, x2:xIndiff});
    segs.push({type:'horizontal', y:1, x1:xIndiff, x2:1});
  } else {
    segs.push({type:'horizontal', y:0, x1:xIndiff, x2:1});
    segs.push({type:'horizontal', y:1, x1:0, x2:xIndiff});
  }
  return segs;
}

// ---------- Intersections ----------
function computeIntersections(segA, segB) {
  const intersections = [];
  segA.forEach(a => {
    segB.forEach(b => {
      if (a.type === 'vertical' && b.type === 'vertical') {
        if (Math.abs(a.x - b.x) < EPS) {
          const y1 = Math.max(Math.min(a.y1, a.y2), Math.min(b.y1, b.y2));
          const y2 = Math.min(Math.max(a.y1, a.y2), Math.max(b.y1, b.y2));
          if (y2 - y1 > EPS) {
            intersections.push({type:'segment', mode:'vertical', x:a.x, y1, y2});
          } else if (Math.abs(y2 - y1) < EPS) {
            intersections.push({type:'point', x:a.x, y:y1});
          }
        }
      } else if (a.type === 'horizontal' && b.type === 'horizontal') {
        if (Math.abs(a.y - b.y) < EPS) {
          const x1 = Math.max(Math.min(a.x1, a.x2), Math.min(b.x1, b.x2));
          const x2 = Math.min(Math.max(a.x1, a.x2), Math.max(b.x1, b.x2));
          if (x2 - x1 > EPS) {
            intersections.push({type:'segment', mode:'horizontal', y:a.y, x1, x2});
          } else if (Math.abs(x2 - x1) < EPS) {
            intersections.push({type:'point', x:x1, y:a.y});
          }
        }
      } else {
        let vert, horiz;
        if (a.type === 'vertical' && b.type === 'horizontal') {
          vert = a; horiz = b;
        } else if (a.type === 'horizontal' && b.type === 'vertical') {
          vert = b; horiz = a;
        } else return;
        const ix = vert.x;
        const iy = horiz.y;
        if (ix >= Math.min(horiz.x1, horiz.x2)-EPS && ix <= Math.max(horiz.x1, horiz.x2)+EPS &&
            iy >= Math.min(vert.y1, vert.y2)-EPS && iy <= Math.max(vert.y1, vert.y2)+EPS) {
          intersections.push({type:'point', x:ix, y:iy});
        }
      }
    });
  });
  return intersections;
}

// ---------- Vertices ----------
function computeVertices(intersections) {
  const verts = [];
  const add = v => {
    if (!verts.some(u => Math.abs(u.x - v.x) < EPS && Math.abs(u.y - v.y) < EPS)) verts.push(v);
  };
  intersections.forEach(ip => {
    if (ip.type === 'point') add({x:ip.x, y:ip.y});
    else if (ip.type === 'segment') {
      if (ip.mode === 'vertical') {
        add({x:ip.x, y:ip.y1});
        add({x:ip.x, y:ip.y2});
      } else {
        add({x:ip.x1, y:ip.y});
        add({x:ip.x2, y:ip.y});
      }
    }
  });
  return verts;
}

// ---------- Update All ----------
function updateAll() {
  // Get current values
  const a = {
    a11: parseInt(document.getElementById('slider-a11').value),
    a12: parseInt(document.getElementById('slider-a12').value),
    a21: parseInt(document.getElementById('slider-a21').value),
    a22: parseInt(document.getElementById('slider-a22').value)
  };
  const b = {
    b11: parseInt(document.getElementById('slider-b11').value),
    b12: parseInt(document.getElementById('slider-b12').value),
    b21: parseInt(document.getElementById('slider-b21').value),
    b22: parseInt(document.getElementById('slider-b22').value)
  };

  // Update payoff table
  updateTable(a, b);

  // Compute best response segments
  p1Segments = computePlayer1Segments(a);
  p2Segments = computePlayer2Segments(b);

  // Compute intersections
  intersections = computeIntersections(p1Segments, p2Segments);

  // Compute vertices
  vertices = computeVertices(intersections);

  // Update equilibria display
  let eqStr = '\\( \\{ ';
  if (vertices.length === 0) {
    eqStr += '\\} \\)';
  } else {
    eqStr += vertices.map(v => {
      const fx = toFraction(v.x);
      const fy = toFraction(v.y);
      return `(${fx},${fy})`;
    }).join(', ');
    eqStr += ' \\} \\)';
  }
  document.getElementById('equilibria-display').textContent = eqStr;
  if (window.MathJax) MathJax.typesetPromise([{html: document.getElementById('equilibria-display')}]);

  // Redraw canvas
  if (p5Instance) p5Instance.redraw();
}

// ---------- Event Listeners ----------
function attachListeners() {
  // Slider change listeners
  ['a11','a12','a21','a22','b11','b12','b21','b22'].forEach(id => {
    const slider = document.getElementById(`slider-${id}`);
    slider.addEventListener('input', e => {
      const val = e.target.value;
      updateValueDisplay(id, val);
      updateAll();
    });

    // Increment buttons
    document.getElementById(`btn-plus-${id}`).addEventListener('click', () => adjustSlider(id, 1));
    document.getElementById(`btn-minus-${id}`).addEventListener('click', () => adjustSlider(id, -1));
  });
}

// ---------- p5.js Sketch ----------
const sketch = p => {
  let w = 400, h = 400;
  p.noLoop();

  p.setup = () => {
    p5Instance = p.createCanvas(w, h);
    p5Instance.parent('canvas-container');
    p.frameRate(30);
  };

  p.draw = () => {
    const margin = 40;
    const scaleX = w - 2*margin;
    const scaleY = h - 2*margin;

    const toPixels = (x, y) => {
      return {x: margin + x*scaleX, y: margin + (1-y)*scaleY};
    };

    p.background(255);

    // Axes    p.stroke(0);
    p.strokeWeight(1);
    let line0 = toPixels(0,0), line1 = toPixels(1,0);
    p.line(line0.x, line0.y, line1.x, line1.y); // x-axis
    line0 = toPixels(0,0); line1 = toPixels(0,1);
    p.line(line0.x, line0.y, line1.x, line1.y); // y-axis

    // Ticks and labels
    for (let t = 0; t <= 1; t += 0.2) {
      const pos = toPixels(t,0);
      p.line(pos.x-3, pos.y-3, pos.x+3, pos.y+3);
      p.noStroke(); p.fill(0); p.textSize(10); p.text(t.toFixed(), pos.x-14, pos.y+12);
      p.stroke(0);
      const posy = toPixels(0,t);
      p.line(posy.x-3, posy.y-3, posy.x+3, posy.y+3);
      p.noStroke(); p.fill(0); p.textSize(10); p.text(t.toFixed(1), posy.x-18, posy.y-4);
      p.stroke(0);
    }

    // Axis labels
    p.noStroke(); p.fill(0); p.textSize(12); p.text('x₁', toPixels(1,0).x+10, toPixels(1,0).y-5);
    p.text('y₁', toPixels(0,1).x-18, toPixels(0,1).y-5);

    // Player 1 best response (blue)
    p.stroke('blue'); p.strokeWeight(2);
    p1Segments.forEach(seg => {
      if (seg.type === 'horizontal') {
        const a = toPixels(seg.x1, seg.y);
        const b = toPixels(seg.x2, seg.y);
        p.line(a.x, a.y, b.x, b.y);
      } else if (seg.type === 'vertical') {
        const a = toPixels(seg.x, seg.y1);
        const b = toPixels(seg.x, seg.y2);
        p.line(a.x, a.y, b.x, b.y);
      }
    });

    // Player 2 best response (green)
    p.stroke('green'); p.strokeWeight(2);
    p2Segments.forEach(seg => {
      if (seg.type === 'horizontal') {
        const a = toPixels(seg.x1, seg.y);
        const b = toPixels(seg.x2, seg.y);
        p.line(a.x, a.y, b.x, b.y);
      } else if (seg.type === 'vertical') {
        const a = toPixels(seg.x, seg.y1);
        const b = toPixels(seg.x, seg.y2);
        p.line(a.x, a.y, b.x, b.y);
      }
    });

    // Nash equilibria (red)
    p.stroke('red'); p.strokeWeight(4);
    intersections.forEach(ip => {
      if (ip.type === 'segment') {
        if (ip.mode === 'vertical') {
          const a = toPixels(ip.x, ip.y1);
          const b = toPixels(ip.x, ip.y2);
          p.line(a.x, a.y, b.x, b.y);
        } else { // horizontal
          const a = toPixels(ip.x1, ip.y);
          const b = toPixels(ip.x2, ip.y);
          p.line(a.x, a.y, b.x, b.y);
        }
      } else if (ip.type === 'point') {
        const pxy = toPixels(ip.x, ip.y);
        p.ellipse(pxy.x, pxy.y, 8, 8);
      }
    });

    // Vertex circles
    vertices.forEach(v => {
      const pxy = toPixels(v.x, v.y);
      p.fill('red'); p.noStroke(); p.ellipse(pxy.x, pxy.y, 8, 8);
    });
  };
};

document.addEventListener('DOMContentLoaded', () => {
  attachListeners();
  updateAll(); // initial draw
  new p5(sketch, document.getElementById('canvas-container'));
});
</script>
</body>
</html>
