<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="utf-8">
<title>Cubic & Osculating Quadratic Demo</title>
<script src="https://cdn.jsdelivr.net/npm/p5@1.6.0/lib/p5.min.js"></script>
<style>
  body{font-family:Arial,Helvetica,sans-serif;background:#fafafa;margin:0;padding:0;}
  #demo-container{max-width:800px;margin:0 auto;padding:10px;}
  #controls-container{margin-bottom:10px;background:#e0e0e0;padding:10px;border-radius:5px;}
  #slider-group, #checkbox-group{margin-top:10px;}
  label{margin-right:8px;}
  input[type=range]{width:200px;}
  input[type=number]{width:60px;}
  button{font-family:'Courier New', monospace;font-size:14px;}
  #canvas-container{border:1px solid #bbb;background:#fff;}
</style>
</head>
<body>
<div id="demo-container">
  <div id="controls-container">
    <div id="slider-group">
      <label for="slider-t">choose quadratic</label>
      <input type="range" id="slider-t" min="-5" max="5" step="0.01" value="0.5">
      <input type="number" id="input-t" min="-5" max="5" step="0.01" value="0.5">
      <button id="btn-reset" title="Reset">&#x21BB;</button>
    </div>
    <div id="checkbox-group">
      <input type="checkbox" id="checkbox-family">
      <label for="checkbox-family">family</label>
    </div>
  </div>
  <div id="canvas-container"></div>
</div>

<script>
/* -------------------- Constants --------------------------------- */
const WORLD_X_MIN = -6, WORLD_X_MAX = 6;
const WORLD_Y_MIN = -5, WORLD_Y_MAX = 5;
const SCALE = 50; // pixels per unit
const LOCATOR_HIT_RADIUS_PX = 10; // for detecting mouse
const FAMILY_T_STEP = 0.5;
const FAMILY_T_MIN = -5, FAMILY_T_MAX = 5;
const FAMILY_PALETTE = ['#e41a1c','#377eb8','#4daf4a','#984ea3','#ff7f00','#ffff33',
                        '#a65628','#f781bf','#999999'];

/* -------------------- Initial Data -------------------------------- */
const INITIAL_LOCATORS = [
  {x:-4, y:1},
  {x:-2, y:1.8},
  {x:1,  y:-1},
  {x:4,  y:-1}
];
let locators = JSON.parse(JSON.stringify(INITIAL_LOCATORS));
let cubicCoeffs = [];   // [a,b,c,d]
let quadraticCoeffs = []; // [p,q,r]
let draggedIndex = null;

/* -------------------- DOM Elements -------------------------------- */
const sliderT = document.getElementById('slider-t');
const inputT  = document.getElementById('input-t');
const checkboxFamily = document.getElementById('checkbox-family');
const btnReset = document.getElementById('btn-reset');
const canvasContainer = document.getElementById('canvas-container');

/* -------------------- p5.js Sketch -------------------------------- */
new p5((p) => {
  let myCanvas;

  p.setup = () => {
    myCanvas = p.createCanvas(600, 500);
    myCanvas.parent(canvasContainer);
    p.noLoop();
    recalcCoeffs();
  };

  /* Convert world to screen coordinates */
  function wx2sx(wx){ return p.width/2 + wx*SCALE; }
  function wy2sy(wy){ return p.height/2 - wy*SCALE; }
  function sx2wx(sx){ return (sx - p.width/2)/SCALE; }
  function sy2wy(sy){ return (p.height/2 - sy)/SCALE; }

  /* Draw Axes with ticks */
  function drawAxes(){
    p.stroke('#ccc'); p.strokeWeight(1);
    // X axis
    p.line(wx2sx(WORLD_X_MIN), wy2sy(0), wx2sx(WORLD_X_MAX), wy2sy(0));
    // Y axis
    p.line(wx2sx(0), wy2sy(WORLD_Y_MIN), wx2sx(0), wy2sy(WORLD_Y_MAX));

    p.stroke('#eee'); p.strokeWeight(0.5);
    // X ticks
    for(let x= Math.ceil(WORLD_X_MIN); x<=Math.floor(WORLD_X_MAX); x++){
      let sx = wx2sx(x);
      p.line(sx, wy2sy(-0.2), sx, wy2sy(0.2));
      p.noStroke(); p.fill('#666'); p.textSize(10);
      p.text(x, sx-4, wy2sy(0)+12);
      p.stroke('#eee');
    }
    // Y ticks
    for(let y= Math.ceil(WORLD_Y_MIN); y<=Math.floor(WORLD_Y_MAX); y++){
      let sy = wy2sy(y);
      p.line(wx2sx(-0.2), sy, wx2sx(0.2), sy);
      p.noStroke(); p.fill('#666'); p.textSize(10);
      if(y!==0) p.text(y, wx2sx(0)+5, sy+3);
      p.stroke('#eee');
    }
  }

  /* Draw Locator (circle with cross) */
  function drawLocator(loc){
    const sx = wx2sx(loc.x), sy = wy2sy(loc.y);
    p.noStroke(); p.fill('#fff'); p.stroke('#000'); p.strokeWeight(2);
    p.ellipse(sx, sy, 20, 20);
    p.line(sx-10, sy-10, sx+10, sy+10);
    p.line(sx-10, sy+10, sx+10, sy-10);
  }

  /* Draw a curve from coefficients */
  function drawCurve(coeffs, color, weight, type){
    p.stroke(color); p.strokeWeight(weight); p.noFill();
    p.beginShape();
    const step = 0.02;
    if(type===4){
      for(let wx= WORLD_X_MIN; wx<=WORLD_X_MAX; wx+=step){
        const wy = coeffs[0]*wx*wx*wx + coeffs[1]*wx*wx + coeffs[2]*wx + coeffs[3];
        p.vertex(wx2sx(wx), wy2sy(wy));
      }
    }else{ // quadratic
      for(let wx= WORLD_X_MIN; wx<=WORLD_X_MAX; wx+=step){
        const wy = coeffs[0]*wx*wx + coeffs[1]*wx + coeffs[2];
        p.vertex(wx2sx(wx), wy2sy(wy));
      }
    }
    p.endShape();
  }

  /* Draw family of quadratics if needed */
  function drawFamily(){
    const tValues = [];
    for(let t=FAMILY_T_MIN; t<=FAMILY_T_MAX; t+=FAMILY_T_STEP) tValues.push(t);
    tValues.forEach((t, idx)=>{
      const [p,q,r] = computeQuadraticCoeffs(t);
      if(Math.abs(p) < 1e-6) return; // skip near linear
      const colorHex = FAMILY_PALETTE[idx % FAMILY_PALETTE.length];
      p.fill(colorHex, 90); p.stroke(colorHex); p.strokeWeight(1);
      p.beginShape();
      if( p > 0 ){ // upward opening => fill above curve to top
        p.vertex(wx2sx(WORLD_X_MIN), wy2sy(WORLD_Y_MAX));
        for(let wx= WORLD_X_MIN; wx<=WORLD_X_MAX; wx+=0.02){
          const wy = p*wx*wx + q*wx + r;
          p.vertex(wx2sx(wx), wy2sy(wy));
        }
        p.vertex(wx2sx(WORLD_X_MAX), wy2sy(WORLD_Y_MAX));
      }else{ // downward opening => fill below curve to bottom
        p.vertex(wx2sx(WORLD_X_MIN), wy2sy(WORLD_Y_MIN));
        for(let wx= WORLD_X_MIN; wx<=WORLD_X_MAX; wx+=0.02){
          const wy = p*wx*wx + q*wx + r;
          p.vertex(wx2sx(wx), wy2sy(wy));
        }
        p.vertex(wx2sx(WORLD_X_MAX), wy2sy(WORLD_Y_MIN));
      }
      p.endShape(p.CLOSE);
    });
  }

  /* p5 Draw Loop */
  p.draw = () => {
    p.background('#fff');
    drawAxes();

    // Draw family if checked
    if(checkboxFamily.checked){
      drawFamily();
    }else{
      // draw cubic
      drawCurve(cubicCoeffs, '#3f51b5', 3, 4);
    }

    // draw locators on top
    locators.forEach(drawLocator);

    // draw selected quadratic
    drawCurve(quadraticCoeffs, '#f57c00', 3, 3);
  };

  /* Mouse Interactions */
  p.mousePressed = () => {
    const mx = sx2wx(p.mouseX), my = sy2wy(p.mouseY);
    const hitR = LOCATOR_HIT_RADIUS_PX / SCALE;
    locators.forEach((loc,i)=>{
      const dx = mx - loc.x, dy = my - loc.y;
      if(dx*dx + dy*dy <= hitR*hitR) draggedIndex = i;
    });
  };
  p.mouseDragged = () => {
    if(draggedIndex!==null){
      locators[draggedIndex].x = sx2wx(p.mouseX);
      locators[draggedIndex].y = sy2wy(p.mouseY);
      recalcCoeffs();
      p.redraw();
    }
  };
  p.mouseReleased = () => { draggedIndex = null; };
});

/* -------------------- Utility Functions ----------------------------- */
function solveLinear4(M,b){
  const n=4;
  // Augmented matrix
  const A = [];
  for(let i=0;i<n;i++){
    A[i] = M[i].slice();
    A[i].push(b[i]);
  }
  // Forward elimination
  for(let i=0;i<n;i++){
    // Pivot
    let maxRow=i;
    for(let r=i+1;r<n;r++){
      if(Math.abs(A[r][i])>Math.abs(A[maxRow][i])) maxRow=r;
    }
    if(Math.abs(A[maxRow][i])<1e-12) continue;
    [A[i],A[maxRow]]=[A[maxRow],A[i]];
    const pivot=A[i][i];
    for(let j=i;j<=n;j++) A[i][j]/=pivot;
    for(let r=0;r<n;r++){
      if(r===i) continue;
      const factor=A[r][i];
      for(let j=i;j<=n;j++) A[r][j]-=factor*A[i][j];
    }
  }
  const sol=[];
  for(let i=0;i<n;i++) sol[i]=A[i][n];
  return sol;
}

function solveCubicCoeffs(points){
  const M = [
    [Math.pow(points[0].x,3), Math.pow(points[0].x,2), points[0].x, 1],
    [Math.pow(points[1].x,3), Math.pow(points[1].x,2), points[1].x, 1],
    [Math.pow(points[2].x,3), Math.pow(points[2].x,2), points[2].x, 1],
    [Math.pow(points[3].x,3), Math.pow(points[3].x,2), points[3].x, 1]
  ];
  const bvec = [points[0].y, points[1].y, points[2].y, points[3].y];
  return solveLinear4(M,bvec);
}

function computeQuadraticCoeffs(t){
  const [a,b,c,d] = cubicCoeffs;
  const p = 3*a*t + b;
  const q = -3*a*t*t + c;
  const r = a*t*t*t + d;
  return [p,q,r];
}

function recalcCoeffs(){
  cubicCoeffs = solveCubicCoeffs(locators);
  const tVal = parseFloat(sliderT.value);
  quadraticCoeffs = computeQuadraticCoeffs(tVal);
}

/* -------------------- Event Listeners ------------------------------ */
sliderT.addEventListener('input', ()=> {
  inputT.value = sliderT.value;
  recalcCoeffs();
  p5.loop(); // redrawing
});
inputT.addEventListener('input', ()=> {
  let val = parseFloat(inputT.value);
  if(isNaN(val)) val = 0.5;
  val = Math.min(Math.max(val,-5),5);
  sliderT.value = val.toFixed(2);
  inputT.value = val.toFixed(2);
  recalcCoeffs();
  p5.loop();
});
checkboxFamily.addEventListener('change', ()=> {
  p5.loop();
});
btnReset.addEventListener('click', ()=> {
  locators = JSON.parse(JSON.stringify(INITIAL_LOCATORS));
  sliderT.value = '0.5'; inputT.value = '0.5';
  checkboxFamily.checked = false;
  recalcCoeffs();
  p5.loop();
});
</script>
</body>
</html>
