<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="utf-8">
<title>Charles's Law Demo</title>
<link rel="preload" as="script" href="https://cdn.plot.ly/plotly-latest.min.js">
<link rel="preload" as="script" href="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js">
<style>
  body {font-family: Arial, sans-serif; margin:0; padding:0; background:#f9f9f9;}
  #demo-container {display:flex; flex-direction:column; align-items:center; width:100%; max-width:1200px; margin:auto; padding:20px;}
  #control-panel {display:flex; flex-wrap:wrap; gap:20px; margin-bottom:20px; width:100%; justify-content:center;}
  #control-panel label {display:block; font-weight:bold; margin-bottom:4px;}
  #control-panel input[type="range"] {width:200px;}
  #control-panel .unit-group {display:flex; align-items:center;}
  #control-panel .unit-group input {margin-left:5px; margin-right:2px;}
  #control-panel .unit-group label {margin-right:10px;}
  #visualization-area {display:flex; gap:20px; width:100%; flex-wrap:wrap; justify-content:center;}
  #plot-container, #piston-container {background:#fff; border:1px solid #ccc; padding:10px; box-shadow:2px 2px 5px rgba(0,0,0,.1);}
  #plot-container {flex:2;}
  #piston-container {flex:1; max-width:350px;}
  input[type="radio"] {margin-left:8px;}
</style>
</head>
<body>
<div id="demo-container">
  <div id="control-panel">
    <div>
      <label for="slider-v0">initial volume v₀ <span id="value-v0"></span></label>
      <input type="range" id="slider-v0" min="100" max="500" step="1" value="211">
    </div>
    <div>
      <label for="slider-T">temperature T <span id="value-T"></span></label>
      <input type="range" id="slider-T" min="-273" max="727" step="1" value="270">
    </div>
    <div class="unit-group">
      <label>temperature units</label>
      <input type="radio" id="radio-kelvin" name="temp-units" value="K">
      <label for="radio-kelvin">Kelvin</label>
      <input type="radio" id="radio-celsius" name="temp-units" value="C" checked>
      <label for="radio-celsius">Celsius</label>
    </div>
  </div>
  <div id="visualization-area">
    <div id="plot-container" style="width:100%;height:500px;"></div>
    <div id="piston-container" style="width:100%;height:500px;"></div>
  </div>
</div>
<script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
<script>
/* ---------- Global state ---------- */
let v0 = 211;                // initial volume at T_ref
const T_ref = 273.15;        // reference temperature in K
let k = v0 / T_ref;          // proportionality constant
let tempUnit = 'C';          // 'C' or 'K'
let T_current = 270;         // value displayed on slider
let currentVolume = k * getTK(); // current volume
let currentTempK = getTK();  // current temperature in Kelvin

/* ---------- Utility functions ---------- */
function getTK() {
  return tempUnit === 'C' ? T_current + T_ref : T_current;
}
function getVolume(tK) { return k * tK; }
function hexToRgb(hex) {
  hex = hex.replace('#','');
  return {r:parseInt(hex.slice(0,2),16), g:parseInt(hex.slice(2,4),16), b:parseInt(hex.slice(4,6),16)};
}
function rgbToHex(r,g,b) {
  return "#" + ((1<<24)+(r<<16)+(g<<8)+b).toString(16).slice(1);
}
function lerpColor(c1, c2, t) {
  return {
    r: Math.round(c1.r + (c2.r - c1.r) * t),
    g: Math.round(c1.g + (c2.g - c1.g) * t),
    b: Math.round(c1.b + (c2.b - c1.b) * t)
  };
}
function getColorForT(tempK) {
  const c1 = hexToRgb('#800080'); // purple
  const c2 = hexToRgb('#FFFF00'); // yellow
  const c3 = hexToRgb('#FF0000'); // red
  if (tempK <= 500) {
    const t = tempK / 500;
    const clr = lerpColor(c1,c2,t);
    return rgbToHex(clr.r,clr.g,clr.b);
  } else {
    const t = (tempK - 500) / 500;
    const clr = lerpColor(c2,c3,t);
    return rgbToHex(clr.r,clr.g,clr.b);
  }
}

/* ---------- DOM references ---------- */
const sliderV0 = document.getElementById('slider-v0');
const sliderT  = document.getElementById('slider-T');
const radioKelvin = document.getElementById('radio-kelvin');
const radioCelsius = document.getElementById('radio-celsius');
const valueV0 = document.getElementById('value-v0');
const valueT = document.getElementById('value-T');

/* ---------- Update UI values display ---------- */
function updateValueDisplays() {
  valueV0.textContent = sliderV0.value;
  valueT.textContent = sliderT.value;
}

/* ---------- Plot Update ---------- */
function updatePlot() {
  const minT = tempUnit==='C'?-273:0;
  const maxT = tempUnit==='C'?727:1000;
  const step = 1;
  const x_vals = [];
  const y_vals = [];
  for (let t = minT; t <= maxT; t+=step) {
    const tK = tempUnit==='C'? t+T_ref : t;
    const V = k * tK;
    x_vals.push(t);
    y_vals.push(V);
  }
  const lineTrace = {
    x: x_vals,
    y: y_vals,
    mode: 'lines',
    name: "Charles's Law",
    line:{color:'steelblue'},
    hoverinfo:'none'
  };
  const V_current = getVolume(getTK());
  const markerTrace = {
    x: [T_current],
    y: [V_current],
    mode: 'markers',
    marker:{
      color: getColorForT(getTK()),
      size:12
    }
  };
  const layout = {
    xaxis: {title:{text:'Temperature '+ (tempUnit==='C'?'°C':'K'), range:[minT-5,maxT+5]}, zeroline:false},
    yaxis: {title:{text:'Volume'}, range:[0,2000], zeroline:false},
    margin:{l:50,r:20,t:20,b:50},
    showlegend:false
  };
  Plotly.react('plot-container', [lineTrace, markerTrace], layout);
}

/* ---------- Piston Update ---------- */
let pistonSketch; // will hold the p5 instance
function updatePiston() {
  currentVolume = getVolume(getTK());
  currentTempK = getTK();
  if (pistonSketch) pistonSketch.redraw();
}

/* ---------- Slider event handlers ---------- */
sliderV0.addEventListener('input', function(){
  v0 = parseInt(this.value);
  k = v0 / T_ref;
  updatePlot();
  updatePiston();
  updateValueDisplays();
});
sliderT.addEventListener('input', function(){
  T_current = parseInt(this.value);
  updatePlot();
  updatePiston();
  updateValueDisplays();
});

/* ---------- Radio button event handlers ---------- */
radioKelvin.addEventListener('change', function(){
  if (this.checked) switchUnit('K');
});
radioCelsius.addEventListener('change', function(){
  if (this.checked) switchUnit('C');
});
function switchUnit(newUnit) {
  let val = parseFloat(sliderT.value);
  if (tempUnit!==newUnit) {
    if (newUnit==='K') {
      val = val + T_ref; // C to K
      sliderT.min = 0;
      sliderT.max = 1000;
    } else { // Celsius
      val = val - T_ref; // K to C
      sliderT.min = -273;
      sliderT.max = 727;
    }
    sliderT.value = Math.round(val);
    T_current = parseInt(val);
    tempUnit = newUnit;
    updatePlot();
    updatePiston();
    updateValueDisplays();
  }
}

/* ---------- P5.js sketch ---------- */
const pistonSketch = function(p){
  let canvas;
  const cylX=80, cylY=30, cylW=40, cylH=350;
  const pistonH=20, pistonW=60;
  function mapV(t){ // returns pistonTop position
    const gasHeight = p.map(currentVolume,0,2000,cylH,0);
    const gasTop = cylY + gasHeight;
    const pistonTop = gasTop - pistonH;
    return pistonTop;
  }
  function gasColor(){ return p.color(...hexToP5Color(getColorForT(currentTempK))); }
  function hexToP5Color(hex){
    const r = parseInt(hex.slice(1,3),16);
    const g = parseInt(hex.slice(3,5),16);
    const b = parseInt(hex.slice(5,7),16);
    return [r,g,b];
  }
  p.setup = function(){
    canvas = p.createCanvas(300,400);
    canvas.parent('piston-container');
    p.noLoop();
    p.stroke(0);
  };
  p.draw = function(){
    p.background(240);
    // cylinder walls
    p.stroke(80,80,200);
    p.strokeWeight(2);
    p.noFill();
    p.rect(cylX, cylY, cylW, cylH);

    // gas
    const gasHeight = p.map(currentVolume,0,2000,cylH,0);
    const gasTop = cylY + gasHeight;
    p.fill(...hexToP5Color(getColorForT(currentTempK)));
    p.rect(cylX, gasTop, cylW, gasHeight);

    // piston
    const pistonTop = gasTop - pistonH;
    p.fill(190);
    p.rect(cylX-10, pistonTop, pistonW, pistonH);

    // pressure arrow
    const arrowX = cylX + cylW/2;
    const arrowY = pistonTop - 15;
    p.fill(255,165,0);
    p.triangle(arrowX, arrowY-10, arrowX-10, arrowY, arrowX+10, arrowY);
  };
};
new p5(pistonSketch);

/* ---------- Initialize ---------- */
updateValueDisplays();
updatePlot();
updatePiston();
</script>
</body>
</html>
