<body>
  <h1>Circles &amp; Triangles</h1>
  <p>
    Circles have 360 degrees in total, and the sum of two inscribed
    angles that intercept the same arc is always 180 degrees.
    This interactive demo lets you explore these relationships by
    moving points on a circle, selecting a vertex, and watching the
    corresponding angles and arcs update in real time.
  </p>
  <div id="main-container">
    <div id="canvas-container"></div>
    <div id="info-panel">
      <span class="info-header">Arc</span>
      <span class="info-header">Vertex</span>
      <span class="info-header"></span>
      <span class="info-header"></span>

      <span id="arc-label-1"></span>
      <span id="arc-value-1"></span>
      <span id="vertex-label-1"></span>
      <span id="vertex-value-1"></span>

      <span id="arc-label-2"></span>
      <span id="arc-value-2"></span>
      <span id="vertex-label-2"></span>
      <span id="vertex-value-2"></span>

      <span id="arc-label-3"></span>
      <span id="arc-value-3"></span>
      <span id="vertex-label-3"></span>
      <span id="vertex-value-3"></span>

      <hr>
      <span class="info-total-label">Total</span>
      <span id="arc-total"></span>
      <span class="info-total-label"></span>
      <span id="vertex-total"></span>
    </div>
  </div>
  <div id="controls-container">
    <div>
      <label>vertex</label>
      <input type="radio" id="radio-a" name="vertex-select" value="A" class="vertex-btn">
      <label for="radio-a">A</label>
      <input type="radio" id="radio-b" name="vertex-select" value="B" class="vertex-btn">
      <label for="radio-b">B</label>
      <input type="radio" id="radio-c" name="vertex-select" value="C" class="vertex-btn" checked>
      <label for="radio-c">C</label>
      <input type="radio" id="radio-none" name="vertex-select" value="None" class="vertex-btn">
      <label for="radio-none">None</label>
    </div>
    <div>
      <label for="slider-a">A</label>
      <input type="range" id="slider-a" min="0" max="360" step="0.5" value="306">
    </div>
    <div>
      <label for="slider-b">B</label>
      <input type="range" id="slider-b" min="0" max="360" step="0.5" value="54">
    </div>
    <div>
      <label for="slider-c">C</label>
      <input type="range" id="slider-c" min="0" max="360" step="0.5" value="180">
    </div>
  </div>

  <script src="https://cdn.jsdelivr.net/npm/p5@1.6.0/lib/p5.min.js"></script>
  <script>
    // Globals
    let angleA = 306, angleB = 54, angleC = 180;
    let selectedVertex = 'C';
    let draggingVertex = null;
    const radius = 200;
    const center = {x: 0, y: 0};

    function setup() {
      const canvas = createCanvas(500, 500);
      canvas.parent('canvas-container');
      angleMode(DEGREES);
      noLoop();
      // Initialize sliders
      const sA = select('#slider-a'); sA.value(angleA);
      const sB = select('#slider-b'); sB.value(angleB);
      const sC = select('#slider-c'); sC.value(angleC);
      // Bind events
      sA.input(() => { angleA = parseFloat(sA.value()); redraw(); updateInfo(); });
      sB.input(() => { angleB = parseFloat(sB.value()); redraw(); updateInfo(); });
      sC.input(() => { angleC = parseFloat(sC.value()); redraw(); updateInfo(); });

      selectAll('input[name=vertex-select]').forEach(elem => elem.changed(() => {
        selectedVertex = this.value();
        redraw();
        updateInfo();
      }));

      // Initial render
      redraw();
      updateInfo();
    }

    function draw() {
      background(255);
      translate(width/2, height/2);
      // Draw colored sectors
      drawSector(angleA, angleB, '#e0f0e0');
      drawSector(angleB, angleC, '#f5f5d5');
      drawSector(angleC, angleA, '#e0e8f8');

      // Draw outer circle
      stroke(102, 102, 102);
      fill(172, 204, 222);
      strokeWeight(2);
      ellipse(0, 0, radius*2);

      // Draw center point
      fill(102);
      noStroke();
      ellipse(0, 0, 5);

      // Compute vertex positions
      const posA = polarToCartesian(angleA);
      const posB = polarToCartesian(angleB);
      const posC = polarToCartesian(angleC);

      // Draw triangle
      fill(173, 216, 230, 127);
      stroke(102, 102, 102);
      beginShape();
      vertex(posA.x, posA.y);
      vertex(posB.x, posB.y);
      vertex(posC.x, posC.y);
      endShape(CLOSE);

      // Vertex labels
      drawVertexLabel(posA, 'A');
      drawVertexLabel(posB, 'B');
      drawVertexLabel(posC, 'C');

      // Highlight arcs if a vertex is selected
      if (selectedVertex !== 'None') {
        strokeWeight(5);
        if (selectedVertex === 'A') {
          // Arc BC
          stroke(0, 128, 0);
          drawSector(angleB, angleC, null, 5);
          // Inscribed angle at A
          stroke(255, 0, 0);
          drawInscribedArc(posA, posB, posC);
        } else if (selectedVertex === 'B') {
          stroke(0, 128, 0);
          drawSector(angleC, angleA, null, 5);
          stroke(255, 0, 0);
          drawInscribedArc(posB, posC, posA);
        } else if (selectedVertex === 'C') {
          stroke(0, 128, 0);
          drawSector(angleA, angleB, null, 5);
          stroke(255, 0, 0);
          drawInscribedArc(posC, posA, posB);
        }
      }
    }

    function drawSector(startDeg, endDeg, color, thickness=3) {
      const startRad = radians(startDeg);
      const endRad = radians(endDeg);
      const arcDeg = (endDeg - startDeg + 360) % 360;
      const arcRad = radians(arcDeg);
      stroke(color || 0);
      fill(color || null);
      strokeWeight(thickness);
      arc(0, 0, radius*2, radius*2, startRad, startRad + arcRad, PIE);
    }

    function drawInscribedArc(vertexPos, other1, other2) {
      // small radius
      const r = 35;
      const a1 = atan2(other1.y - vertexPos.y, other1.x - vertexPos.x);
      const a2 = atan2(other2.y - vertexPos.y, other2.x - vertexPos.x);
      arc(vertexPos.x, vertexPos.y, r*2, r*2, a1, a2, PIE);
    }

    function polarToCartesian(deg) {
      return {
        x: radius * cos(deg),
        y: radius * sin(deg)
      };
    }

    function drawVertexLabel(pos, label) {
      fill('#00008b');
      noStroke();
      textSize(20);
      textAlign(CENTER, CENTER);
      text(label, pos.x*1.1, pos.y*1.1);
    }

    // Info panel update
    function updateInfo() {
      let arc1, arc2, arc3, vA, vB, vC;
      const arcAB = ((angleB - angleA + 360) % 360).toFixed(1);
      const arcBC = ((angleC - angleB + 360) % 360).toFixed(1);
      const arcCA = ((angleA - angleC + 360) % 360).toFixed(1);
      const vA_angle = (arcBC/2).toFixed(1);
      const vB_angle = (arcCA/2).toFixed(1);
      const vC_angle = (arcAB/2).toFixed(1);

      // First row always Arc AB + Vertex C
      select('#arc-label-1').html('Arc AB');
      select('#arc-value-1').html(arcAB + '°');
      select('#vertex-label-1').html('Vertex C');
      select('#vertex-value-1').html(vC_angle + '°');

      if (selectedVertex === 'None') {
        // Show all rows
        select('#arc-label-2').html('Arc BC');
        select('#arc-value-2').html(arcBC + '°');
        select('#vertex-label-2').html('Vertex A');
        select('#vertex-value-2').html(vA_angle + '°');

        select('#arc-label-3').html('Arc CA');
        select('#arc-value-3').html(arcCA + '°');
        select('#vertex-label-3').html('Vertex B');
        select('#vertex-value-3').html(vB_angle + '°');

        select('#arc-total').html('360°');
        select('#vertex-total').html('180°');
      } else {
        // Only one row shown; clear others
        select('#arc-label-2').html('');
        select('#arc-value-2').html('');
        select('#vertex-label-2').html('');
        select('#vertex-value-2').html('');

        select('#arc-label-3').html('');
        select('#arc-value-3').html('');
        select('#vertex-label-3').html('');
        select('#vertex-value-3').html('');

        select('#arc-total').html('?');
        select('#vertex-total').html('?');
      }
    }

    // Mouse interaction
    function mousePressed() {
      const pos = {x: mouseX - width/2, y: mouseY - height/2};
      if (dist(pos.x, pos.y, polarToCartesian(angleA).x, polarToCartesian(angleA).y) < 10) {
        draggingVertex = 'A';
      } else if (dist(pos.x, pos.y, polarToCartesian(angleB).x, polarToCartesian(angleB).y) < 10) {
        draggingVertex = 'B';
      } else if (dist(pos.x, pos.y, polarToCartesian(angleC).x, polarToCartesian(angleC).y) < 10) {
        draggingVertex = 'C';
      }
    }
    function mouseDragged() {
      if (!draggingVertex) return;
      const angle = atan2(mouseY - height/2, mouseX - width/2);
      const deg = (angle + 360) % 360;
      if (draggingVertex === 'A') { angleA = deg; select('#slider-a').value(deg); }
      else if (draggingVertex === 'B') { angleB = deg; select('#slider-b').value(deg); }
      else if (draggingVertex === 'C') { angleC = deg; select('#slider-c').value(deg); }
      redraw(); updateInfo();
    }
    function mouseReleased() {
      draggingVertex = null;
    }
  </script>
</body>
</html>
