<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Coupled PDE Visualizer</title>
<script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
<style>
  body{margin:0;font-family:sans-serif;}
  #main-container{display:flex;}
  #control-panel{width:250px;padding:20px;border-right:1px solid #ccc;}
  #control-panel div{margin-bottom:15px;}
  #control-panel label{display:inline-block;width:20px;font-weight:bold;}
  #control-panel button{width:24px;height:24px;}
  #control-panel input[type=range]{width:140px;margin:0 5px;}
  #control-panel span{display:inline-block;width:40px;text-align:right;}
  #visualization-panel{flex-grow:1;display:flex;flex-direction:column;}
  .plot{height:400px;flex:1;}
</style>
</head>
<body>
<div id="main-container">
  <div id="control-panel">
    <!-- τ slider -->
    <div>
      <label for="slider-tau">τ</label>
      <button id="btn-dec-tau">-</button>
      <input type="range" id="slider-tau" min="0.01" max="5" step="0.01" value="1">
      <button id="btn-inc-tau">+</button>
      <span id="value-tau">1.00</span>
    </div>
    <!-- φ slider -->
    <div>
      <label for="slider-phi">φ</label>
      <button id="btn-dec-phi">-</button>
      <input type="range" id="slider-phi" min="0.1" max="10" step="0.1" value="2">
      <button id="btn-inc-phi">+</button>
      <span id="value-phi">2.0</span>
    </div>
    <!-- β slider -->
    <div>
      <label for="slider-beta">β</label>
      <button id="btn-dec-beta">-</button>
      <input type="range" id="slider-beta" min="-0.5" max="0.5" step="0.01" value="0.2">
      <button id="btn-inc-beta">+</button>
      <span id="value-beta">0.20</span>
    </div>
    <!-- γ slider -->
    <div>
      <label for="slider-gamma">γ</label>
      <button id="btn-dec-gamma">-</button>
      <input type="range" id="slider-gamma" min="0" max="40" step="1" value="20">
      <button id="btn-inc-gamma">+</button>
      <span id="value-gamma">20</span>
    </div>
    <!-- ξ slider -->
    <div>
      <label for="slider-xi">ξ</label>
      <button id="btn-dec-xi">-</button>
      <input type="range" id="slider-xi" min="0.001" max="1" step="0.001" value="0.4">
      <button id="btn-inc-xi">+</button>
      <span id="value-xi">0.400</span>
    </div>
  </div>
  <div id="visualization-panel">
    <div id="plot-concentration" class="plot"></div>
    <div id="plot-temperature" class="plot"></div>
  </div>
</div>

<script>
const params = {
  tau: 1.0, phi: 2.0, beta: 0.2, gamma: 20, xi: 0.4
};

const N = 100; // spatial grid points excluding boundaries
const dx = 1 / N;
const xiGrid = Array.from({length: N+1}, (_, i) => i * dx);

function clamped(value, min, max) {
  return Math.min(Math.max(value, min), max);
}

function updateSpan(id, value) {
  const span = document.getElementById(id);
  span.textContent = value.toFixed(3);
}

function initSliders() {
  const controls = ['tau','phi','beta','gamma','xi'];
  controls.forEach(p => {
    const slider = document.getElementById(`slider-${p}`);
    const spanId = `value-${p}`;
    updateSpan(spanId, params[p]);
    slider.addEventListener('input', () => {
      params[p] = parseFloat(slider.value);
      updateSpan(spanId, params[p]);
      if(p !== 'xi') {
        simulateAndRender();
      } else {
        updateXiMarker();
      }
    });
  });

  // Increment/Decrement buttons
  controls.forEach(p => {
    const inc = document.getElementById(`btn-inc-${p}`);
    const dec = document.getElementById(`btn-dec-${p}`);
    const slider = document.getElementById(`slider-${p}`);
    inc.addEventListener('click', () => {
      let step = parseFloat(slider.step);
      let newVal = clamped(slider.valueAsNumber + step, slider.min, slider.max);
      slider.value = newVal;
      slider.dispatchEvent(new Event('input'));
    });
    dec.addEventListener('click', () => {
      let step = parseFloat(slider.step);
      let newVal = clamped(slider.valueAsNumber - step, slider.min, slider.max);
      slider.value = newVal;
      slider.dispatchEvent(new Event('input'));
    });
  });
}

// Numerical Solver
function solvePDE(tauFinal) {
  const c = new Float64Array(N+1).fill(0);
  const th = new Float64Array(N+1).fill(1);
  c[N] = 1; // surface boundary
  th[N] = 1;
  // Interior initial conditions already zero/1

  const dt = 0.01; // fixed time step
  const steps = Math.ceil(tauFinal / dt);
  let t = 0;
  for(let step=0; step<steps; step++) {
    const k1c = new Float64Array(N-1);
    const k1t = new Float64Array(N-1);
    computeDerivatives(c, th, k1c, k1t);

    const c1 = new Float64Array(N-1);
    const th1 = new Float64Array(N-1);
    for(let i=0;i<N-1;i++){
      c1[i] = c[i+1] + 0.5*dt*k1c[i];
      th1[i] = th[i+1] + 0.5*dt*k1t[i];
    }
    const k2c = new Float64Array(N-1);
    const k2t = new Float64Array(N-1);
    computeDerivatives(c1, th1, k2c, k2t);

    const c2 = new Float64Array(N-1);
    const th2 = new Float64Array(N-1);
    for(let i=0;i<N-1;i++){
      c2[i] = c[i+1] + 0.5*dt*k2c[i];
      th2[i] = th[i+1] + 0.5*dt*k2t[i];
    }
    const k3c = new Float64Array(N-1);
    const k3t = new Float64Array(N-1);
    computeDerivatives(c2, th2, k3c, k3t);

    const c3 = new Float64Array(N-1);
    const th3 = new Float64Array(N-1);
    for(let i=0;i<N-1;i++){
      c3[i] = c[i+1] + dt*k3c[i];
      th3[i] = th[i+1] + dt*k3t[i];
    }
    const k4c = new Float64Array(N-1);
    const k4t = new Float64Array(N-1);
    computeDerivatives(c3, th3, k4c, k4t);

    for(let i=0;i<N-1;i++){
      c[i+1] += (dt/6)*(k1c[i]+2*k2c[i]+2*k3c[i]+k4c[i]);
      th[i+1] += (dt/6)*(k1t[i]+2*k2t[i]+2*k3t[i]+k4t[i]);
    }

    // Neumann at center
    c[0] = c[1];
    th[0] = th[1];
    // Dirichlet at surface already fixed
    t += dt;
    if(t >= tauFinal) break;
  }
  return {c, th};
}

function computeDerivatives(c, th, dC, dT) {
  for(let i=1;i<N;i++){
    // second derivative using central differences
    const d2c = (c[i-1] - 2*c[i] + c[i+1]) / (dx*dx);
    const d2t = (th[i-1] - 2*th[i] + th[i+1]) / (dx*dx);
    const reaction = params.phi*params.phi * c[i] * Math.exp(params.gamma * (1 - 1/th[i]));
    dC[i-1] = d2c - reaction;
    dT[i-1] = d2t + params.beta * reaction;
  }
}

// Interpolation at position xi
function interpolate(values, xi {
  let idx = Math.floor(xi / dx);
  if(idx < 0) idx = 0;
  if(idx >= N) idx = N-1;
  const x1 = idx * dx;
  const x2 = (idx+1) * dx;
  const y1 = values[idx];
  const y2 = values[idx+1];
  const t = (xi - x1) / (x2 - x1);
  return y1 + t*(y2 - y1);
}

let lastSolution = null;

function simulateAndRender() {
  lastSolution = solvePDE(params.tau);
  const xiVal = params.xi;
  const cAtXi = interpolate(lastSolution.c, xiVal);
  const thAtXi = interpolate(lastSolution.th, xiVal);

  // Concentration plot data
  const concTrace = {
    x: xiGrid,
    y: lastSolution.c,
    mode: 'lines',
    line: {color: 'red', width:2},
    name: 'c(ξ)'
  };
  const concMarker = {
    x: [xiVal, xiVal, 0],
    y: [0, cAtXi, cAtXi],
    mode: 'lines',
    line: {color: '#333333', width: 2},
    name: 'Marker'
  };
  const concAnnotation = {
    xref: 'paper', yref: 'paper',
    x: 0.8, y: 0.5,
    text: `τ = ${params.tau.toFixed(3)}<br>ξ = ${xiVal.toFixed(3)}<br>c = ${cAtXi.toFixed(5)}`,
    showarrow: false,
    font: {color: '#333'}
  };

  Plotly.react('plot-concentration', [concTrace, concMarker], {
    xaxis: {title: 'ξ', range: [0,1]},
    yaxis: {title: 'c', range: [0,1.1]},
    annotations: [concAnnotation],
    margin: {l: 45, r: 30, t: 10, b: 40}
  });

  // Temperature plot data
  const thMin = Math.min(...lastSolution.th);
  const thTrace = {
    x: xiGrid,
    y: lastSolution.th,
    mode: 'lines',
    line: {color: 'blue', width:2},
    name: 'θ(ξ)'
  };
  const thMarker = {
    x: [xiVal, xiVal, 0],
    y: [thMin, thAtXi, thAtXi],
    mode: 'lines',
    line: {color: '#333333', width: 2},
    name: 'Marker'
  };
  const thAnnotation = {
    xref: 'paper', yref: 'paper',
    x: 0.8, y: 0.5,
    text: `τ = ${params.tau.toFixed(3)}<br>ξ = ${xiVal.toFixed(3)}<br>θ = ${thAtXi.toFixed(5)}`,
    showarrow: false,
    font: {color: '#333'}
  };

  Plotly.react('plot-temperature', [thTrace, thMarker], {
    xaxis: {title: 'ξ', range: [0,1]},
    yaxis: {title: 'θ', autorange: true},
    annotations: [thAnnotation],
    margin: {l: 45, r: 30, t: 10, b: 40}
  });
}

function updateXiMarker() {
  const xiVal = params.xi;
  const cAtXi = interpolate(lastSolution.c, xiVal);
  const thAtXi = interpolate(lastSolution.th, xiVal);
  const thMin = Math.min(...lastSolution.th);

  // Update marker line traces
  Plotly.restyle('plot-concentration', 'x', [[xiVal, xiVal, 0]], [1]); // marker trace index=1
  Plotly.restyle('plot-concentration', 'y', [[0, cAtXi, cAtXi]], [1]);

  Plotly.restyle('plot-temperature', 'x', [[xiVal, xiVal, 0]], [1]);
  Plotly.restyle('plot-temperature', 'y', [[thMin, thAtXi, thAtXi]], [1]);

  // Update annotations
  const concAnnotation = {
    text: `τ = ${params.tau.toFixed(3)}<br>ξ = ${xiVal.toFixed(3)}<br>c = ${cAtXi.toFixed(5)}`
  };
  const thAnnotation = {
    text: `τ = ${params.tau.toFixed(3)}<br>ξ = ${xiVal.toFixed(3)}<br>θ = ${thAtXi.toFixed(5)}`
  };
  Plotly.relayout('plot-concentration', {annotations: [concAnnotation]} );
  Plotly.relayout('plot-temperature', {annotations: [thAnnotation]} );
}

// Initialize everything
window.addEventListener('DOMContentLoaded', () => {
  initSliders();
  simulateAndRender();
});
</script>
</body>
</html>
