                <table id="data-table">
                    <thead><tr><th>Age (years)</th><th>Volume (m³ ha⁻¹)</th></tr></thead>
                    <tbody>
                        <tr><td>30</td><td>289</td></tr>
                        <tr><td>35</td><td>445</td></tr>
                        <tr><td>40</td><td>565</td></tr>
                        <tr><td>45</td><td>704</td></tr>
                        <tr><td>50</td><td>833</td></tr>
                        <tr><td>55</td><td>924</td></tr>
                    </tbody>
                </table>
            </div>
        </div>
        <div id="results-display">
            <p id="result-u"></p>
            <p id="result-p"></p>
        </div>
    </div>
</div>

<script>
(function(){
  const ages = [30,35,40,45,50,55];
  const volumes = [289,445,565,704,833,924];
  const tMin = 15, tMax = 80, tStep = 0.1;
  const tVals = [];
  for(let t=tMin; t<=tMax; t+=tStep){ tVals.push(t); }

  // Prepare initial Plotly plot
  const dataPoints = {
    x: ages,
    y: volumes,
    mode: 'markers',
    marker: {color: 'blue', size: 8},
    type: 'scatter',
    name: 'Data',
    hoverinfo: 'text',
    text: ages.map(a=>`Age: ${a}`),
  };
  const richardsTrace = {
    x: [], y: [],
    mode: 'lines',
    line: {color: 'orange', width: 2},
    name: 'Richards'
  };
  const lawTrace = {
    x: [], y: [],
    mode: 'lines',
    line: {color: 'blue', width: 2},
    name: '4/5 Law'
  };

  const layout = {
    title: 'Forest Growth Curve',
    xaxis: {title: 'Stand age (years)', range: [tMin, tMax], gridcolor: 'lightgrey', zeroline: false},
    yaxis: {title: 'Wood volume, m³ ha⁻¹', range: [0, 1600], gridcolor: 'lightgrey', zeroline: false},
    plot_bgcolor: '#FFFFF0',
    showlegend: false,
    shapes: []
  };

  Plotly.newPlot('plot-container', [dataPoints, richardsTrace, lawTrace], layout);

  // Slider handling
  const sliders = [
    {id: 'slider-bmax', valueId: 'value-bmax', decimals: 0},
    {id: 'slider-b0', valueId: 'value-b0', decimals: 1},
    {id: 'slider-r', valueId: 'value-r', decimals: 3},
    {id: 'slider-beta', valueId: 'value-beta', decimals: 3}
  ];

  sliders.forEach(sl => {
    document.getElementById(sl.id).addEventListener('input', update);
  });

  function update(){
    // Read slider values
    const bmax = parseFloat(document.getElementById('slider-bmax').value);
    const b0   = parseFloat(document.getElementById('slider-b0').value);
    const r    = parseFloat(document.getElementById('slider-r').value);
    const beta = parseFloat(document.getElementById('slider-beta').value);

    // Update display values
    document.getElementById('value-bmax').innerText = bmax.toFixed(0);
    document.getElementById('value-b0').innerText   = b0.toFixed(1);
    document.getElementById('value-r').innerText    = r.toFixed(3);
    document.getElementById('value-beta').innerText = beta.toFixed(3);

    // Richards function
    const M = Math.pow(bmax / b0, beta) - 1;
    const richness = tVals.map(t => {
      const denom = Math.pow(1 + M * Math.exp(-r * t), 1 / beta);
      return bmax / denom;
    });

    // 4/5 law parameters
    const t_i = Math.log( Math.pow(bmax / b0, beta) - 1) * beta / r; // Actually ln((M)*beta)/r
    // Correction according to formula: ln((M*beta))/r
    const t_i_corr = Math.log(M * beta) / r;
    const V_i = bmax * Math.pow( (beta + 1) / (2 * beta), 1 / beta );
    const Vprime_i = (r / beta) * V_i * (1 - Math.pow(V_i / bmax, beta));
    const u = t_i_corr - 0.8 * V_i / Vprime_i;
    const p = V_i / Math.pow(t_i_corr - u, 0.8);

    // 4/5 law curve
    const lawVals = tVals.map(t => (t > u) ? p * Math.pow(t - u, 0.8) : null);

    // Update results display
    document.getElementById('result-u').innerText = `rejuvenation bias u = ${u.toFixed(4)}`;
    document.getElementById('result-p').innerText = `site productivity p = ${p.toFixed(4)}`;

    // Update Plotly traces
    richardsTrace.x = tVals;
    richardsTrace.y = richness;
    lawTrace.x = tVals;
    lawTrace.y = lawVals;

    Plotly.react('plot-container', [dataPoints, richardsTrace, lawTrace], layout);
  }

  // Initial call
  update();
})();
</script>
</body>
</html>
