<script>
/* Global Three.js objects */
let scene, camera, renderer, pineGroup;

// Utility: create a regular polygon shape
function createPolygonShape(n, radius) {
  const shape = new THREE.Shape();
  for (let i = 0; i < n; i++) {
    const theta = (i / n) * Math.PI * 2;
    const x = radius * Math.cos(theta);
    const y = radius * Math.sin(theta);
    if (i === 0) shape.moveTo(x, y);
    else shape.lineTo(x, y);
  }
  shape.closePath();
  return shape;
}

// Redraw function
function redraw() {
  // Read values
  const polyRadio = document.querySelector('input[name="base-polygon"]:checked');
  const n = parseInt(polyRadio.value, 10);

  const units = parseInt(document.getElementById('slider-units').value, 10);
 Deg = parseFloat(document.getElementById('slider-angle').value);
  const conical = parseFloat(document.getElementById('slider-conical').value);
  const rotXdeg = parseFloat(document.getElementById('slider-rotate-x').value);
  const rotZdeg = parseFloat(document.getElementById('slider-rotate-z').value);

  const angleRad = angleDeg * Math.PI / 180;

  // Remove old segments
  while (pineGroup.children.length > 0) {
    const child = pineGroup.remove(pineGroup.children[0]);
    child.geometry.dispose();
    child.material.dispose();
  }

  // Base geometry
  const baseShape = createPolygonShape(n, 10);
  const baseGeometry = new THREE.ShapeGeometry(baseShape);

  // Create segments
  for (let i = 0; i < units; i++) {
    const theta = i * angleRad;
    const radius = 4 * Math.sqrt(i);
    const z = conical * radius;
    const x = radius * Math.cos(theta);
    const y = radius * Math.sin(theta);
    const scale = 0.5 * Math.log(i + 2);

    const material = new THREE.MeshStandardMaterial();
    const hue = (theta / (Math.PI * 2)) % 1;
    material.color.setHSL(hue, 0.8, 0.6);

    const mesh = new THREE.Mesh(baseGeometry.clone(), material);
    mesh.position.set(x, y, z);
    mesh.scale.set(scale, scale, scale);
    mesh.rotation.z = theta;

    pineGroup.add(mesh);
  }

  // Apply master rotations
  pineGroup.rotation.x = rotXdeg * Math.PI / 180;
  pineGroup.rotation.z = rotZdeg * Math.PI / 180;

  renderer.render(scene, camera);
}

// Initialization
function init() {
  const canvas = document.getElementById('main-canvas');
  renderer = new THREE.WebGLRenderer({ canvas, antialias: true });
  renderer.setSize(canvas.clientWidth, canvas.clientHeight);
  renderer.setClearColor(0xffffff);

  scene = new THREE.Scene();

  camera = new THREE.PerspectiveCamera(45, canvas.clientWidth / canvas.clientHeight, 0.1, 1000);
  camera.position.set(0, 0, 200);

  const ambient = new THREE.AmbientLight(0xffffff, 0.6);
  scene.add(ambient);

  const dirLight = new THREE.DirectionalLight(0xffffff, 0.6);
  dirLight.position.set(1, 1, 0.5).normalize();
  scene.add(dirLight);

