<script type="module">
import * as THREE from 'https://cdn.jsdelivr.net/npm/three@0.150.1/build/three.module.js';
import {OrbitControls} from 'https://cdn.jsdelivr.net/npm/three@0.150.1/examples/jsm/controls/OrbitControls.js';

const container = document.getElementById('canvas-container');
const btnSC = document.getElementById('btn-sc');
const btnBCC = document.getElementById('btn-bcc');
const btnFCC = document.getElementById('btn-fcc');
const inputH = document.getElementById('input-h');
const inputK = document.getElementById('input-k');
const inputL = document.getElementById('input-l');
const sliderOpacity = document.getElementById('slider-opacity');

let currentLattice = 'sc';
let planeOpacity = 0.8;

// Scene, camera, renderer
const scene = new THREE.Scene();
scene.background = new THREE.Color(0xf0f0f0);
const camera = new THREE.PerspectiveCamera(45, container.client / container.clientHeight, 0.1, 100);
camera.position.set(1.5, 1.5, 1.5);
camera.lookAt(0,0,0);
const renderer = new THREE.WebGLRenderer({antialias:true});
renderer.setSize(container.clientWidth, container.clientHeight);
container.appendChild(renderer.domElement);

// Controls
const orbitControls = new OrbitControls(camera, renderer.domElement);
orbitControls.enableDamping = true;

// Lighting
const ambient = new THREE.AmbientLight(0xcccccc, 0.4);
scene.add(ambient);
const dirLight1 = new THREE.DirectionalLight(0xffffff, 0.8);
dirLight1.position.set(5,5,5);
scene.add(dirLight1);
const dirLight2 = new THREE.DirectionalLight(0xffffff, 0.8);
dirLight2.position.set(-5,-5,-5);
scene.add(dirLight2);

// Groups
let atomsGroup = new THREE.Group();
let planesGroup = new THREE.Group();
scene.add(atomsGroup);
scene.add(planesGroup);

// Unit cell
const cellEdges = new THREE.EdgesGeometry(new THREE.BoxGeometry(1,1,1));
const cellMat = new THREE.LineBasicMaterial({color:0x000000});
const cellWire = new THREE.LineSegments(cellEdges, cellMat);
scene.add(cellWire);

// Axes
const axesHelper = new THREE.AxesHelper(0.6);
scene.add(axesHelper);

// Helpers
const vertices = [
  new THREE.Vector3(-0.5,-0.5,-0.5),
  new THREE.Vector3( 0.5,-0.5,-0.5),
  new THREE.Vector3(-0.5, 0.5,-0.5),
  new THREE.Vector3( 0.5, 0.5,-0.5),
  new THREE.Vector3(-0.5,-0.5, 0.5),
  new THREE.Vector3( 0.5,-0.5, 0.5),
  new THREE.Vector3(-0.5, 0.5, 0.5),
  new THREE.Vector3( 0.5, 0.5, 0.5)
];
const edgesIdx = [
  [0,1],[0,2],[0,4],
  [1,3],[1,5],
  [2,3],[2,6],
  [3,7],
  [4,5],[4,6],
  [5,7],
  [6,7]
];

// Event Listeners
btnSC.addEventListener('click',()=>{currentLattice='sc'; setActiveButton(btnSC); updateScene();});
btnBCC.addEventListener('click',()=>{currentLattice='bcc'; setActiveButton(btnBCC); updateScene();});
btnFCC.addEventListener('click',()=>{currentLattice='fcc'; setActiveButton(btnFCC); updateScene();});
[inputH, inputK, inputL].forEach(input=>input.addEventListener('change',()=>{updateScene();}));
sliderOpacity.addEventListener('input',()=>{planeOpacity=parseFloat(sliderOpacity.value); updateOpacity();});

setActiveButton(btnSC);
updateScene();
animate();

function setActiveButton(activeBtn){
  [btnSC, btnBCC, btnFCC].forEach(btn=>btn.classList.remove('active'));
  activeBtn.classList.add('active');
}

function updateScene(){
  // Clear groups
  atomsGroup.clear();
  planesGroup.clear();

  const h = parseInt(inputH.value)||0;
  const k = parseInt(inputK.value)||0;
  const l = parseInt(inputL.value)||0;

  // ---- Draw Atoms ----
  const sphereGeom = new THREE.SphereGeometry(0.08, 32, 32);
  const sphereMat = new THREE.MeshPhongMaterial({
    color:0x2233ff,
    specular:0x88cc88,
    shininess:25
  });

  const positions = [];
  switch(currentLattice){
    case 'sc':
      for(let x of [-0.5,0.5])
        for(let y of [-0.5,0.5])
          for(let z of [-0.5,0.5])
            positions.push(new THREE.Vector3(x,y,z));
      break;
    case 'bcc':
      for(let x of [-0.5,0.5])
        for(let y of [-0.5,0.5])
          for(let z of [-0.5,0.5])
            positions.push(new THREE.Vector3(x,y,z));
      positions.push(new THREE.Vector3(0,0,0));
      break;
    case 'fcc':
      for(let x of [-0.5,0.5])
        for(let y of [-0.5,0.5])
          for(let z of [-0.5,0.5])
            positions.push(new THREE.Vector3(x,y,z));
      positions.push(new THREE.Vector3(0,0.5,0.5));
      positions.push(new THREE.Vector3(0,-0.5,0.5));
      positions.push(new THREE.Vector3(0,0.5,-0.5));
      positions.push(new THREE.Vector3(0,-0.5,-0.5));
      positions.push(new THREE.Vector3(0.5,0,0.5));
      positions.push(new THREE.Vector3(-0.5,0,0.5));
      positions.push(new THREE.Vector3(0.5,0,-0.5));
      positions.push(new THREE.Vector3(-0.5,0,-0.5));
      break;
  }
  positions.forEach(pos=>{
    const atom = new THREE.Mesh(sphereGeom, sphereMat);
    atom.position.copy(pos);
    atomsGroup.add(atom);
  });

  // ---- Draw Planes ----
  if(h===0 && k===0 && l===0) return; // No planes

  // Simplify indices
  let [sh, sk, sl] = [h,k,l];
  const G = gcd(gcd(Math.abs(sh), Math.abs(sk)), Math.abs(sl));
  sh/=G; sk/=G; sl/=G;

  const coeff = {x:sh, y:sk, z:sl};

  // Find min/max over cube vertices
  let min = Infinity, max = -Infinity;
  vertices.forEach(v=>{
    const val = coeff.x*v.x + coeff.y*v.y + coeff.z*v.z;
    if(val<min) min=val;
    if(val>max) max=val;
  });

  const nMin = Math.floor(min);
  const nMax = Math.ceil(max);

  for(let n=nMin;n<=nMax;n++){
    const points=[]; // intersection points
    edgesIdx.forEach(([i,j])=>{
      const p1=vertices[i], p2=vertices[j];
      const v1 = coeff.x*p1.x + coeff.y*p1.y + coeff.z*p1.z - n;
      const v2 = coeff.x*p2.x + coeff.y*p2.y + coeff.z*p2.z - n;
      if(v1===0) points.push(p1.clone());
      if(v2===0) points.push(p2.clone());
      if(v1*v2<0){
        const t=v1/(v1-v2);
        const inter = new THREE.Vector3().lerpVectors(p1,p2,t);
        points.push(inter);
      }
    });
    // deduplicate
    const uniq=[];
    const eps=1e-6;
    points.forEach(pt=>{
      if(!uniq.some(up=>up.distanceTo(pt)<eps)) uniq.push(pt);
    });
    if(uniq.length<3) continue;

    // Build polygon in plane
    const centroid = new THREE.Vector3();
    uniq.forEach(p=>centroid.add(p));
    centroid.divideScalar(uniq.length);

    const normal = new THREE.Vector3(coeff.x,coeff.y,coeff.z).normalize();
    // find two orthogonal vectors
    let u = new THREE.Vector3(1,0,0);
    if(Math.abs(normal.dot(u))>0.99) u.set(0,1,0);
    u.sub(normal.clone().multiplyScalar(normal.dot(u))).normalize();
    const v = new THREE.Vector3().crossVectors(normal, u);

    // angles
    const pts2D = uniq.map(p=>{
      const rel = new THREE.Vector3().subVectors(p, centroid);
      return {
        x: rel.dot(u),
        y: rel.dot(v),
        world: p
      };
    });
    pts2D.sort((a,b)=>Math.atan2(a.y,a.x)-Math.atan2(b.y,b.x));

    const shape = new THREE.Shape();
    shape.moveTo(pts2D[0].x, pts2D[0].y);
    for(let i=1;i<pts2D.length;i++){
      shape.lineTo(pts2D[i].x, pts2D[i].y);
    }
    // close
    // create geometry
    const geom = new THREE.ShapeGeometry(shape);
    // align geometry to plane
    const planeMatrix = new THREE.Matrix4();
    const planeNormal = normal;
    planeMatrix.lookAt(new THREE.Vector3(0,0,0), planeNormal, new THREE.Vector3(0,1,0));
    // Invert orientation
    geom.applyMatrix4(planeMatrix);
    const mat = new THREE.MeshBasicMaterial({
      color:0x0000ff,
      transparent:true,
      opacity:planeOpacity,
      side:THREE.DoubleSide,
      depthTest:true,
      depthWrite:true
    });
    const planeMesh = new THREE.Mesh(geom, mat);
    // translate to centroid
    planeMesh.position.copy(centroid);
    planesGroup.add(planeMesh);
  }
}

function updateOpacity(){
  planesGroup.children.forEach(mesh=>{
    mesh.material.opacity = planeOpacity;
    mesh.material.needsUpdate = true;
  });
  renderer.render(scene,camera);
}

function animate(){
  requestAnimationFrame(animate);
  orbitControls.update();
  renderer.render(scene,camera);
}

// window resize
window.addEventListener('resize',()=>{
  const width=container.clientWidth;
  const height=container.clientHeight;
  renderer.setSize(width,height);
  camera.aspect=width/height;
  camera.updateProjectionMatrix();
});

function gcd(a,b){
  if(b===0) return a;
  return gcd(b, a%b);
}
</script>
</body>
