<script src="https://cdn.jsdelivr.net/npm/three@0.158.0/examples/js/controls/OrbitControls.js"></script>
<script>
(() => {
  /* ---------- Data Store ---------- */
  const THOMSON_POINTS = {
    /* Example precomputed data (limited to 4 points for brevity) */
    "2": [[-1,0,0],[1,0,0]],
    "4": [[0,1,0],[0,-1,0],[0,0,1],[0,0,-1]]
    /* More precomputed arrays can be added here */
  };

  /* ---------- State Variables ---------- */
  let currentN = 20;
  let is3DView = true;
  let pointsCoordinates = [];
  // Three.js related
  let scene, camera, renderer, controls;
  const pointGroup = new THREE.Group();
  const pointPairs = []; // [{solid, ring}]
  // 2D canvas
  const canvas2d = document.getElementById('canvas-2d');
  const ctx2d = canvas2d.getContext('2d');
  const div2dText = document.getElementById('div-2d-text');

  /* ---------- DOM Elements ---------- */
  const sliderPoints = document.getElementById('slider-points');
  const labelPoints = document.getElementById('label-points');
  const btn2d = document.getElementById('btn-2d');
  const btn3d = document.getElementById('btn-3d');
  const canvas3d = document.getElementById('canvas-3d');
  const div2dPlot = document.getElementById('div-2d-plot');

  /* ---------- Utility Functions ---------- */
  function generateRandomPoints(n) {
    const arr = [];
    for (let i = 0; i < n; i++) {
      const u = Math.random();
      const v = Math.random();
      const theta = 2 * Math.PI * u;
      const phi = Math.acos(2 * v - 1);
      const x = Math.sin(phi) * Math.cos(theta);
      const y = Math.sin(phi) * Math.sin(theta);
      const z = Math.cos(phi);
      arr.push([x, y, z]);
    }
    return arr;
  }

  function getPoints(n) {
    return THOMSON_POINTS[n] ? THOMSON_POINTS[n] : generateRandomPoints(n);
  }

  /* ---------- Three.js Setup ---------- */
  function initThree() {
    renderer = new THREE.WebGLRenderer({canvas: canvas3d, antialias:true});
    renderer.setClearColor(0xf0f0f0);
    renderer.setSize(window.innerWidth, window.innerHeight*0.5);
    scene = new THREE.Scene();
    camera = new THREE.PerspectiveCamera(45, renderer.domElement.width / renderer.domElement.height, 0.1, 1000);
    camera.position.set(3, 3, 3);
    camera.lookAt(0,0,0);

    controls = new THREE.OrbitControls(camera, renderer.domElement);
    controls.update();

    // Lights
    const ambLight = new THREE.AmbientLight(0xcccccc);
    scene.add(ambLight);
    const dirLight = new THREE.DirectionalLight(0xffffff, 0.7);
    dirLight.position.set(5, 5, 5);
    scene.add(dirLight);

    // Main sphere
    const sphereGeom = new THREE.SphereGeometry(1, 32, 32);
    const sphereMat = new THREE.MeshLambertMaterial({color: 0xFFFFE0, transparent:true, opacity:0.6});
    const sphereMesh = new THREE.Mesh(sphereGeom, sphereMat);
    scene.add(sphereMesh);

    // Point group
    scene.add(pointGroup);

    // Animation loop
    renderer.setAnimationLoop(animate);
    window.addEventListener('resize', onWindowResize);
  }

  function onWindowResize() {
    camera.aspect = renderer.domElement.width / renderer.domElement.height;
    camera.updateProjectionMatrix();
    renderer.setSize(window.innerWidth, window.innerHeight*0.5);
  }

  function clearPointPairs() {
    pointPairs.forEach(pair => {
      pointGroup.remove(pair.solid);
      pointGroup.remove(pair.ring);
    });
    pointPairs.length = 0;
  }

  function createPointMeshes(coord) {
    const {x, y, z} = coord;
    const pos = new THREE.Vector3(x, y, z);

    const solidGeom = new THREE.SphereGeometry(0.025, 12, 12);
    const solidMat = new THREE.MeshBasicMaterial({color: 0x0000ff});
    const solidMesh = new THREE.Mesh(solidGeom, solidMat);
    solidMesh.position.copy(pos);

    const ringGeom = new THREE.RingGeometry(0.02, 0.025, 32);
    const ringMat = new THREE.MeshBasicMaterial({color:0x0000ff, side:THREE.DoubleSide});
    const ringMesh = new THREE.Mesh(ringGeom, ringMat);
    ringMesh.position.copy(pos);
    ringMesh.lookAt(camera.position);

    // Initially set according to front/back
    const camDir = new THREE.Vector3();
    camera.getWorldDirection(camDir);
    const rel = pos.clone().sub(camera.position);
    if (rel.dot(camDir) > 0) {
      solidMesh.visible = true;
      ringMesh.visible = false;
    } else {
      solidMesh.visible = false;
      ringMesh.visible = true;
    }

    pointGroup.add(solidMesh);
    pointGroup.add(ringMesh);
    pointPairs.push({solid:solidMesh, ring:ringMesh, pos});
  }

  function updatePoints3D(coords) {
    clearPointPairs();
    coords.forEach(c => createPointMeshes({x:c[0],y:c[1],z:c[2]}));
  }

  function animate() {
    // Update ring orientation and front/back visibility
    pointPairs.forEach(pair => {
      pair.ring.lookAt(camera.position);
      const camDir = new THREE.Vector3();
      camera.getWorldDirection(camDir);
      const rel = pair.pos.clone().sub(camera.position);
      if (rel.dot(camDir) > 0) {
        pair.solid.visible = true;
        pair.ring.visible = false;
      } else {
        pair.solid.visible = false;
        pair.ring.visible = true;
      }
    });
    controls.update();
    renderer.render(scene, camera);
  }

  /* ---------- 2D Rendering ---------- */
  function draw2D(coords) {
    const w = canvas2d.width, h = canvas2d.height;
    ctx2d.clearRect(0,0,w,h);
    ctx2d.fillStyle = '#0000ff';
    let minDist = Infinity;
    const n = coords.length;
    for (let i=0;i<n;i++) {
      const [x1,y1,z1] = coords[i];
      // spherical coords
      const theta = Math.atan2(y1, x1);
      const phi = Math.acos(z1);
      const x = ((theta + Math.PI)/ (2*Math.PI)) * w;
      const y = (phi / Math.PI) * h;
      ctx2d.beginPath();
      ctx2d.arc(x,y,3,0,2*Math.PI);
      ctx2d.fill();

      // distance calculations
      for (let j=i+1;j<n;j++){
        const [x2,y2,z2] = coords[j];
        const d = Math.sqrt((x1-x2)**2+(y1-y2)**2+(z1-z2)**2);
        if (d < minDist) minDist = d;
      }
    }
    const maxDist = 2; // for unit sphere
    div2dText.innerHTML = `minimum distance between points = ${minDist.toFixed(5)}<br>maximum distance between points = ${maxDist}`;
  }

  /* ---------- View Toggle Handlers ---------- */
  function show3D() {
    is3DView = true;
    btn3d.classList.add('active'); btn2d.classList.remove('active');
    canvas3d.style.display = 'block';
    div2dPlot.style.display = 'none';
    if (!renderer) initThree(); // lazy init
    renderer.setAnimationLoop(animate);
  }
  function show2D() {
    is3DView = false;
    btn2d.classList.add('active'); btn3d.classList.remove('active');
    canvas3d.style.display = 'none';
    div2dPlot.style.display = 'block';
    renderer.setAnimationLoop(null); // stop 3D rendering
    draw2D(pointsCoordinates);
  }

  /* ---------- Slider Handler ---------- */
  sliderPoints.addEventListener('input', () => {
    const n = parseInt(sliderPoints.value, 10);
    currentN = n;
    labelPoints.textContent = `+ ${n}`;
    pointsCoordinates = getPoints(n);
    if (is3DView) updatePoints3D(pointsCoordinates);
    else draw2D(pointsCoordinates);
  });

  /* ---------- Button Handlers ---------- */
  btn3d.addEventListener('click', () => show3D());
  btn2d.addEventListener('click', () => show2D());

  /* ---------- Initialize ---------- */
  pointsCoordinates = getPoints(currentN);
  show3D(); // default view
})();
</script>
</body>
</html>
