<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Orbit and Equation of Time Demo</title>
<style>
  body {font-family: Arial, sans-serif; margin: 0; padding: 20px;}
  #main-container {max-width: 800px; margin: 0 auto;}
  #control-panel {display: flex; flex-direction: column; gap: 15px; margin-bottom: 20px;}
  .control-group {display: flex; align-items: center; gap: 10px;}
  .control-group label {min-width: 160px;}
  .control-group input[type="range"] {flex: 1;}
  .control-group span {width: 80px; text-align: right;}
  #visualization {display: flex; flex-direction: column; gap: 30px;}
  canvas {border: 1px solid #ccc; background-color: #fff;}
  #orbit-canvas {background-color: #f0f0f0;}
  #plot-canvas {background-color: #fff;}
</style>
</head>
<body>
<div id="main-container">
  <!-- Control Panel -->
  <div id="control-panel">
    <div class="control-group">
      <label for="slider-angle">angle relative to ecliptic</label>
      <input type="range" id="slider-angle" min="0" max="60" step="0.01" value="23.45">
      <span id="value-angle"></span>
    </div>
    <div class="control-group">
      <label for="slider-longitude">longitude of periapsis</label>
      <input type="range" id="slider-longitude" min="0" max="360" step="0.01" value="102.95">
      <span id="value-longitude"></span>
    </div>
    <div class="control-group">
      <label for="slider-eccentricity">orbit eccentricity</label>
      <input type="range" id="slider-eccentricity" min="0" max="0.99" step="0.000001" value="0.016722">
      <span id="value-eccentricity"></span>
    </div>
  </div>

  <!-- Visualization -->
  <div id="visualization">
    <canvas id="orbit-canvas" width="600" height="400"></canvas>
    <canvas id="plot-canvas" width="600" height="400"></canvas>
  </div>
</div>

<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
<script>
  // ---------- Helper Functions ----------
  function toRadians(deg) {
    return deg * Math.PI / 180;
  }

  function formatValue(value, decimals) {
    return value.toFixed(decimals);
  }

  // ---------- Slider and Display ----------
  const sliderAngle   = document.getElementById('slider-angle');
  const sliderLon     = document.getElementById('slider-longitude');
  const sliderEcc     = document.getElementById('slider-eccentricity');
  const spanAngle     = document.getElementById('value-angle');
  const spanLon       = document.getElementById('value-longitude');
  const spanEcc       = document.getElementById('value-eccentricity');

  function updateDisplays() {
    spanAngle.textContent = formatValue(parseFloat(sliderAngle.value), 2);
    spanLon.textContent   = formatValue(parseFloat(sliderLon.value),   2);
    spanEcc.textContent   = formatValue(parseFloat(sliderEcc.value),   6);
  }

  // Initialize displays
  updateDisplays();

  // ---------- Orbit Sketch ----------
  const orbitSketch = new p5((p) => {
    let a, b, angleRad, e;
    p.setup = () => {
      p.createCanvas(600, 400).id('orbit-canvas');
      p.angleMode(p.RADIANS);
      p.background('#f0f0f0');
      p.angleMode(p.DEGREES);
    };
    p.draw = () => {
      // Fetch current values
      e       = parseFloat(sliderEcc.value);
      angleRad = toRadians(parseFloat(sliderLon.value));

      // Calculate ellipse axes
      a = p.width * 0.4;               // semi-major axis
      b = a * Math.sqrt(1 - e * e);    // semi-minor axis

      // Draw orbit
      p.background('#f0f0f0'); // reset background
      p.push();
      p.translate(p.width / 2, p.height / 2);
      p.rotate(angleRad);
      p.stroke('#a0522d');
      p.noFill();
      p.strokeWeight(2);
      p.ellipse(0, 0, 2 * a, 2 * b);
      p.pop();
    };
    // Allow external redraw
    p.redrawOrbit = () => p.redraw();
  }, 'orbit-canvas');

  // ---------- Equation of Time Plot Sketch ----------
  const plotSketch = new p5((p) => {
    p.setup = () => {
      p.createCanvas(600, 400).id('plot-canvas');
      p.angleMode(p.RADIANS);
      p.noLoop();
    };

    function computeEOT() {
      // Read slider values
      const epsilonDeg = parseFloat(sliderAngle.value);   // obliquity
      const e          = parseFloat(sliderEcc.value);
      const omegaDeg   = parseFloat(sliderLon.value);

      const epsilon = toRadians(epsilonDeg);
      const omega   = toRadians(omegaDeg);

      const points = []; // array of {M, eot}

      const numPts = 400;
      const twoPi = 2 * Math.PI;
      let prevEOTrad = 0;

      for (let i = 0; i <= numPts; i++) {
        const M = twoPi * i / numPts; // Mean Anomaly

        // Solve Kepler's Equation via Newton-Raphson
        let E = M;
        for (let k = 0; k < 8; k++) {
          const f = E - e * Math.sin(E) - M;
          const fp = 1 - e * Math.cos(E);
          E = E - f / fp;
        }

        // True Anomaly ν
        const sinE = Math.sin(E/2);
        const cosE = Math.cos(E/2);
        const sqrt1pe = Math.sqrt(1 + e);
        const sqrt1me = Math.sqrt(1 - e);
        const nu = 2 * Math.atan2(sqrt1pe * sinE, sqrt1me * cosE);

        // True Longitude λ
        const lambda = nu + omega;

        // Right Ascension α
        const sinLambda = Math.sin(lambda);
        const cosLambda = Math.cos(lambda);
        const alpha = Math.atan2(Math.cos(epsilon) * sinLambda, cosLambda);

        // Equation of Time in radians
        let eotRad = M - alpha;

        // Phase unwrapping
        let diff = eotRad - prevEOTrad;
        if (Math.abs(diff) > Math.PI) {
          if (diff > 0) {
            eotRad -= 2 * Math.PI;
          } else {
            eotRad += 2 * Math.PI;
          }
        }
        prevEOTrad = eotRad;

        const eotMin = eotRad * (720 / Math.PI);
        points.push({ M, eot: eotMin });
      }
      return points;
    }

    function drawAxes(minY, maxY) {
      const marginX = 60;
      const marginY = 40;
      const plotW = p.width - 2 * marginX;
      const plotH = p.height - 2 * marginY;

      p.stroke(0);
      p.strokeWeight(1);
      // X axis
      p.line(marginX, p.height - marginY, p.width - marginX, p.height - marginY);
      // Y axis
      p.line(marginX, marginY, marginX, p.height - marginY);

      // X ticks and labels (integer 1-6)
      p.textSize(12);
      p.fill(0);
      p.noStroke();
      for (let x = 1; x <= 6; x++) {
        const xPos = marginX + (x / (2 * Math.PI)) * plotW;
        p.line(xPos, p.height - marginY, xPos, p.height - marginY + 6);
        p.text(x, xPos - 4, p.height - marginY + 20);
      }

      // Y ticks (min, max, 0)
      const tickVals = [minY, 0, maxY];
      for (let val of tickVals) {
        if (val < minY - 1 || val > maxY + 1) continue;
        const yPos = marginY + (maxY - val) / (maxY - minY) * plotH;
        p.line(marginX - 6, yPos, marginX, yPos);
        p.text(val.toFixed(1), marginX - 50, yPos + 4);
      }

      // Gridlines
      p.stroke('#e0e0e0');
      for (let t of tickVals) {
        if (t < minY - 1 || t > maxY + 1) continue;
        const yPos = marginY + (maxY - t) / (maxY - minY) * plotH;
        p.line(marginX, yPos, p.width - marginX, yPos);
      }
    }

    p.draw = () => {
      p.background(255);

      const points = computeEOT();

      const yValues = points.map(p => p.eot);
      const minY = Math.min(...yValues);
      const maxY = Math.max(...yValues);
      if (minY === maxY) {
        // Avoid zero range
        minY -= 1;
        maxY += 1;
      }

      const marginX = 60;
      const marginY = 40;
      const plotW = p.width - 2 * marginX;
      const plotH = p.height - 2 * marginY;

      // Draw axes and grid
      drawAxes(minY, maxY);

      // Plot curve
      p.stroke('#ff8c00');
      p.strokeWeight(2);
      p.noFill();
      p.beginShape();
      for (let pt of points) {
        const xPos = marginX + (pt.M / (2 * Math.PI)) * plotW;
        const yPos = marginY + (maxY - pt.eot) / (maxY - minY) * plotH;
        p.vertex(xPos, yPos);
      }
      p.endShape();
    };
    // Expose redraw method
    p.redrawPlot = () => p.redraw();
  }, 'plot-canvas');

  // ---------- Slider Change Handlers ----------
  function handleInputChange() {
    updateDisplays();
    orbitSketch.redrawOrbit();
    plotSketch.redrawPlot();
  }

  sliderAngle.addEventListener('input', handleInputChange);
  sliderLon.addEventListener('input', handleInputChange);
  sliderEcc.addEventListener('input', handleInputChange);

  // Initial render
  handleInputChange();
</script>
</body>
</html>
