    // Update value displays
    sliderC.addEventListener('input', () => { valC.textContent = sliderC.value; redrawScene(); });
    sliderHC.addEventListener('input', () => { valHC.textContent = sliderHC.value; redrawScene(); });
    sliderDelta.addEventListener('input', () => { valDelta.textContent = sliderDelta.value; redrawScene(); });
    slidePlot.addEventListener('input', () => { valPlot.textContent = slidePlot.value; redrawScene(); });

    for (const r of radios) r.addEventListener('change', redrawScene);
    checkVerif.addEventListener('change', redrawScene);

    // p5.js sketch
    const sketch = () => {
        let canvas;
        function setup() {
            canvas = createCanvas(800, 600);
            canvas.parent('visualization');
            noLoop();
            redrawScene();
        }
        function draw() {
            // nothing continuous; all rendering done in redrawScene()
        }
    };
    new p5(sketch);

    // Geometry computations
    function computeM(c, hC, delta) {
        const tanDelta = Math.tan(delta);
        const f = (y) => {
            const x = (2*hC*y - hC*hC + c*c/4)/c;
            return tanDelta * (y*y + x*x - 1.5*c*x + c*c/2) - (c/2 * y);
        };
        let y = -Math.max(c, hC)/2;
        for (let i=0; i<20; i++) {
            const fy = f(y);
            const h = 1e-6;
            const fprime = (f(y+h)-f(y-h))/(2*h);
            if (Math.abs(fprime) < 1e-12) break;
            const ynew = y - fy/fprime;
            if (Math.abs(ynew - y) < 1e-10) break;
            y = ynew;
        }
        const x = (2*hC*y - hC*hC + c*c/4)/c;
        return {x, y};
    }

    function distance(ax, ay, bx, by) {
        return Math.hypot(ax-bx, ay-by);
    }

    // Screen mapping
    function toScreenX(x) { return width/2 + x*scaleFactor; }
    function toScreenY(y) { return height*0.6 - y*scaleFactor; }

    // Drawing primitives
    function drawPoint(x, y, label) {
        const sx = toScreenX(x);
        const sy = toScreenY(y);
        fill(0);
        noStroke();
        ellipse(sx, sy, 6, 6);
        fill(0);
        textSize(12);
        textAlign(LEFT, TOP);
        text(label, sx + 5, sy - 5);
    }

    function drawLine(ax, ay, bx, by, opts={}) {
        stroke(0);
        strokeWeight(opts.weight||1);
        if (opts.dashed) drawingContext.setLineDash([5, 5]); else drawingContext.setLineDash([]);
        line(toScreenX(ax), toScreenY(ay), toScreenX(bx), toScreenY(by));
        drawingContext.setLineDash([]);
    }

    function drawArc(cx, cy, R, opts={}) {
        stroke(0);
        strokeWeight(opts.weight||1);
        drawingContext.setLineDash([]);
        let sX = toScreenX(cx);
        let sY = toScreenY(cy);
        let rPix = R*scaleFactor;
        ellipse(sX, sY, rPix*2, rPix*2);
    }

    // Main redraw function
    function redrawScene() {
        const c = parseFloat(sliderC.value);
        const hC = parseFloat(sliderHC.value);
        const delta = parseFloat(sliderDelta.value);
        const plotRange = parseFloat(slidePlot.value);
        const step = getSelectedStep();
        const verify = checkVerif.checked;

        // Wait until the canvas is ready
        const canvasElem = document.getElementById('main-canvas');
        if (!canvasElem) return;
        const ctx = canvasElem.getContext('2d');
        if (!ctx) return;

        // Clear canvas
        p5._isGlobalMode = true; // use global p5 functions
        background('#E0E0E0');

        scaleFactor = baseScale * plotRange;

        // Geometry
        const F = {x:0, y:0};
        const C = {x:0, y:hC};
        const D = {x:c/2, y:0};
        const K = {x:c, y:0};
        const Mobj = computeM(c, hC, delta);
        const M = {x:Mobj.x, y:Mobj.y};
        const R = distance(M.x, M.y, C.x, C.y);
        const xB = 2*M.x - c/2;
        const B = {x:xB, y:0};
        const A = {x:xB - c, y:0};
        const E = {x:c - xB, y:0};

        // Step drawing
        if (step >= 1) {
            // λ line (x-axis)
            drawLine(-width, 0, width, 0, {weight:1});
            // Labels points F, C, D
            drawPoint(F.x, F.y, 'F');
            drawPoint(C.x, C.y, 'C');
            drawPoint(D.x, D.y, 'D');
            // Line FC
            drawLine(F.x, F.y, C.x, C.y, {weight:1});
        }
        if (step >= 2) {
            drawPoint(K.x, K.y, 'K');
            drawDashedLine := (ax,ay,bx,by)=>drawLine(ax,ay,bx,by,{dashed:true,weight:1});
            drawDashedLine(C.x,C.y,D.x,D.y);
            drawDashedLine(D.x,D.y,K.x,K.y);
            drawPoint(M.x, M.y, 'M');
            drawDashedLine(M.x,M.y,C.x,C.y);
            drawDashedLine(M.x,M.y,D.x,D.y);
            drawDashedLine(M.x,M.y,K.x,K.y);
            drawArc(M.x, M.y, R);
        }
        if (step >= 3) {
            drawPoint(B.x, B.y, 'B');
        }
        if (step >= 4) {
            drawPoint(A.x, A.y, 'A');
        }
        if (step >= 5) {
            stroke(0);
            strokeWeight(2);
            drawLine(A.x, A.y, B.x, B.y);
            drawLine(B.x, B.y, C.x, C.y);
            drawLine(C.x, C.y, A.x, A.y);
        }
        if (verify) {
            drawPoint(E.x, E.y, 'E');
            drawDashedLine(C.x, C.y, E.x, E.y);
            drawDashedLine(K.x, K.y, B.x, B.y);
        }
    }
</script>
</body>
</html>
