<script>
(() => {

  // ---------- Time array ----------
  const tValues = Array.from({length:301}, (_,i)=>i-50); // -50 to 250 ns

  // ---------- Red (z=0) curves ----------
  const Ired = t => {
    if (t < -15 || t > 170) return 0;
    if (t > 15) return 1;
    return 0.5*(1 + Math.tanh(t/4));
  };
  const Preg = t => {
    if (t < -15) return 0;
    return 4.5*(1 - Math.exp(-(t+15)/50));
  };
  const Creg = t => {
    if (t < -15) return 0;
    return 2.2*(1 - Math.exp(-(t+15)/50));
  };
  const IredArr = tValues.map(Ired);
  const PregArr = tValues.map(Preg);
  const CregArr = tValues.map(Creg);

  // ---------- Helper: format z ----------
  const formatZ = z => Number.isInteger(z) ? `${z}.` : z.toFixed(5);

  // ---------- Helper: compute blue curves ----------
  const computeBlueData = z => {
    const tDelay = z/0.3; // ns
    const scaling = 1 - 0.45*(z/32);

    const IblueArr = tValues.map(t=>{
      const tp = t - tDelay;
      let base;
      if (tp < -15) base = 0;
      else if (tp > 15) base = 1;
      else base =0.5*(1 + Math.tanh(tp/4));
      const envelope = tp > 0 ? Math.exp(-0.035*z*tp/32) : 1;
      let val = base*envelope;
      if (t > 170 + tDelay) val = 0;
      return val;
    });

    const PblueArr = tValues.map(t=>{
      const tp = t - tDelay;
      let val = 0;
      if (tp >= -15) val = 4.5*(1 - Math.exp(-(tp+15)/50));
      val *= scaling;
      if (t > 170 + tDelay) val = 0;
      return val;
    });

    const CblueArr = tValues.map(t=>{
      const tp = t - tDelay;
      let val = 0;
      if (tp >= -15) val = 2.2*(1 - Math.exp(-(tp+15)/50));
      val *= scaling;
      if (t > 170 + tDelay) val = 0;
      return val;
    });

    return {IblueArr, PblueArr, CblueArr, tDelay};
  };

  // ---------- Plot initializer ----------
  const initPlot = (containerId, yRed, yBlue, yTitle, yRange, annText) => {
    const traceRed = {
      x: tValues,
      y: yRed,
      name: 'z = 0 meters',
      mode: 'lines',
      line: {color: 'red', width: 2}
    };
    const traceBlue = {
      x: tValues,
      y: yBlue,
      name: 'z = 0 meters',
      mode: 'lines',
      line: {color: 'blue', width: 2, dash: 'dash'}
    };
    const layout = {
      xaxis: {title: 'time (ns)', range: [-50,250], zeroline: true},
      yaxis: {title: yTitle, range: yRange, zeroline: true},
      margin: {l: 80, r: 20, t: 40, b: 40},
      showlegend: true,
      legend: {x:0.7, y:0.9},
      shapes: [
        {type:'line', x0:0, y0:0, x1:0, y1:1, yref:'paper',
         line:{color:'black', width:2, dash:'dash'}},
        {type:'line', x0:0, y0:0, x1:0, y1:1, yref:'paper',
         line:{color:'black', width:2, dash:'dash'}}
      ],
      annotations: [
        {x: -45, y: 0.9, xref:'x', yref:'y',
         text: annText, showarrow:false},
        {x: 0, y: 0.7, xref:'x', yref:'y',
         text: 'pulse<br>midpoint', showarrow:false,
         xanchor:'left', yanchor:'bottom'}
      ]
    };
    const config = {displayModeBar: false};

    Plotly.newPlot(containerId, [traceRed, traceBlue], layout, config);
  };

  // ---------- Update function ----------
  const updatePlots = z => {
    const {IblueArr, PblueArr, CblueArr, tDelay} = computeBlueData(z);
    const newLegend = `z = ${formatZ(z)} meters`;

    const ids = ['plot-intensity', 'plot-population', 'plot-coherence'];
    const blueArrays = [IblueArr, PblueArr, CblueArr];

    ids.forEach((id, i) => {
      // Update y and legend name for blue trace (index 1)
      Plotly.restyle(id, {'y':[blueArrays[i]]}, [1]);
      Plotly.restyle(id, {'name':[newLegend]}, [1]);

      // Update moving vertical line
      const shape = {
        type:'line', x0:tDelay, y0:0, x1:tDelay, y1:1, yref:'paper',
        line:{color:'black', width:2, dash:'dash'}
      };
      Plotly.relayout(id, { 'shapes[1]': shape });
    });

    document.getElementById('label-z').textContent = formatZ(z);
  };

  // ---------- DOM ready ----------
  window.addEventListener('DOMContentLoaded', () => {
    const slider = document.getElementById('slider-z');
    document.getElementById('label-z').textContent = formatZ(0);

    initPlot('plot-intensity', IredArr, IredArr, 'intensity (arb)', [-0.1,1.1], '(a)');
    initPlot('plot-population', PregArr, PregArr, 'excited-state population × 10⁴', [-0.5,5.5], '(b)');
    initPlot('plot-coherence', CregArr, CregArr, 'coherence × 10²', [-0.2,2.4], '(c)');

    slider.addEventListener('input', e => {
      const z = parseFloat(e.target.value);
      updatePlots(z);
    });
  });

})();
</script>
</body>
