<script>
  // Constants
  const C_A0 = 10;          // mmol/dm³
  const R = 1.987;          // cal/(mol·K)
  const E_f = 25000;        // cal/mol
  const E_r = 225000;       // cal/mol
  const k_f0 = 1e15;        // 1/s
  const rhoCp = 20000;     // cal/(dm³·K)
  const ΔH_rxn = -200000;  // cal/mol

  // Grab elements
  const uaSlider = document.getElementById('slider-ua');
  const kr0Slider = document.getElementById('slider-kr0');
  const tfSlider = document.getElementById('slider-tf');
  const tauSlider = document.getElementById('slider-tau');

  const uaValSpan = document.getElementById('ua-value');
  const kr0ValSpan = document.getElementById('kr0-value');
  const tfValSpan = document.getElementById('tf-value');
  const tauValSpan = document.getElementById('tau-value');

  const plotDiv = document.getElementById('plot-container');

  // Generate temperature array
  const T_vals = [];
  for (let T = 250; T <= 400; T += 1) T_vals.push(T);

  function updateDisplay() {
    uaValSpan.textContent = uaSlider.value;
    tfValSpan.textContent = tfSlider.value;
    tauValSpan.textContent = tauSlider.value;
    const k_r0 = kr0Slider.value * 1e12;
    // Scientific notation with one decimal
    kr0ValSpan.textContent = k_r0.toExponential(1).replace('e', ' x 10');
  }

  function calculateCurves() {
    const UA = parseFloat(uaSlider.value);
    const k_r0_real = parseFloat(kr0Slider.value) * 1e12;
    const T_f = parseFloat(tfSlider.value);
    const tau = parseFloat(tauSlider.value);

    const massY = [];
    const energyY = [];

    T_vals.forEach(T => {
      const k_f = k_f0 * Math.exp(-E_f/(R*T));
      const k_r = k_r0_real * Math.exp(-E_r/(R*T));
      const C_B_mass = (tau * k_f * C_A0) / (1 + tau * (k_f + k_r));
      massY.push(C_B_mass);

      const C_B_energy = (rhoCp * (T - T_f) + UA * tau * (T - T_f)) / (-ΔH_rxn);
      energyY.push(C_B_energy);
    });

    return { massY, energyY };
  }

  function plotData() {
    const curves = calculateCurves();

    // Find indices for annotations
    const idxMass = T_vals.indexOf(360);
    const idxEnergy = T_vals.indexOf(280);

    const traceMass = {
      x: T_vals,
      y: curves.massY,
      mode: 'lines',
