      name: 'Mass balance'
    };

    const traceEnergy = {
      x: T_vals,
      y: curves.energyY,
      mode: 'lines',
      line: { color: '#0000FF', width: 2 },
      name: 'Energy balance'
    };

    const layout = {
      title: 'Product concentration vs Temperature',
      xaxis: {
        title: 'temperature (K)',
        range: [250, 400],
        gridcolor: '#d3d3d3'
      },
      yaxis: {
        title: 'product concentration (mmol/dm³)',
