        gridcolor: '#d3d3d3'
      },
      annotations: [
        {
          x: 360,
          y: curves.massY[idxMass],
          xref: 'x',
          yref: 'y',
          text: 'mass balance',
          showarrow: false,
          font: { color: '#008000', size: 12 }
        },
        {
          x: 280,
          y: curves.energyY[idxEnergy],
          xref: 'x',
          yref: 'y',
          text: 'energy balance',
          showarrow: false,
          font: { color: '#0000FF', size: 12 }
        }
      ],
      legend: { itemsizing: 'constant' },
      margin: { t: 50, l: 70, r: 20, b: 60 }
    };

    Plotly.react(plotDiv, [traceMass, traceEnergy], layout);
  }

  function updateAll() {
    updateDisplay();
    plotData();
  }

  // Attach listeners
  [uaSlider, kr0Slider, tfSlider, tauSlider].forEach(slider =>
    slider.addEventListener('input', updateAll)
  );

  // Initial rendering
  updateAll();
</script>
</body>
</html>
