      <span>reference frame</span>
      <input type="radio" name="ref-frame" id="radio-epitrochoid" value="epitrochoid" checked>
      <label for="radio-epitrochoid">epitrochoid</label>
      <input type="radio" name="ref-frame" id="radio-envelope" value="envelope">
      <label for="radio-envelope">envelope</label>
      <input type="radio" name="ref-frame" id="radio-fixed-centers" value="fixed centers">
      <label for="radio-fixed-centers">fixed centers</label>
    </div>
    <div class="radio-group">
      <span>envelope to show</span>
      <input type="radio" name="envelope-type" id="radio-inner" value="inner" checked>
      <label for="radio-inner">inner</label>
      <input type="radio" name="envelope-type" id="radio-outer" value="outer">
      <label for="radio-outer">outer</label>
      <input type="radio" name="envelope-type" id="radio-both" value="both">
      <label for="radio-both">both</label>
    </div>
    <div>
      <label for="slider-rotation">rotation angle</label>
      <input type="range" id="slider-rotation" min="0" max="6.28318" step="0.0001" value="0">
      <span id="label-rotation">0.0000</span>
    </div>
  </div>
  <div id="canvas-container"></div>
</div>

<script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js"></script>
<script>
/* State */
let state = {
  k: 0.14,
  phi: 0,
  refFrame: 'epitrochoid',
  envelopeType: 'inner'
};

let pInstance = null;

/* P5 Sketch */
const sketch = (p) => {
  pInstance = p;
  const W = 800, H = 600;
  const SCALE = 150;

  p.setup = function() {
    p.createCanvas(W, H).parent('canvas-container');
    p.noLoop();
  };

  p.draw = function() {
    const cx = p.width/2;
    const cy = p.height/2;
    p.background(255);

    /* Axes */
    p.stroke(150);
    p.strokeWeight(1);
    p.line(cx, 0, cx, p.height);   // y-axis
    p.line(0, cy, p.width, cy);   // x-axis

    const ticks = [-1.5, -1, -0.5, 0, 0.5, 1, 1.5];
    p.noStroke();
    p.textSize(12);
    p.textAlign(p.CENTER, p.CENTER);
    p.fill(0);
    ticks.forEach(t => {
      const px = cx + t * SCALE;
      const py = cy;
      /* X tick */
      p.stroke(150);
      p.line(px, py-5, px, py+5);
      /* X label */
      p.noStroke();
      p.text(t.toFixed(1), px, py+15);
      /* Y tick */
      const py2 = cy - t * SCALE;
      const px2 = cx;
      p.stroke(150);
      p.line(px2-5, py2, px2+5, py2);
      /* Y label */
      p.noStroke();
      p.text(t.toFixed(1), px2-30, py2);
    });

    /* Parameters */
    const k = state.k;
    const phi = state.phi;
    const ref = state.refFrame;
    const envelopeT = state.envelopeType;

    const phi_shaft = 2 * phi;
    const theta = -phi_shaft / 3;
    const cosT = Math.cos(theta);
    const sinT = Math.sin(theta);
    const Cx = k * Math.cos(phi_shaft);
    const Cy = k * Math.sin(phi_shaft);

    /* Generate points */
    const N = 200;
    const dt = 2 * Math.PI / (N-1);
    const blue = [];
    const inner = [];
    const outer = [];
    for (let i = 0; i < N; i++) {
      const t = i * dt;
      /* Blue (epitrochoid) */
      const xb = Math.cos(t) - k * Math.cos(3*t);
      const yb = Math.sin(t) - k * Math.sin(3*t);
      blue.push([xb, yb]);
      /* Inner envelope */
      const xi = (2/3) * Math.cos(t) + k * Math.cos(2*t);
      const yi = (2/3) * Math.sin(t) - k * Math.sin(2*t);
      inner.push([xi, yi]);
      /* Outer envelope */
      const xo = (2/3) * Math.cos(t) - k * Math.cos(2*t);
      const yo = (2/3) * Math.sin(t) + k * Math.sin(2*t);
      outer.push([xo, yo]);
    }

    const toPixel = (x, y) => {
      const px = cx + x * SCALE;
      const py = cy - y * SCALE; // inverted y
      return [px, py];
    };

    /* Draw blue curve */
    p.stroke(50,100,200);
    p.strokeWeight(2);
    p.noFill();
    p.beginShape();
    blue.forEach(([x, y]) => {
      let wx = x, wy = y;
      if (ref === 'epitrochoid') {
        /* no transform */
      } else if (ref === 'envelope' || ref === 'fixed centers') {
        /* blue rotates relative to envelope */
        wx = x * cosT + y * sinT - Cx;
        wy = -x * sinT + y * cosT - Cy;
      }
      const [px, py] = toPixel(wx, wy);
      p.vertex(px, py);
    });
    p.endShape();

    /* Draw orange envelope(s) */
    const orangeSets = [];
    if (envelopeT === 'inner' || envelopeT === 'both') orangeSets.push(inner);
    if (envelopeT === 'outer' || envelopeT === 'both') orangeSets.push(outer);

    orangeSets.forEach(pointsSet => {
      p.stroke(230,150,0);
      p.strokeWeight(2);
      p.noFill();
      p.beginShape();
      pointsSet.forEach(([x, y]) => {
        let wx = x, wy = y;
        if (ref === 'epitrochoid' || ref === 'fixed centers') {
          /* orange moves relative to blue */
          wx = x * cosT - y * sinT + Cx;
          wy = x * sinT + y * cosT + Cy;
        } else if (ref === 'envelope') {
          /* orange static */
        }
        const [px, py] = toPixel(wx, wy);
        p.vertex(px, py);
      });
      p.endShape();
    });
  };
};

new p5(sketch);

/* Update numeric labels */
function updateLabels() {
  document.getElementById('label-eccentricity').textContent = state.k.toFixed(4);
  document.getElementById('label-rotation').textContent = state.phi.toFixed(4);
}
updateLabels();

/* Event listeners */
document.getElementById('slider-eccentricity').addEventListener('input', e => {
  state.k = parseFloat(e.target.value);
  updateLabels();
  pInstance.redraw();
});
document.getElementById('slider-rotation').addEventListener('input', e => {
  state.phi = parseFloat(e.target.value);
  updateLabels();
  pInstance.redraw();
});
document.querySelectorAll('input[name="ref-frame"]').forEach(radio => {
  radio.addEventListener('change', e => {
    if (e.target.checked) {
      state.refFrame = e.target.value;
      pInstance.redraw();
    }
  });
});
document.querySelectorAll('input[name="envelope-type"]').forEach(radio => {
  radio.addEventListener('change', e => {
    if (e.target.checked) {
      state.envelopeType = e.target.value;
      pInstance.redraw();
    }
  });
});
</script>
</body>
</html>
