<script>
/* Constants */
const C_UNIT_CONVERSION = 1000;      // kg/m³ conversion
const K_DEPLETION = 0.137;           // empirical constant

/* Helper to get slider values as float */
function sv(id){ return parseFloat(document.getElementById(id).value); }

/* Update slider value display */
function updateDisplay(id, val){ document.getElementById(id).textContent = val.toFixed(3); }

/* Calculate curves and phi_max */
function computeData(){
    const phiAxisMax = sv('slider-phi-axis');
    const steps = 600;
    const df = phiAxisMax/steps;
    const x = [];
    const y1 = [], y2 = [], y3 = [];
    const gamma = sv('slider-gamma-sat');
    const r = sv('slider-r');
    const rpe = sv('slider-rpe');
    const mw = sv('slider-mw');

    const A = (3*gamma/r)*C_UNIT_CONVERSION;
    const B = K_DEPLETION * mw / Math.pow(rpe,3);

    for(let i=0;i<=steps;i++){
        const phi = i*df;
        x.push(phi);
        const y1v = A*phi;
        const y2v = y1v/(1-phi);
        const y3v = y1v + B/phi;
        y1.push(y1v);
        y2.push(y2v);
        y3.push(y3v);
    }

    const phi_max = Math.sqrt(B/A);
    return {x,y1,y2,y3,phi_max, A, B, gamma, r, rpe, mw, phiAxisMax};
}

/* Initial plot */
let plotDiv = document.getElementById('plot-container');
let currentPhi = sv('slider-phi'); // initial phi
let currentPlotData = [];
let currentPlotLayout = {};

function renderPlot(){
    const data = computeData();
    const idxMax = data.x.findIndex(v=>v>=data.phi_max);
    const xShade = data.x.slice(0,idxMax+1);
    const y2Shade = data.y2.slice(0,idxMax+1);
    const y3Shade = data.y3.slice(0,idxMax+1);

    const traces = [
        {x:data.x, y:data.y1, mode:'lines', name:'y1', line:{color:'blue'}},
        {x:data.x, y:data.y2, mode:'lines', name:'y2', line:{color:'red'}},
        {x:xShade, y:y2Shade, mode:'lines', name:'y2_shade',
            line:{color:'red'}, fillcolor:'rgba(173,216,230,0.5)', fill:'tonexty'},
        {x:xShade, y:y3Shade, mode:'lines', name:'y3_shade',
            line:{color:'green'}},
        {x:[currentPhi,currentPhi,currentPhi], y:[
            computeY1(currentPhi), computeY2(currentPhi), computeY3(currentPhi)
        ], mode:'markers', name:'Markers',
            marker:{color:['blue','red','green'], size:8}}
    ];

    const layout = {
        title:'Stability Model',
        xaxis:{title:'ϕ (volume fraction)',range:[0,sv('slider-phi-axis')]},
        yaxis:{title:'C (kg/m³)',range:[0, sv('slider-c-axis')]},
        shapes:[
            {type:'line',x0:currentPhi,x1:currentPhi,y0:0,y1:sv('slider-c-axis'),
             line:{color:'grey',dash:'dash'} }
        ],
        showlegend:false,
        margin:{t:30,l:50,r:50,b:50}
    };

    Plotly.react(plotDiv, traces, layout);
    currentPlotData = traces;
    currentPlotLayout = layout;
    updateInfoDisplays(data);
}

/* Functions to compute y1,y2,y3 at a specific φ */
function computeY1(phi){
    const gamma = sv('slider-gamma-sat');
    const r = sv('slider-r');
    return (3*gamma/r)*C_UNIT_CONVERSION*phi;
}
function computeY2(phi){ return computeY1(phi)/(1-phi); }
function computeY3(phi){
    const gamma = sv('slider-gamma-sat');
    const r = sv('slider-r');
    const rpe = sv('slider-rpe');
    const mw = sv('slider-mw');
    const A = (3*gamma/r)*C_UNIT_CONVERSION;
    const B = K_DEPLETION * mw / Math.pow(rpe,3);
    return A*phi + B/phi;
}

/* Update info display */
function updateInfoDisplays(data){
    const phiMaxStr = `$\\phi_{max}= ${data.phi_max.toFixed(4)}$`;
    document.getElementById('phimax-display').textContent = phiMaxStr;
    const lower = computeY2(currentPhi).toFixed(2);
    const upper = computeY3(currentPhi).toFixed(2);
    const rangeStr = `$\\text{Stability range: } [${lower}, ${upper}]$`;
    document.getElementById('range-display').textContent = rangeStr;
    MathJax.typesetPromise();
}

/* Event listeners for sliders */
function attachListeners(){
    const sliders = [
        {id:'slider-phi', disp:'phi-value-display', type:'number'},
        {id:'slider-gamma-sat', disp:'gamma-sat-value-display', type:'number'},
        {id:'slider-r', disp:'r-value-display', type:'number'},
        {id:'slider-rpe', disp:'rpe-value-display', type:'number'},
        {id:'slider-mw', disp:'mw-value-display', type:'number'},
        {id:'slider-phi-axis', disp:'phi-axis-value-display', type:'number'},
        {id:'slider-c-axis', disp:'c-axis-value-display', type:'number'}
    ];
    sliders.forEach(s=>{
        const el=document.getElementById(s.id);
        el.addEventListener('input', ()=>{
            const val=parseFloat(el.value);
            updateDisplay(s.disp,val);
            if(s.id==='slider-phi'){
                currentPhi=val;
                // Update only markers and vertical line
                const newY=[computeY1(val),computeY2(val),computeY3(val)];
                Plotly.restyle(plotDiv,{'x':[ [val,val,val] ], 'y':[ [newY[0],newY[1],newY[2]] ], 'mode':['markers'] }, [4]); // index 4 markers trace
                Plotly.relayout(plotDiv,{'shapes':[{
                    type:'line',x0:val,x1:val,y0:0,y1:sv('slider-c-axis'),
                    line:{color:'grey',dash:'dash'} }]});
                updateInfoDisplays(computeData());
            }else if(s.id==='slider-phi-axis'){
                // Update max of slider-phi
                const phiAxisMax=val;
                document.getElementById('slider-phi').max=phiAxisMax;
                if(parseFloat(document.getElementById('slider-phi').value)>phiAxisMax){
                    document.getElementById('slider-phi').value=phiAxisMax;
                    updateDisplay('phi-value-display',phiAxisMax);
                    currentPhi = phiAxisMax;
                }
                Plotly.relayout(plotDiv,{xaxis:{range:[0,phiAxisMax]}});
                renderPlot();
            }else{
                renderPlot();
            }
        });
    });
    // Initial display
    sliders.forEach(s=>updateDisplay(s.disp, sv(s.id)));
}

/* Initialize all */
function init(){
    attachListeners();
    renderPlot();
}

window.onload=init;
</script>
</body>
</html>
