  #canvas-container canvas{display:block;}
</style>
</head>
<body>
<div id="main-container">
  <div id="control-panel">
    <label for="slider-time">time relative to collision (s)</label>
    <input type="range" id="slider-time" min="-30" max="30" step="0.1" value="-10">
    <span id="label-time"></span>

    <h4>object 1</h4>
    <label for="slider-m1">mass (kg)</label>
    <input type="range" id="slider-m1" min="1" max="10" step="0.01" value="5">
    <span id="label-m1"></span>
    <label for="slider-v1">speed (m/s)</label>
    <input type="range" id="slider-v1" min="1" max="20" step="0.1" value="10">
    <span id="label-v1"></span>

    <h4>object 2</h4>
    <label for="slider-m2">mass (kg)</label>
    <input type="range" id="slider-m2" min="1" max="10" step="0.01" value="3">
    <span id="label-m2"></span>
    <label for="slider-v2">speed (m/s)</label>
    <input type="range" id="slider-v2" min="1" max="20" step="0.1" value="5">
    <span id="label-v2"></span>

    <label>observer velocity (m/s)</label>
    <div id="joystick-container">
      <div id="joystick-knob"></div>
    </div>
    <span id="label-v0x"></span>
    <span id="label-v0y"></span>
  </div>

  <div id="canvas-container"></div>
</div>

<script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.min.js"></script>
<script>
/* ------- Global state ------- */
let m1 = 5, m2 = 3, v1 = 10, v2 = 5; // v2 slider value not used for physics
let v0x = 1.1, v0y = 1.1;
let t = -10; // current time
const maxT = 30, minT = -30;

// Derived velocities
let u1_cm, u2_cm, v1_cm, v2_cm;

/* ------- Helper functions ------- */
function degToRad(d){return d*Math.PI/180;}
function rotate(vec, angle){
  const cos = Math.cos(angle), sin= Math.sin(angle);
  return {x: vec.x*cos - vec.y*sin, y: vec.x*sin + vec.y*cos};
}
function vectorMag(v){return Math.sqrt(v.x*v.x + v.y*v.y);}
function updateDerived(){
  const s1 = v1;
  const s2 = (m1/m2) * s1;
  u1_cm = {x: s1, y: 0};
  u2_cm = {x: -s2, y: 0};
  const rotA = -45 * Math.PI/180;
  v1_cm = rotate(u1_cm, rotA);
  v2_cm = rotate(u2_cm, rotA);
}
function computeObserverSpeed(){
  const vObs0 = Math.sqrt(v0x*v0x + v0y*v0y);
  const vObs1 = Math.sqrt((u1_cm.x+v0x)**2 + (u1_cm.y+v0y)**2);
  const vObs2 = Math.sqrt((u2_cm.x+v0x)**2 + (u2_cm.y+v0y)**2);
  const KE = 0.5*m1*vObs1*vObs1 + 0.5*m2*vObs2*vObs2;
  return {v0:vObs0,v1:vObs1,v2:vObs2,energy:KE};
}
/* ------- P5.js sketch ------- */
let canvas;
function setup(){
  const cnv = createCanvas(
    document.getElementById('canvas-container').clientWidth,
    document.getElementById('canvas-container').clientHeight);
  cnv.parent('canvas-container');
  canvas = cnv;
  updateDerived();
  window.addEventListener('resize', () => {
    resizeCanvas(
      document.getElementById('canvas-container').clientWidth,
      document.getElementById('canvas-container').clientHeight);
  });
}
function draw(){
  background('#fafafa');
  push();
  translate(width/2, height/2);
  const scale = 20; // 1 m/s ~ 20 pixels
  const vObs0 = {x: v0x, y: v0y};
  // Draw trajectories
  drawingContext.setLineDash([5,5]);
  strokeWeight(2);
  // Particle 1
  stroke('#ff0000');
  const start1 = computePos(u1_cm, t=-maxT, vObs0);
  const mid1 = computePos(u1_cm, t=0, vObs0);
  const end1 = computePos(v1_cm, t=maxT, vObs0);
  line(start1.x*scale, start1.y*scale, mid1.x*scale, mid1.y*scale);
  line(mid1.x*scale, mid1.y*scale, end1.x*scale, end1.y*scale);
  // Particle 2
  stroke('#0000ff');
  const start2 = computePos(u2_cm, t=-maxT, vObs0);
  const mid2 = computePos(u2_cm, t=0, vObs0);
  const end2 = computePos(v2_cm, t=maxT, vObs0);
  line(start2.x*scale, start2.y*scale, mid2.x*scale, mid2.y*scale);
  line(mid2.x*scale, mid2.y*scale, end2.x*scale, end2.y*scale);
  // Draw particles at current time
  const curr1 = computePos((t<0)?u1_cm:v1_cm, t, vObs0);
  const curr2 = computePos((t<0)?u2_cm:v2_cm, t, vObs0);
  noStroke();
  fill('#ff0000');
  ellipse(curr1.x*scale, curr1.y*scale, Math.sqrt(m1)*4, Math.sqrt(m1)*4);
  fill('#0000ff');
  ellipse(curr2.x*scale, curr2.y*scale, Math.sqrt(m2)*4, Math.sqrt(m2)*4);
  // Observer velocity vector
  drawingContext.setLineDash([]);
  stroke('#000');
  strokeWeight(3);
  const arrowLen = Math.sqrt(v0x*v0x+v0y*v0y)*scale;
  line(0,0, -arrowLen, 0);
  // Arrowhead
  const headSize = 10;
  push();
  translate(-arrowLen,0);
  rotate(-Math.atan2(0,1));
  triangle(-headSize, -headSize, -headSize, headSize, 0, 0);
  pop();
  // Text metrics
  const metrics = computeObserverSpeed();
  noStroke();
  fill('#000');
  textSize(16);
  textAlign(LEFT,BOTTOM);
  const info = `v₀=${metrics.v0.toFixed(3)} m/s  v₁=${metrics.v1.toFixed(3)} m/s  v₂=${metrics.v2.toFixed(3)} m/s  KE=${metrics.energy.toFixed(2)} J`;
  text(info, -width/2+10, height/2-10);
  pop();
}
function computePos(v, t, vObs0){
  return {x: v.x*t + vObs0.x*t, y: v.y*t + vObs0.y*t};
}
/* ------- UI updates and event handling ------- */
function initSliders(){
  const controls = [
    {id:'slider-time',labelId:'label-time',step:0.1,default:-10},
    {id:'slider-m1',labelId:'label-m1',step:0.01,default:5},
    {id:'slider-v1',labelId:'label-v1',step:0.1,default:10},
    {id:'slider-m2',labelId:'label-m2',step:0.01,default:3},
    {id:'slider-v2',labelId:'label-v2',step:0.1,default:5}
  ];
  controls.forEach(ctrl=>{
    const el = document.getElementById(ctrl.id);
    const lbl = document.getElementById(ctrl.labelId);
    el.value = ctrl.default;
    lbl.textContent = `${el.value}`;
    el.addEventListener('input', ()=>{
      lbl.textContent = `${el.value}`;
      switch(ctrl.id){
        case 'slider-time': t=parseFloat(el.value); break;
        case 'slider-m1': m1=parseFloat(el.value); break;
        case 'slider-v1': v1=parseFloat(el.value); break;
        case 'slider-m2': m2=parseFloat(el.value); break;
        case 'slider-v2': v2=parseFloat(el.value); break; // cosmetic
      }
      if(ctrl.id !== 'slider-time') updateDerived();
    });
  });
}
function initJoystick(){
  const cont = document.getElementById('joystick-container');
  const knob = document.getElementById('joystick-knob');
  let dragging=false;
  let offsetX=0, offsetY=0;
  const size=cont.clientWidth;
  const half=size/2;
  const knobSize=knob.clientWidth;
  function setKnob(x,y){
    knob.style.left = `${x - knobSize/2}px`;
    knob.style.top = `${y - knobSize/2}px`;
  }
  function updateValues(){
    const knobRect = knob.getBoundingClientRect();
    const contRect = cont.getBoundingClientRect();
    const centerX = contRect.left + half;
    const centerY = contRect.top + half;
    const x = knobRect.left + knobSize/2;
    const y = knobRect.top + knobSize/2;
    const dx = x - centerX;
    const dy = y - centerY;
    v0x = dx / half * 5;
    v0y = dy / half * 5;
    document.getElementById('label-v0x').textContent = `v₀ₓ=${v0x.toFixed(2)} m/s`;
    document.getElementById('label-v0y').textContent = `v₀ᵧ=${v0y.toFixed(2)} m/s`;
  }
  // initialize knob position
  setKnob(half + half*(v0x/5), half + half*(v0y/5));
  updateValues();
  cont.addEventListener('mousedown', e=>{
    if(e.target===knob){
      dragging=true;
      const rect = knob.getBoundingClientRect();
      offsetX = e.clientX - rect.left;
      offsetY = e.clientY - rect.top;
      e.preventDefault();
    }
  });
  document.addEventListener('mousemove', e=>{
    if(dragging){
      const rect = cont.getBoundingClientRect();
      let x = e.clientX - rect.left - offsetX + knobSize/2;
      let y = e.clientY - rect.top - offsetY + knobSize/2;
      const rad = half - knobSize/2;
      x = Math.max(ke, Math.min(press, 0));
      if(x<0) x=0; if(y<0) y=0; // no, we can allow movement to full area
      // constrain
      const clampedX = Math.min(Math.max(x, knobSize/2), size-knobSize/2);
      const clampedY = Math.min(Math.max(y, knobSize/2), size-knobSize/2);
      setKnob(clampedX, clampedY);
      updateValues();
    }
  });
  document.addEventListener('mouseup', e=>{dragging=false;});
}
