<body>
    <div id="main-container">
        <!-- Control Panel -->
        <div id="control-panel">
            <div>
                <label for="shift-slider">shift</label>
                <input type="range" id="shift-slider" min="0" max="25" step="1" value="12">
            </div>
            <input type="text" id="text-input" list="phrase-list" value="abcd">
            <datalist id="phrase-list">
                <option value="abcd">
                <option value="test phrase">
                <option value="to be or not to be">
                <option value="the quick brown fox jumped over the lazy dog">
            </datalist>
        </div>
        <!-- Display Area -->
        <div id="display-area">
            <!-- Alphabet Visualization -->
            <div id="alphabet-visualization">
                <div id="plaintext-alphabet"></div>
                <div id="ciphertext-alphabet"></div>
            </div>
            <!-- Key Section -->
            <div id="key-section">
                <p id="encryption-key-display">encryption key: 12</p>
                <p id="decryption-key-display">decryption key: 14</p>
            </div>
            <!-- Text Output Section -->
            <div id="text-output-section">
                <p id="plaintext-output"><span>plaintext : </span>abcd</p>
                <p id="ciphertext-output"><span>ciphertext: </span>MNOP</p>
            </div>
        </div>
    </div>

    <script>
        const shiftSlider = document.getElementById('shift-slider');
        const textInput = document.getElementById('text-input');
        const plaintextAlphabet = document.getElementById('plaintext-alphabet');
        const ciphertextAlphabet = document.getElementById('ciphertext-alphabet');
        const encryptionKeyDisplay = document.getElementById('encryption-key-display');
        const decryptionKeyDisplay = document.getElementById('decryption-key-display');
        const plaintextOutput = document.getElementById('plaintext-output');
        const ciphertextOutput = document.getElementById('ciphertext-output');

        function generateAlphabets(shift) {
            const lower = 'abcdefghijklmnopqrstuvwxyz';
            const upper = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
            plaintextAlphabet.innerHTML = '';
            ciphertextAlphabet.innerHTML = '';

            for (let i = 0; i < 26; i++) {
                const plainSpan = document.createElement('span');
                plainSpan.textContent = lower[i];
                plaintextAlphabet.appendChild(plainSpan);

                const cipherSpan = document.createElement('span');
                cipherSpan.textContent = upper[(i + shift) % 26];
                ciphertextAlphabet.appendChild(cipherSpan);
            }
        }

        function encrypt(text, shift) {
            return text.split('').map(char => {
                const code = char.charCodeAt(0);
                if (code >= 97 && code <= 122) {
                    return String.fromCharCode(((code - 97 + shift) % 26) + 65);
                }
                return char;
            }).join('');
        }

        function updateCipher() {
            const shiftValue = parseInt(shiftSlider.value);
            const inputText = textInput.value.toLowerCase();

            // Update alphabets
            generateAlphabets(shiftValue);

            // Update keys
            encryptionKeyDisplay.textContent = `encryption key: ${shiftValue}`;
            const decryptKey = (26 - shiftValue) % 26;
            decryptionKeyDisplay.textContent = `decryption key: ${decryptKey}`;

            // Update text outputs
            plaintextOutput.innerHTML = `<span>plaintext : </span>${inputText}`;
            const cipherText = encrypt(inputText, shiftValue);
            ciphertextOutput.innerHTML = `<span>ciphertext: </span>${cipherText}`;
        }

        // Event listeners
        shiftSlider.addEventListener('input', updateCipher);
        textInput.addEventListener('input', updateCipher);

        // Initial setup
        window.addEventListener('load', () => {
            generateAlphabets(12);
            updateCipher();
        });
    </script>
</body>
</html>
