<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>Inclined Plane Visualization</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <style>
        body {
            margin: 0;
            display: flex;
            height: 100vh;
            font-family: Arial, sans-serif;
        }
        #control-panel {
            width: 20%;
            background: #f0f0f0;
            padding: 20px;
            display: flex;
            flex-direction: column;
            align-items: center;
            border-right: 1px solid #ccc;
        }
        #visualization-area {
            flex: 1;
            display: flex;
            flex-direction: column;
            padding: 20px;
        }
        #p5-canvas {
            flex: 1;
            background: #fff;
            border: 1px solid #ccc;
        }
        #slider-angle {
            writing-mode: bt-lr;
            -webkit-appearance: slider-vertical;
            height: 200px;
            margin: 10px 0;
        }
        #animation-controls {
            margin-top: 20px;
        }
        button {
            margin: 5px;
            padding: 8px 12px;
            font-size: 16px;
        }
    </style>
</head>
<body>
    <div id="control-panel">
        <label for="slider-angle">slope θ</label>
        <input type="range" id="slider-angle" min="10" max="80" value="30" step="1">
        <div id="animation-controls">
            <p>release block</p>
            <button id="btn-play">▶</button>
            <button id="btn-pause">||</button>
            <button id="btn-reset">|<</button>
        </div>
    </div>
    <div id="visualization-area">
        <p id="info-display"></p>
        <canvas id="p5-canvas"></canvas>
    </div>

    <script>
        let angle = 30;
        let isPlaying = false;
        let startTime = 0;
        let blockPosition = 0;
        const g = 9.81;
        const mass = 1; // Arbitrary mass for visualization
        let currentAcceleration = g * sin(radians(angle));

        function setup() {
            const canvas = createCanvas(windowWidth * 0.8, windowHeight - 100);
            canvas.parent('p5-canvas');
            updateInfoDisplay();
        }

        function draw() {
            background(255);
            translate(width/4, height - 50);

            // Draw inclined plane
            fill(173, 216, 230);
            stroke(255, 255, 0);
            strokeWeight(2);
            triangle(0, 0, width/2, 0, 0, -tan(radians(angle)) * width/2);

            // Draw coordinate axes
            rotate(radians(angle));
            stroke(0);
            strokeWeight(1);
            line(0, 0, 50, 0);
            line(0, 0, 0, -50);
            text('x', 55, 0);
            text('y', 0, -55);
            rotate(-radians(angle));

            // Draw block
            const blockX = blockPosition * cos(radians(angle));
            const blockY = -blockPosition * sin(radians(angle));
            fill(105, 105, 105);
            stroke(0);
            rectMode(CENTER);
            rect(blockX, blockY, 40, 20);

            // Draw force vectors
            const gravVector = createVector(0, 50);
            const normalVector = createVector(0, -50 * cos(radians(angle)));
            const netVector = createVector(50 * sin(radians(angle)), 0);

            // Gravity
            drawArrow(blockX, blockY, blockX + gravVector.x, blockY + gravVector.y, 'red', 'F_grav=mg');

            // Normal force
            drawArrow(blockX, blockY, blockX + normalVector.x, blockY + normalVector.y, 'deepskyblue', 'normal force');

            // Net force
            drawArrow(blockX, blockY, blockX + netVector.x, blockY + netVector.y, 'purple', 'F_net = m g sin(θ)');

            // Decomposed components
            stroke(0);
            strokeWeight(1);
            line(blockX, blockY, blockX + gravVector.x, blockY);
            line(blockX, blockY, blockX, blockY + gravVector.y);
            text('m g cos(θ)', blockX + 10, blockY - 20);

            // Angle marker
            noFill();
            arc(0, 0, 40, 40, -PI/2, -radians(angle) - PI/2);
            text('θ', 25, -20);
        }

        function drawArrow(x1, y1, x2, y2, color, label) {
            stroke(color);
            strokeWeight(2);
            line(x1, y1, x2, y2);
            const vec = createVector(x2 - x1, y2 - y1);
            vec.normalize().mult(5);
            triangle(x2, y2, x2 - vec.x, y2 - vec.y, x2 - vec.y, y2 + vec.x);
            fill(color);
            noStroke();
            text(label, x2 + 5, y2 + 5);
        }

        function updateInfoDisplay() {
            const acceleration = g * sin(radians(angle));
            const info = `slope = \\( \\theta \\) = ${angle}°; acceleration = \\( g \\sin(\\theta) \\) = ${acceleration.toFixed(3)} m/s²`;
            document.getElementById('info-display').innerHTML = info;
            MathJax.typeset();
        }

        // Event Listeners
        document.getElementById('slider-angle').addEventListener('input', (e) => {
            angle = parseInt(e.target.value);
            currentAcceleration = g * sin(radians(angle));
            blockPosition = 0;
            isPlaying = false;
            updateInfoDisplay();
            redraw();
        });

        document.getElementById('btn-play').addEventListener('click', () => {
            if (!isPlaying) {
                isPlaying = true;
                startTime = millis();
            }
        });

        document.getElementById('btn-pause').addEventListener('click', () => {
            isPlaying = false;
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            isPlaying = false;
            blockPosition = 0;
            redraw();
        });

        function windowResized() {
            resizeCanvas(windowWidth * 0.8, windowHeight - 100);
        }

        function draw() {
            background(255);
            translate(width/4, height - 50);

            // Draw inclined plane
            fill(173, 216, 230);
            stroke(255, 255, 0);
            strokeWeight(2);
            triangle(0, 0, width/2, 0, 0, -tan(radians(angle)) * width/2);

            // Draw coordinate axes
            rotate(radians(angle));
            stroke(0);
            strokeWeight(1);
            line(0, 0, 50, 0);
            line(0, 0, 0, -50);
            text('x', 55, 0);
            text('y', 0, -55);
            rotate(-radians(angle));

            // Update block position if playing
            if (isPlaying) {
                const elapsed = millis() - startTime;
                const t = elapsed / 1000;
                blockPosition = 0.5 * currentAcceleration * t * t;
            }

            // Draw block
            const blockX = blockPosition * cos(radians(angle));
            const blockY = -blockPosition * sin(radians(angle));
            fill(105, 105, 105);
            stroke(0);
            rectMode(CENTER);
            rect(blockX, blockY, 40, 20);

            // Draw force vectors
            const gravVector = createVector(0, 50);
            const normalVector = createVector(0, -50 * cos(radians(angle)));
            const netVector = createVector(50 * sin(radians(angle)), 0);

            // Gravity
            drawArrow(blockX, blockY, blockX + gravVector.x, blockY + gravVector.y, 'red', 'F_grav=mg');

            // Normal force
            drawArrow(blockX, blockY, blockX + normalVector.x, blockY + normalVector.y, 'deepskyblue', 'normal force');

            // Net force
            drawArrow(blockX, blockY, blockX + netVector.x, blockY + netVector.y, 'purple', 'F_net = m g sin(θ)');

            // Decomposed components
            stroke(0);
            strokeWeight(1);
            line(blockX, blockY, blockX + gravVector.x, blockY);
            line(blockX, blockY, blockX, blockY + gravVector.y);
            text('m g cos(θ)', blockX + 10, blockY - 20);

            // Angle marker
            noFill();
            arc(0, 0, 40, 40, -PI/2, -radians(angle) - PI/2);
            text('θ', 25, -20);
        }
    </script>
</body>
</html>
