<body>
  <div id="control-panel">
    <div class="control-row" id="elements-control">
      <label for="slider-elements">elements (set, then click "randomize")</label>
      <input type="range" id="slider-elements" min="2" max="50" step="1" value="10">
      <span id="elements-value">10</span>
    </div>
    <div class="control-row" id="step-control">
      <label for="slider-step">step</label>
      <input type="range" id="slider-step" min="1" max="1" step="1" value="1">
      <button id="btn-play-pause">▶</button>
      <button id="btn-step-backward">-</button>
      <button id="btn-step-forward">+</button>
      <button id="btn-reset-step">«</button>
      <span id="step-value">1</span>
    </div>
    <div class="control-row" id="sort-control">
      <label>sort</label>
      <button id="btn-shellsort" class="sort-btn active">shellsort</button>
      <button id="btn-bubblesort" class="sort-btn">bubblesort</button>
      <button id="btn-insertionsort" class="sort-btn">insertionsort</button>
      <button id="btn-cocktail" class="sort-btn">cocktail</button>
      <button id="btn-bogosort" class="sort-btn">bogosort</button>
      <button id="btn-randomize">randomize</button>
    </div>
  </div>
  <div id="visualization-area">
    <canvas id="bar-chart-canvas"></canvas>
  </div>
  <script>
    let state = {
      currentAlgorithm: 'shellsort',
      numElements: 10,
      sortHistory: [],
      currentStep: 1,
      isPlaying: false,
      intervalId: null
    };

    document.getElementById('slider-elements').addEventListener('input', function() {
      document.getElementById('elements-value').textContent = this.value;
    });

    document.getElementById('btn-randomize').addEventListener('click', function() {
      let N = parseInt(document.getElementById('slider-elements').value);
      state.numElements = N;
      let arr = shuffleArray(generateArray(N));
      let algorithm = state.currentAlgorithm;
      let history = [];
      switch(algorithm) {
        case 'shellsort':
          history = shellSort(arr);
          break;
        case 'bubblesort':
          history = bubbleSort(arr);
          break;
        case 'insertionsort':
          history = insertionSort(arr);
          break;
        case 'cocktail':
          history = cocktailSort(arr);
          break;
        case 'bogosort':
          history = bogosort(arr);
          break;
      }
      state.sortHistory = history;
      document.getElementById('slider-step').max = history.length - 1;
      document.getElementById('slider-step').value = 1;
      state.currentStep = 1;
      document.getElementById('step-value').textContent = 1;
      if (state.isPlaying) {
        clearInterval(state.intervalId);
        state.isPlaying = false;
        document.getElementById('btn-play-pause').textContent = '▶';
      }
      draw();
    });

    document.querySelectorAll('.sort-btn').forEach(button => {
      button.addEventListener('click', function() {
        if (this.classList.contains('active')) return;
        document.querySelectorAll('.sort-btn').forEach(b => b.classList.remove('active'));
        this.classList.add('active');
        state.currentAlgorithm = this.id.replace('btn-', '');
        if (state.currentAlgorithm === 'bogosort') {
          document.getElementById('slider-elements').disabled = true;
          document.getElementById('slider-elements').value = 7;
          document.getElementById('elements-value').textContent = 7;
          state.numElements = 7;
        } else {
          document.getElementById('slider-elements').disabled = false;
        }
        document.getElementById('btn-randomize').click();
      });
    });

    document.getElementById('btn-play-pause').addEventListener('click', function() {
      if (state.isPlaying) {
        clearInterval(state.intervalId);
        state.isPlaying = false;
        this.textContent = '▶';
      } else {
        state.isPlaying = true;
        this.textContent = '❚❚';
        state.intervalId = setInterval(() => {
          let slider = document.getElementById('slider-step');
          let max = parseInt(slider.max);
          let current = parseInt(slider.value);
          if (current < max) {
            current++;
            slider.value = current;
            state.currentStep = current;
            document.getElementById('step-value').textContent = current;
            draw();
          } else {
            clearInterval(state.intervalId);
            state.isPlaying = false;
            this.textContent = '▶';
          }
        }, 200);
      }
    });

    document.getElementById('btn-step-backward').addEventListener('click', function() {
      let slider = document.getElementById('slider-step');
      let current = parseInt(slider.value);
      if (current > 1) {
        current--;
        slider.value = current;
        state.currentStep = current;
        document.getElementById('step-value').textContent = current;
        draw();
      }
    });

    document.getElementById('btn-step-forward').addEventListener('click', function() {
      let slider = document.getElementById('slider-step');
      let max = parseInt(slider.max);
      let current = parseInt(slider.value);
      if (current < max) {
        current++;
        slider.value = current;
        state.currentStep = current;
        document.getElementById('step-value').textContent = current;
        draw();
      }
    });

    document.getElementById('btn-reset-step').addEventListener('click', function() {
      document.getElementById('slider-step').value = 1;
      state.currentStep = 1;
      document.getElementById('step-value').textContent = 1;
      draw();
    });

    document.getElementById('slider-step').addEventListener('input', function() {
      state.currentStep = parseInt(this.value);
      document.getElementById('step-value').textContent = this.value;
      draw();
    });

    function generateArray(n) {
      return Array.from({length: n}, (_, i) => i + 1);
    }

    function shuffleArray(array) {
      let arr = array.slice();
      for (let i = arr.length - 1; i > 0; i--) {
        let j = Math.floor(Math.random() * (i + 1));
        [arr[i], arr[j]] = [arr[j], arr[i]];
      }
      return arr;
    }

    function shellSort(arr) {
      let history = [arr.slice()];
      let n = arr.length;
      let gaps = [Math.floor(n / 2)];
      while (gaps[0] > 0) {
        let gap = gaps[0];
        for (let i = gap; i < n; i++) {
          let temp = arr[i];
          let j = i;
          while (j >= gap && arr[j - gap] > temp) {
            arr[j] = arr[j - gap];
            j -= gap;
            history.push(arr.slice());
          }
          arr[j] = temp;
          history.push(arr.slice());
        }
        gaps.unshift(Math.floor(gap / 2));
      }
      return history;
    }

    function bubbleSort(arr) {
      let history = [arr.slice()];
      let n = arr.length;
      let swapped;
      do {
        swapped = false;
        for (let i = 0; i < n - 1; i++) {
          if (arr[i] > arr[i + 1]) {
            [arr[i], arr[i + 1]] = [arr[i + 1], arr[i]];
            swapped = true;
            history.push(arr.slice());
          }
        }
        n--;
      } while (swapped);
      return history;
    }

    function insertionSort(arr) {
      let history = [arr.slice()];
      for (let i = 1; i < arr.length; i++) {
        let key = arr[i];
        let j = i - 1;
        while (j >= 0 && arr[j] > key) {
          arr[j + 1] = arr[j];
          j--;
          history.push(arr.slice());
        }
        arr[j + 1] = key;
        history.push(arr.slice());
      }
      return history;
    }

    function cocktailSort(arr) {
      let history = [arr.slice()];
      let swapped;
      let start = 0;
      let end = arr.length;
      do {
        swapped = false;
        for (let i = start; i < end - 1; i++) {
          if (arr[i] > arr[i + 1]) {
            [arr[i], arr[i + 1]] = [arr[i + 1], arr[i]];
            swapped = true;
            history.push(arr.slice());
          }
        }
        end--;
        if (!swapped) break;
        swapped = false;
        for (let i = end - 1; i > start; i--) {
          if (arr[i] < arr[i - 1]) {
            [arr[i], arr[i - 1]] = [arr[i - 1], arr[i]];
            swapped = true;
            history.push(arr.slice());
          }
        }
        start++;
      } while (swapped);
      return history;
    }

    function bogosort(arr) {
      let history = [arr.slice()];
      function isSorted(a) {
        for (let i = 0; i < a.length - 1; i++) {
          if (a[i] > a[i + 1]) return false;
        }
        return true;
      }
      while (!isSorted(arr)) {
        for (let i = arr.length - 1; i > 0; i--) {
          let j = Math.floor(Math.random() * (i + 1));
          [arr[i], arr[j]] = [arr[j], arr[i]];
        }
        history.push(arr.slice());
      }
      return history;
    }

    let myp5 = new p5(function(p) {
      p.setup = function() {
        let canvas = p.createCanvas(800, 400);
        canvas.parent('visualization-area');
        p.noLoop();
      };

      p.draw = function() {
        p.background(255);
        drawYAxis(p);
        drawBars(p);
      };

      function drawYAxis(p) {
        let margin = 40;
        let chartHeight = p.height - 2 * margin;
        p.stroke(0);
        p.line(margin, margin, margin, p.height - margin);
        for (let y = 0; y <= 20; y += 5) {
          let py = p.map(y, 0, 20, margin + chartHeight, margin);
          p.line(margin - 5, py, margin + 5, py);
          p.fill(0);
          p.noStroke();
          p.textAlign(p.RIGHT, p.CENTER);
          p.text(y, margin - 10, py);
        }
      }

      function drawBars(p) {
        let margin = 40;
        let chartWidth = p.width - 2 * margin;
        let chartHeight = p.height - 2 * margin;
        let N = state.numElements;
        let currentArray = state.sortHistory[state.currentStep];

        if (!currentArray) return;

        let barWidth = chartWidth / N;
        p.stroke(0);
        for (let i = 0; i < N; i++) {
          let value = currentArray[i];
          let barHeight = p.map(value, 1, N, 0, chartHeight);
          let x = margin + i * barWidth;
          let y = p.height - margin - barHeight;
          let hue = p.map(value, 1, N, 240, 0);
          p.fill(p.color('hsb(' + hue + ', 100%, 80%)'));
          p.rect(x, y, barWidth, barHeight);
        }
      }
    }, 'bar-chart-canvas');

    function draw() {
      myp5.redraw();
    }

    // Initialize with default values
    document.getElementById('btn-randomize').click();
  </script>
</body>
</html>
