            <div class="slider-row">
                <label for="slider-x2">x₂</label>
                <input type="range" id="slider-x2" min="-1000" max="1000" step="1" value="100">
                <span id="value-x2"></span>
            </div>
            <div class="slider-row">
                <label for="slider-v2">v₂</label>
                <input type="range" id="slider-v2" min="-15" max="15" step="0.01" value="5">
                <span id="value-v2"></span>
            </div>
        </div>

        <!-- Visualization Section -->
        <div id="visualization-area">
            <h2 id="result-text"></h2>
            <div id="plot-container"></div>
        </div>

    </div>

    <!-- CDN for Plotly.js -->
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>

    <script>
        document.addEventListener('DOMContentLoaded', function() {
            const sliders = {
                x1: document.getElementById('slider-x1'),
                v1: document.getElementById('slider-v1'),
                x2: document.getElementById('slider-x2'),
                v2: document.getElementById('slider-v2')
            };
            const valueDisplays = {
                x1: document.getElementById('value-x1'),
                v1: document.getElementById('value-v1'),
                x2: document.getElementById('value-x2'),
                v2: document.getElementById('value-v2')
            };
            const resultText = document.getElementById('result-text');
            const resetButton = document.getElementById('btn-reset');
            let plotInitialized = false;

            function updateValues() {
                valueDisplays.x1.textContent = sliders.x1.value;
                valueDisplays.v1.textContent = parseFloat(sliders.v1.value).toFixed(2);
                valueDisplays.x2.textContent = sliders.x2.value;
                valueDisplays.v2.textContent = parseFloat(sliders.v2.value).toFixed(2);
            }

            function calculateIntersection() {
                const x1 = parseFloat(sliders.x1.value);
                const v1 = parseFloat(sliders.v1.value);
                const x2 = parseFloat(sliders.x2.value);
                const v2 = parseFloat(sliders.v2.value);

                if (v1 === v2) {
                    resultText.textContent = "The runners' paths never intersect";
                    return { valid: false };
                }

                const T = (x1 - x2) / (v2 - v1);
                if (T < 0 || T > 1000) {
                    resultText.textContent = "The runners' paths never intersect";
                    return { valid: false };
                }

                const X = x1 + v1 * T;
                resultText.textContent = `T = ${T.toFixed(2)} s | X = ${X.toFixed(2)} m`;
                return { valid: true, T, X };
            }

            function updatePlot() {
                const x1 = parseFloat(sliders.x1.value);
                const v1 = parseFloat(sliders.v1.value);
                const x2 = parseFloat(sliders.x2.value);
                const v2 = parseFloat(sliders.v2.value);
                const intersection = calculateIntersection();

                const runner1 = {
                    x: [0, 1000],
                    y: [x1, x1 + v1 * 1000],
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'rgb(70, 130, 180)' }
                };

                const runner2 = {
                    x: [0, 1000],
                    y: [x2, x2 + v2 * 1000],
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'rgb(255, 165, 0)' }
                };

                const intersectionMarker = {
                    x: intersection.valid ? [intersection.T] : [],
                    y: intersection.valid ? [intersection.X] : [],
                    type: 'scatter',
                    mode: 'markers',
                    marker: {
                        color: 'green',
                        size: 10,
                        line: {
                            color: 'darkgreen',
                            width: 2
                        }
                    }
                };

                const data = [runner1, runner2, intersectionMarker];

                const layout = {
                    xaxis: {
                        title: 't',
                        range: [0, 1000],
                        gridcolor: '#ddd',
                        zerolinecolor: '#888'
                    },
                    yaxis: {
                        title: 'distance',
                        autorange: true,
                        gridcolor: '#ddd',
                        zerolinecolor: '#888'
                    },
                    showlegend: false,
                    margin: { t: 20, r: 20, b: 40, l: 60 }
                };

                if (plotInitialized) {
                    Plotly.react('plot-container', data, layout);
                } else {
                    Plotly.newPlot('plot-container', data, layout);
                    plotInitialized = true;
                }
            }

            function update() {
                updateValues();
                updatePlot();
            }

            function reset() {
                sliders.x1.value = 500;
                sliders.v1.value = -10;
                sliders.x2.value = 100;
                sliders.v2.value = 5;
                update();
            }

            // Initialize event listeners
            Object.values(sliders).forEach(slider => {
                slider.addEventListener('input', update);
            });
            resetButton.addEventListener('click', reset);

            // Initial update
            update();
        });
    </script>
</body>
</html>
