        }
        #main-container {
            display: flex;
            border: 1px solid #ccc;
            width: 95vw;
            height: 95vh;
        }
        #control-panel {
            width: 250px;
            padding: 10px;
            background: #fff;
            overflow-y: auto;
        }
        #control-panel div {
            margin-bottom: 10px;
            display: flex;
            flex-direction: column;
        }
        #control-panel label {
            font-size: 12px;
            margin-bottom: 5px;
        }
        #control-panel input[type="range"] {
            width: 100%;
        }
        #control-panel span {
            font-size: 12px;
