        }
        #visualization-container {
            flex: 1;
            position: relative;
            background: #e0e0e0;
        }
        #main-canvas {
            width: 100%;
            height: 100%;
            display: block;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div>
                <label for="slider-cylinder-radius">cylinder radius</label>
                <input type="range" id="slider-cylinder-radius" min="0.5" max="3.0" step="0.05">
                <span id="label-cylinder-radius"></span>
            </div>
            <div>
                <label for="slider-opacity">opacity</label>
                <input type="range" id="slider-opacity" min="0.0" max="1.0" step="0.01">
                <span id="label-opacity"></span>
            </div>
            <div>
                <label for="slider-drill-radius">drill radius</label>
                <input type="range" id="slider-drill-radius" min="0.1" max="2.5" step="0.05">
                <span id="label-drill-radius"></span>
            </div>
            <div>
                <label for="slider-inclination">inclination</label>
                <input type="range" id="slider-inclination" min="0" max="1.57" step="0.01">
                <span id="label-inclination"></span>
            </div>
            <div>
                <label for="slider-rotation">rotation around vertical</label>
                <input type="range" id="slider-rotation" min="-3.14" max="3.14" step="0.01">
                <span id="label-rotation"></span>
            </div>
            <div>
                <label for="slider-offset">offset</label>
                <input type="range" id="slider-offset" min="-2.0" max="2.0" step="0.01">
                <span id="label-offset"></span>
            </div>
            <div>
                <label for="checkbox-boundary-only">boundary curve only</label>
                <input type="checkbox" id="checkbox-boundary-only">
            </div>
        </div>
        <div id="visualization-container">
            <canvas id="main-canvas"></canvas>
        </div>
    </div>

    <script type="module">
        import * as THREE from 'https://unpkg.com/three@0.158.0/build/three.module.js';
        import { OrbitControls } from 'https://unpkg.com/three@0.158.0/examples/jsm/controls/OrbitControls.js';
        import { CSG } from 'https://unpkg.com/three-bvh-csg@0.0.16/dist/index.module.js';

        // Scene setup
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xe0e0e0);

        const camera = new THREE.PerspectiveCamera(45, window.innerWidth / window.innerHeight, 0.1, 100);
        camera.position.set(5, 5, 5);

        const renderer = new THREE.WebGLRenderer({ canvas: document.getElementById('main-canvas'), antialias: true });
        renderer.setSize(window.innerWidth * 0.75, window.innerHeight);

        const controls = new OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;

        // Lighting
        const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
        scene.add(ambientLight);

        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
        directionalLight.position.set(5, 10, 7.5);
        scene.add(directionalLight);

        // Default values
        const params = {
            cylinderRadius: 2.1,
            opacity: 0.95,
            drillRadius: 1.35,
            inclination: 0.76,
            rotation: 0.9,
            offset: -0.92,
            boundaryOnly: false
        };

        // Geometry references
        let csgMesh = null;
        let boundaryCurves = [];
        let mainCylinderMesh = null;

        function updateLabels() {
            document.getElementById('label-cylinder-radius').textContent = params.cylinderRadius.toFixed(2);
            document.getElementById('label-opacity').textContent = params.opacity.toFixed(2);
            document.getElementById('label-drill-radius').textContent = params.drillRadius.toFixed(2);
            document.getElementById('label-inclination').textContent = params.inclination.toFixed(2);
            document.getElementById('label-rotation').textContent = params.rotation.toFixed(2);
            document.getElementById('label-offset').textContent = params.offset.toFixed(2);
        }

        function createMainCylinder() {
            const geometry = new THREE.CylinderGeometry(params.cylinderRadius, params.cylinderRadius, 8, 64);
            const material = new THREE.MeshStandardMaterial({
                color: params.boundaryOnly ? 0xffffff : 0xD2B48C,
                opacity: params.boundaryOnly ? 0.15 : params.opacity,
                transparent: true
            });
            mainCylinderMesh = new THREE.Mesh(geometry, material);
            mainCylinderMesh.castShadow = true;
            mainCylinderMesh.receiveShadow = true;
            return mainCylinderMesh;
        }

        function createDrillCylinder() {
            const geometry = new THREE.CylinderGeometry(params.drillRadius, params.drillRadius, 10, 64);
            const material = new THREE.MeshStandardMaterial({ color: 0x000000, visible: false });
            const drillMesh = new THREE.Mesh(geometry, material);
            drillMesh.rotation.z = Math.PI / 2; // Horizontal
            drillMesh.rotation.x = params.inclination;
            drillMesh.rotation.y = params.rotation;
            drillMesh.position.y = params.offset;
            return drillMesh;
        }

        function createBoundaryCurve() {
            const frontPoints = [];
            const backPoints = [];
            const R = params.cylinderRadius;
            const r = params.drillRadius;
            const θ = params.inclination;
            const φ = params.rotation;
            const d = params.offset;
            const sinθ = Math.sin(θ);
            const cosθ = Math.cos(θ);
            const sinφ = Math.sin(φ);
            const cosφ = Math.cos(φ);

            for (let t = 0; t <= 2 * Math.PI; t += 0.1) {
                const cosT = Math.cos(t);
                const sinT = Math.sin(t);
                const cosTSq = cosT * cosT;
                const discriminant = R * R - r * r * cosTSq;

                if (discriminant < 0) continue;

                const sqrtDisc = Math.sqrt(discriminant);
                const s1 = (-r * cosθ * sinT + sqrtDisc) / sinθ;
                const s2 = (-r * cosθ * sinT - sqrtDisc) / sinθ;

                [s1, s2].forEach(s => {
                    const x_final = -r * cosT * cosφ + (s * sinθ + r * sinT * cosθ) * sinφ;
                    const z_final = r * cosT * sinφ + (s * sinθ + r * sinT * cosθ) * cosφ;
                    const y_final = s * cosθ - r * sinT * sinθ + d;

                    const point = new THREE.Vector3(x_final, y_final, z_final);

                    // Simple front/back determination based on transformed z_final
                    if (z_final > 0) {
                        frontPoints.push(point);
                    } else {
                        backPoints.push(point);
                    }
                });
            }

            const createTube = (points, color) => {
                if (points.length < 2) return null;
                const curve = new THREE.CatmullRomCurve3(points);
                const geometry = new THREE.TubeGeometry(curve, points.length, 0.05, 8, false);
                const material = new THREE.MeshStandardMaterial({ color, wireframe: false });
                return new THREE.Mesh(geometry, material);
            };

            return {
                front: createTube(frontPoints, 0xFFA500),
                back: createTube(backPoints, 0x4169E1)
            };
        }

        function updateScene() {
            // Clear previous objects
            scene.children.forEach(obj => {
                if (obj.isMesh || obj.isLine) scene.remove(obj);
            });
            boundaryCurves = [];

            // Create geometries
            const mainCylinder = createMainCylinder();
            const drillCylinder = createDrillCylinder();

            // Perform CSG subtraction
            CSG.fromMesh(mainCylinder).then(mainCSG => {
                CSG.fromMesh(drillCylinder).then(drillCSG => {
                    const resultCSG = mainCSG.subtract(drillCSG);
                    resultCSG.toMesh(mainCylinder.geometry, mainCylinder.material).then(csgResult => {
                        csgResult.material.color.set(0xD2B48C);
                        csgResult.castShadow = true;
                        csgResult.receiveShadow = true;
                        scene.add(csgResult);

                        // Add boundary curves
                        const curves = createBoundaryCurve();
                        if (curves.front) scene.add(curves.front);
                        if (curves.back) scene.add(curves.back);
                        boundaryCurves.push(curves.front, curves.back);

                        // Handle boundary-only mode
                        if (params.boundaryOnly) {
                            csgResult.visible = false;
                            mainCylinder.material.opacity = 0.15;
                            mainCylinder.material.color.set(0xffffff);
                        } else {
                            mainCylinder.material.opacity = params.opacity;
                            mainCylinder.material.color.set(0xD2B48C);
                        }
                        scene.add(mainCylinder);
                    });
                });
            });
        }

        // Event listeners
        function setupControls() {
            const sliders = [
                { id: 'slider-cylinder-radius', param: 'cylinderRadius', def: 2.1 },
                { id: 'slider-opacity', param: 'opacity', def: 0.95 },
                { id: 'slider-drill-radius', param: 'drillRadius', def: 1.35 },
                { id: 'slider-inclination', param: 'inclination', def: 0.76 },
                { id: 'slider-rotation', param: 'rotation', def: 0.9 },
                { id: 'slider-offset', param: 'offset', def: -0.92 }
            ];

            sliders.forEach(slider => {
                const element = document.getElementById(slider.id);
                element.value = slider.def;
                element.addEventListener('input', () => {
                    params[slider.param] = parseFloat(element.value);
                    updateLabels();
                    updateScene();
                });
            });

            const checkbox = document.getElementById('checkbox-boundary-only');
            checkbox.checked = params.boundaryOnly;
            checkbox.addEventListener('change', () => {
                params.boundaryOnly = checkbox.checked;
                updateScene();
            });

            updateLabels();
        }

        // Initial setup
        setupControls();
        updateScene();

        // Resize handling
        window.addEventListener('resize', () => {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth * 0.75, window.innerHeight);
        });

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }
        animate();
    </script>
</body>
</html>
