<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>ODE Visualization</title>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <style>
        body {
            margin: 0;
            font-family: Arial, sans-serif;
        }
        #main-container {
            display: flex;
            flex-direction: column;
            min-height: 100vh;
        }
        #controls-container {
            background-color: #f0f0f0;
            padding: 15px;
            border-bottom: 1px solid #ccc;
            position: relative;
        }
        #controls-container::after {
            content: '+';
            position: absolute;
            top: 5px;
            right: 10px;
            font-size: 24px;
            color: #666;
        }
        .control-row {
            display: flex;
            align-items: center;
            margin-bottom: 10px;
        }
        label {
            width: 30px;
            font-weight: bold;
        }
        input[type="range"] {
            flex: 1;
            margin: 0 10px;
        }
        span.value-display {
            width: 60px;
            text-align: right;
        }
        #plot-container {
            flex: 1;
            padding: 20px;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="controls-container">
            <div class="control-row">
                <label for="slider-alpha">α</label>
                <input type="range" id="slider-alpha" min="0.01" max="0.2" step="0.0001" value="0.05">
                <span id="value-alpha">0.0500</span>
            </div>
            <div class="control-row">
                <label for="slider-beta">β</label>
                <input type="range" id="slider-beta" min="1" max="20" step="0.01" value="5">
                <span id="value-beta">5.00</span>
            </div>
            <div class="control-row">
                <label for="slider-gamma">γ</label>
                <input type="range" id="slider-gamma" min="100" max="2000" step="1" value="500">
                <span id="value-gamma">500</span>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        // Get DOM elements
        const sliders = {
            alpha: document.getElementById('slider-alpha'),
            beta: document.getElementById('slider-beta'),
            gamma: document.getElementById('slider-gamma')
        };
        const valueDisplays = {
            alpha: document.getElementById('value-alpha'),
            beta: document.getElementById('value-beta'),
            gamma: document.getElementById('value-gamma')
        };

        // Update value displays on load
        valueDisplays.alpha.textContent = sliders.alpha.value;
        valueDisplays.beta.textContent = parseFloat(sliders.beta.value).toFixed(2);
        valueDisplays.gamma.textContent = Math.round(sliders.gamma.value);

        // RK4 ODE solver
        function solveODE(alpha, beta, gamma) {
            const h = 0.05;
            const tMax = 20;
            const steps = Math.floor(tMax / h);
            const t = new Array(steps);
            const dT = new Array(steps);
            const cX = new Array(steps);

            // Initial conditions
            t[0] = 0;
            dT[0] = 0;
            cX[0] = 0;

            // Derivatives function
            function derivatives(y, t) {
                const [dT, cX] = y;
                const expTerm = Math.exp(dT / (1 + dT/alpha));
                return [
                    gamma * cX * expTerm - beta * dT,
                    1 - cX * expTerm
                ];
            }

            // RK4 integration
            for (let i = 0; i < steps - 1; i++) {
                const k1 = derivatives([dT[i], cX[i]], t[i]);
                const k2 = derivatives(
                    [dT[i] + h/2 * k1[0], cX[i] + h/2 * k1[1]],
                    t[i] + h/2
                );
                const k3 = derivatives(
                    [dT[i] + h/2 * k2[0], cX[i] + h/2 * k2[1]],
                    t[i] + h/2
                );
                const k4 = derivatives(
                    [dT[i] + h * k3[0], cX[i] + h * k3[1]],
                    t[i] + h
                );

                dT[i+1] = dT[i] + h/6 * (k1[0] + 2*k2[0] + 2*k3[0] + k4[0]);
                cX[i+1] = cX[i] + h/6 * (k1[1] + 2*k2[1] + 2*k3[1] + k4[1]);
                t[i+1] = t[i] + h;
            }

            return { t, dT, cX };
        }

        // Plot initialization
        function updatePlot() {
            const alpha = parseFloat(sliders.alpha.value);
            const beta = parseFloat(sliders.beta.value);
            const gamma = parseFloat(sliders.gamma.value);

            // Update value displays
            valueDisplays.alpha.textContent = alpha.toFixed(4);
            valueDisplays.beta.textContent = beta.toFixed(2);
            valueDisplays.gamma.textContent = Math.round(gamma);

            // Solve ODE
            const { t, dT, cX } = solveODE(alpha, beta, gamma);

            // Scale cX
            const scaledCX = cX.map(x => x * 1000);

            // Create plot data
            const data = [
                {
                    x: t,
                    y: dT,
                    mode: 'lines',
                    line: { color: 'orange', width: 2 },
                    name: 'ΔT'
                },
                {
                    x: t,
                    y: scaledCX,
                    mode: 'lines',
                    line: { color: 'steelblue', width: 2 },
                    name: '1000 C_X'
                }
            ];

            // Plot layout
            const layout = {
                xaxis: { title: '', range: [0, 20], zeroline: true },
                yaxis: { title: '', range: [0, 100], zeroline: true },
                showlegend: false,
                margin: { l: 50, r: 20, b: 30, t: 20 },
                plot_bgcolor: '#f0f0f0',
                paper_bgcolor: 'white',
                annotations: [
                    {
                        text: 'ΔT',
                        x: -0.05,
                        y: 100,
                        xref: 'paper',
                        yref: 'y',
                        xanchor: 'right',
                        yanchor: 'top',
                        showarrow: false,
                        font: { color: 'purple' }
                    },
                    {
                        text: '1000 C<sub>X</sub>',
                        x: -0.05,
                        y: 95,
                        xref: 'paper',
                        yref: 'y',
                        xanchor: 'right',
                        yanchor: 'top',
                        showarrow: false,
