        }

        // Event listeners
        sliders.alpha.addEventListener('input', updatePlot);
        sliders.beta.addEventListener('input', updatePlot);
        sliders.gamma.addEventListener('input', updatePlot);

        // Initial plot
        updatePlot();
    </script>
</body>
