    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <style>
        body {
            margin: 0;
            font-family: Arial, sans-serif;
            display: flex;
            flex-direction: column;
            align-items: center;
            background-color: #f0f0f0;
        }
        #controls-container {
            display: flex;
            flex-direction: column;
            gap: 10px;
            padding: 15px;
            background-color: #ffffff;
            width: 100%;
            max-width: 800px;
            box-shadow: 0 2px 5px rgba(0,0,0,0.1);
        }
        .control-group {
            display: flex;
            align-items: center;
            gap: 10px;
        }
        label {
            width: 180px;
        }
        input[type="range"] {
            flex: 1;
            margin: 0 10px;
        }
        #canvas-container {
            margin-top: 10px;
        }
    </style>
</head>
<body>
    <div id="controls-container">
        <div class="control-group">
            <label for="slider-time">Time</label>
            <input type="range" id="slider-time" min="0" max="25" step="0.01" value="7.0">
            <span id="value-time">7.00</span>
        </div>
        <div class="control-group">
            <label for="slider-wavelength">Wavelength</label>
            <input type="range" id="slider-wavelength" min="1" max="8" step="0.01" value="4.0">
            <span id="value-wavelength">4.00</span>
        </div>
        <div class="control-group">
            <label for="slider-longitudinal-amplitude">Longitudinal Amplitude</label>
            <input type="range" id="slider-longitudinal-amplitude" min="0" max="3" step="0.01" value="2.0">
            <span id="value-longitudinal-amplitude">2.00</span>
        </div>
        <div class="control-group">
            <label for="slider-latitudinal-amplitude">Latitudinal Amplitude</label>
            <input type="range" id="slider-latitudinal-amplitude" min="0" max="3" step="0.01" value="1.0">
            <span id="value-latitudinal-amplitude">1.00</span>
        </div>
    </div>
    <div id="canvas-container"></div>

    <script>
        let time = 7.0;
        let wavelength = 4.0;
        let longitudinalAmplitude = 2.0;
        let latitudinalAmplitude = 1.0;
        let isTimeControlled = false;
        let particles = [];
        const gridCols = 10;
        const gridRows = 6;

        function setup() {
            const canvas = createCanvas(700, 450);
            canvas.parent('canvas-container');

            // Initialize particle grid
            const padding = 50;
            const colWidth = (width - 2 * padding) / (gridCols - 1);
            const rowHeight = (height - 2 * padding) / (gridRows - 1);

            for (let row = 0; row < gridRows; row++) {
                for (let col = 0; col < gridCols; col++) {
                    const x = padding + col * colWidth;
                    const y = padding + row * rowHeight;
                    particles.push({ x0: x, y0: y, row });
                }
            }

            // Slider event listeners
            document.getElementById('slider-time').addEventListener('input', (e) => {
                time = parseFloat(e.target.value);
                isTimeControlled = true;
                document.getElementById('value-time').textContent = time.toFixed(2);
            });

            document.getElementById('slider-time').addEventListener('mouseup', () => {
                isTimeControlled = false;
            });

            document.getElementById('slider-wavelength').addEventListener('input', (e) => {
                wavelength = parseFloat(e.target.value);
                document.getElementById('value-wavelength').textContent = wavelength.toFixed(2);
            });

            document.getElementById('slider-longitudinal-amplitude').addEventListener('input', (e) => {
                longitudinalAmplitude = parseFloat(e.target.value);
                document.getElementById('value-longitudinal-amplitude').textContent = longitudinalAmplitude.toFixed(2);
            });

            document.getElementById('slider-latitudinal-amplitude').addEventListener('input', (e) => {
                latitudinalAmplitude = parseFloat(e.target.value);
                document.getElementById('value-latitudinal-amplitude').textContent = latitudinalAmplitude.toFixed(2);
            });
        }

        function draw() {
            if (!isTimeControlled) {
                time += 0.03;
                document.getElementById('slider-time').value = time.toFixed(2);
                document.getElementById('value-time').textContent = time.toFixed(2);
            }

            background('#E0F0FF');

            const k = TWO_PI / wavelength;

            // Draw particles and paths
            particles.forEach(p => {
                const ampDecay = exp(-(p.row / (gridRows - 1)) * 1.5);
                const ampX = longitudinalAmplitude * ampDecay * 20;
                const ampY = latitudinalAmplitude * ampDecay * 20;
                const phase = k * p.x0 - time;

                // Draw elliptical path
                noFill();
                stroke('#8F998F');
                strokeWeight(2);
                ellipse(p.x0, p.y0, 2 * ampX, 2 * ampY);

                // Calculate particle position
                const dx = ampX * cos(phase);
                const dy = ampY * sin(phase);
                p.currentX = p.x0 + dx;
                p.currentY = p.y0 + dy;
            });

            // Draw connecting lines
            stroke('#0000FF');
            strokeWeight(2);
            drawingContext.setLineDash([5, 5]);

            for (let row = 0; row < gridRows; row++) {
                beginShape();
                for (let col = 0; col < gridCols; col++) {
                    const idx = row * gridCols + col;
                    vertex(particles[idx].currentX, particles[idx].currentY);
                }
                endShape();
            }
            drawingContext.setLineDash([]);

            // Draw particles
            fill('#FF0000');
            noStroke();
            particles.forEach(p => {
                ellipse(p.currentX, p.currentY, 8);
            });
        }
    </script>
</body>
</html>
