            flex: 1;
            margin-bottom: 20px;
        }
    </style>
</head>
<body>
    <div class="container">
        <div class="left-column">
            <div class="control-section">
                <label>data generation model</label>
                <button id="btn-arrhenius" class="active-button">Arrhenius</button>
                <button id="btn-exponential">exponential</button>
            </div>
            <div class="control-section">
                <label>n</label>
                <input type="range" id="slider-n" min="2" max="20" step="1" value="6">
                <span id="value-n">6</span>
            </div>
            <div class="control-section">
                <label>T<sub>min</sub> (°C)</label>
                <input type="range" id="slider-tmin" min="0" max="50" step="1" value="25">
                <span id="value-tmin">25</span>
            </div>
            <div class="control-section">
                <label>T<sub>max</sub> (°C)</label>
                <input type="range" id="slider-tmax" min="51" max="100" step="1" value="75">
                <span id="value-tmax">75</span>
            </div>
            <div class="control-section">
                <label>model parameters</label>
                <label>T<sub>ref</sub> (°C)</label>
                <input type="range" id="slider-tref" min="0" max="100" step="1" value="35">
                <span id="value-tref">35</span>
                <label>c (°C<sup>-1</sup>)</label>
                <input type="range" id="slider-c" min="0.01" max="0.1" step="0.001" value="0.025">
                <span id="value-c">0.025</span>
                <label>b (°C)</label>
                <input type="range" id="slider-b" min="200" max="350" step="0.01" value="273.16">
                <span id="value-b">273.16</span>
                <label>a (°C)</label>
                <input type="range" id="slider-a" min="1000" max="10000" step="1" value="4000">
                <span id="value-a">4000</span>
            </div>
        </div>
        <div class="right-column">
            <div id="plot-top"></div>
            <div id="plot-bottom"></div>
        </div>
    </div>

    <script>
        // Initialize state
        let activeModel = 'Arrhenius';
        const buttons = {
            arrhenius: document.getElementById('btn-arrhenius'),
            exponential: document.getElementById('btn-exponential')
        };
        const sliders = {
            n: document.getElementById('slider-n'),
            tmin: document.getElementById('slider-tmin'),
            tmax: document.getElementById('slider-tmax'),
            tref: document.getElementById('slider-tref'),
            c: document.getElementById('slider-c'),
            b: document.getElementById('slider-b'),
            a: document.getElementById('slider-a')
        };
        const values = {
            n: document.getElementById('value-n'),
            tmin: document.getElementById('value-tmin'),
            tmax: document.getElementById('value-tmax'),
            tref: document.getElementById('value-tref'),
            c: document.getElementById('value-c'),
            b: document.getElementById('value-b'),
            a: document.getElementById('value-a')
        };

        // Update display values
        function updateDisplay() {
            Object.keys(sliders).forEach(key => {
                values[key].textContent = sliders[key].value;
            });
        }

        // Enforce T_max > T_min
        function updateTmaxConstraints() {
            const tmin = parseFloat(sliders.tmin.value);
            sliders.tmax.min = tmin + 1;
            if (parseFloat(sliders.tmax.value) <= tmin) {
                sliders.tmax.value = tmin + 1;
                values.tmax.textContent = tmin + 1;
            }
        }

        // Linear regression helper
        function linearRegression(data) {
            const n = data.length;
            const x = data.map(d => d[0]);
            const y = data.map(d => d[1]);
            const xMean = x.reduce((a, b) => a + b) / n;
            const yMean = y.reduce((a, b) => a + b) / n;
            let numerator = 0, denominator = 0, ssTot = 0, ssRes = 0;
            for (let i = 0; i < n; i++) {
                numerator += (x[i] - xMean) * (y[i] - yMean);
                denominator += (x[i] - xMean) ** 2;
                ssTot += (y[i] - yMean) ** 2;
            }
            const slope = numerator / denominator;
            const intercept = yMean - slope * xMean;
            for (let i = 0; i < n; i++) {
                const yPred = slope * x[i] + intercept;
                ssRes += (y[i] - yPred) ** 2;
            }
            const rSquared = 1 - (ssRes / ssTot);
            return { slope, intercept, rSquared };
        }

        // Generate data and fit
        function processData() {
            const n = parseInt(sliders.n.value);
            const tmin = parseFloat(sliders.tmin.value);
            const tmax = parseFloat(sliders.tmax.value);
            const tref = parseFloat(sliders.tref.value);
            const c = parseFloat(sliders.c.value);
            const b = parseFloat(sliders.b.value);
            const a = parseFloat(sliders.a.value);

            const temperatures = Array.from({length: n}, (_, i) => 
                tmin + (tmax - tmin) * i / (n - 1)
            );

            let data, fitResult;
            if (activeModel === 'Arrhenius') {
                // Generate Arrhenius data
                const yRef = Math.exp(-a / (tref + b));
                const y = temperatures.map(T => Math.exp(-a / (T + b)) / yRef);
                data = { x: temperatures, y };

                // Fit exponential model
                const fitData = temperatures.map((T, i) => [T - tref, Math.log(y[i])]);
                fitResult = linearRegression(fitData);
                fitResult.fittedC = fitResult.slope;
            } else {
                // Generate exponential data
                const y = temperatures.map(T => Math.exp(c * (T - tref)));
                data = { x: temperatures, y };

                // Fit Arrhenius model
                const fitData = temperatures.map((T, i) => [1/(T + b), Math.log(y[i])]);
                fitResult = linearRegression(fitData);
                fitResult.fittedA = -fitResult.slope;
            }
            return { data, fitResult };
        }

        // Update plots
        function updatePlots() {
            const { data, fitResult } = processData();
            const tref = parseFloat(sliders.tref.value);
            const b = parseFloat(sliders.b.value);
            const c = parseFloat(sliders.c.value);
            const a = parseFloat(sliders.a.value);

            // Top plot
            const topTraces = [
                {
                    x: data.x,
                    y: data.y,
                    mode: 'markers',
                    marker: { color: '#d62728' },
                    name: 'Data'
                }
            ];

            const fitX = Array.from({length: 100}, (_, i) => 
                data.x[0] + (data.x[data.x.length-1] - data.x[0]) * i / 99
            );

            if (activeModel === 'Arrhenius') {
                const fitY = fitX.map(T => Math.exp(fitResult.fittedC * (T - tref)));
                topTraces.push({
                    x: fitX,
                    y: fitY,
                    mode: 'lines',
                    line: { color: '#d62728' },
                    name: 'Fit'
                });
                Plotly.newPlot('plot-top', topTraces, {
                    title: `exponential model fit to Arrhenius data<br>c = ${fitResult.fittedC.toFixed(4)} R² = ${fitResult.rSquared.toFixed(4)}`,
                    xaxis: { title: 'T (°C)' },
                    yaxis: { title: 'k(T)/k(T_ref)' }
                });
            } else {
                const fitY = fitX.map(T => Math.exp(-fitResult.fittedA / (T + b)) / 
                    Math.exp(-fitResult.fittedA / (tref + b)));
                topTraces.push({
                    x: fitX,
                    y: fitY,
                    mode: 'lines',
                    line: { color: '#d62728' },
                    name: 'Fit'
                });
                Plotly.newPlot('plot-top', topTraces, {
                    title: `Arrhenius model fit to exponential data<br>a = ${fitResult.fittedA.toFixed(2)} R² = ${fitResult.rSquared.toFixed(4)}`,
                    xaxis: { title: 'T (°C)' },
                    yaxis: { title: 'k(T)/k(T_ref)' }
                });
            }

            // Bottom plot
            const bottomTraces = [];
            if (activeModel === 'Arrhenius') {
                const xFit = data.x.map(T => T - tref);
                const yFit = data.y.map(y => Math.log(y));
                bottomTraces.push({
                    x: xFit,
                    y: yFit,
                    mode: 'markers',
                    marker: { color: '#2ca02c' },
                    name: 'Transformed Data'
                });
                const lineY = xFit.map(x => fitResult.slope * x + fitResult.intercept);
                bottomTraces.push({
                    x: [Math.min(...xFit), Math.max(...xFit)],
                    y: [fitResult.slope * Math.min(...xFit) + fitResult.intercept, 
                        fitResult.slope * Math.max(...xFit) + fitResult.intercept],
                    mode: 'lines',
                    line: { color: '#2ca02c' },
                    name: 'Fit'
                });
                Plotly.newPlot('plot-bottom', bottomTraces, {
                    title: `linear fit of the exponential model<br>R² = ${fitResult.rSquared.toFixed(4)}`,
                    xaxis: { title: 'T - T_ref' },
                    yaxis: { title: 'log(k(T)/k(T_ref))' },
                    shapes: [
                        { type: 'line', x0: 0, x1: 0, y0: Math.min(...yFit), y1: Math.max(...yFit), line: { color: 'gray', width: 1 } },
                        { type: 'line', y0: 0, y1: 0, x0: Math.min(...xFit), x1: Math.max(...xFit), line: { color: 'gray', width: 1 } }
                    ]
                });
            } else {
                const xFit = data.x.map(T => 1/(T + b));
                const yFit = data.y.map(y => Math.log(y));
                bottomTraces.push({
                    x: xFit,
                    y: yFit,
                    mode: 'markers',
                    marker: { color: '#1f77b4' },
                    name: 'Transformed Data'
                });
                const lineY = xFit.map(x => fitResult.slope * x + fitResult.intercept);
                bottomTraces.push({
                    x: [Math.min(...xFit), Math.max(...xFit)],
                    y: [fitResult.slope * Math.min(...xFit) + fitResult.intercept, 
                        fitResult.slope * Math.max(...xFit) + fitResult.intercept],
                    mode: 'lines',
                    line: { color: '#1f77b4' },
                    name: 'Fit'
                });
                Plotly.newPlot('plot-bottom', bottomTraces, {
                    title: `linear fit of the Arrhenius model<br>R² = ${fitResult.rSquared.toFixed(4)}`,
                    xaxis: { title: '1/(T + b)' },
                    yaxis: { title: 'log(k(T)/k(T_ref))' },
                    shapes: [
                        { type: 'line', x0: 0, x1: 0, y0: Math.min(...yFit), y1: Math.max(...yFit), line: { color: 'gray', width: 1 } },
                        { type: 'line', y0: 0, y1: 0, x0: Math.min(...xFit), x1: Math.max(...xFit), line: { color: 'gray', width: 1 } }
                    ]
                });
            }
        }

        // Event listeners
        buttons.arrhenius.addEventListener('click', () => {
            activeModel = 'Arrhenius';
            buttons.arrhenius.classList.add('active-button');
            buttons.exponential.classList.remove('active-button');
            updatePlots();
        });
        buttons.exponential.addEventListener('click', () => {
            activeModel = 'exponential';
            buttons.exponential.classList.add('active-button');
            buttons.arrhenius.classList.remove('active-button');
            updatePlots();
        });

        Object.keys(sliders).forEach(key => {
            sliders[key].addEventListener('input', () => {
                updateDisplay();
                if (key === 'tmin') updateTmaxConstraints();
                updatePlots();
            });
        });

        // Initial setup
        updateDisplay();
        updateTmaxConstraints();
        updatePlots();
    </script>
</body>
</html>
