                <label for="slider-lambda">permeability ratio, λ</label>
                <input type="range" id="slider-lambda" min="-8" max="-4" step="0.01" value="-5">
                <span id="value-lambda">0.00001</span>
            </div>
            <div class="control-group">
                <label for="slider-omega">storativity ratio, ω</label>
                <input type="range" id="slider-omega" min="-3" max="-1" step="0.01" value="-2">
                <span id="value-omega">0.01</span>
            </div>
            <div class="control-group">
                <label>option</label>
                <button id="btn-pressure-drop" class="active">pressure drop</button>
                <button id="btn-difference">difference</button>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        // Bessel function approximations (K0 and K1)
        function besselI0(x) {
            const ax = Math.abs(x);
            if (ax < 3.75) {
                const y = x * x;
                return 1 + y * (3.5156229 + y * (3.0899424 + y * (1.2067492 + y * (0.2659732 + y * (0.0360768 + y * 0.0045813)))));
            } else {
                const z = 3.75 / ax;
                return (Math.exp(ax) / Math.sqrt(ax)) * (0.39894228 + z * (0.01328592 + z * (0.00225319 + z * (-0.00157565))));
            }
        }

        function besselK0(x) {
            const ax = Math.abs(x);
            if (ax < 2) {
                const y = x * x;
                return -Math.log(x / 2) * besselI0(x) - 0.25 * y * (1 + y / 16 * (1 + y / 36 * (1 + y / 100)));
            } else {
                const z = 2 / ax;
                return Math.exp(-ax) * Math.sqrt(Math.PI / (2 * ax)) * (1 + z * (1/8 + z * (-1/128 + z * (1/1024))));
            }
        }

        function besselK1(x) {
            const ax = Math.abs(x);
            if (ax < 2) {
                const y = x * x;
                return 1/x + x * (0.15443144 + y * (-0.67278579 + y * (-0.1815689 + y * (0.01031555 + y * (0.02658733 + y * 0.03015052)))));
            } else {
                const z = 2 / ax;
                return Math.exp(-ax) * Math.sqrt(Math.PI / (2 * ax)) * (1 + z * (3/8 + z * (15/128 + z * (-105/1024))));
            }
        }

        // Stehfest algorithm for inverse Laplace transform
        function factorial(n) {
            if (n < 0) return NaN;
            let result = 1;
            for (let i = 2; i <= n; i++) result *= i;
            return result;
        }

        function stehfest(F, t, n_terms) {
            const ln2 = Math.log(2);
            let sum = 0;
            for (let k = 1; k <= n_terms; k++) {
                let vk = 0;
                const m_min = Math.floor((k + 1) / 2);
                const m_max = Math.min(k, n_terms / 2);
                for (let m = m_min; m <= m_max; m++) {
                    const numerator = Math.pow(-1, m + n_terms / 2) * factorial(2 * m) * Math.pow(m, n_terms / 2);
                    const denominator = factorial(n_terms / 2 - m) * factorial(m) * factorial(k - m) * factorial(2 * m - k);
                    vk += numerator / denominator;
                }
                sum += vk * F(k * ln2 / t);
            }
            return (ln2 / t) * sum;
        }

        // Generate logarithmically spaced array
        function generateLogSpace(start, end, count) {
            const result = [];
            const step = (end - start) / (count - 1);
            for (let i = 0; i < count; i++) {
                result.push(Math.pow(10, start + i * step));
            }
            return result;
        }

        // Calculate pressure drop for a single time point
        function calculatePressureDrop(tau, lambda, omega) {
            const laplaceFunc = (s) => {
                const f_s = (omega * (1 - omega) * s + lambda) / ((1 - omega) * s + lambda);
                const sqrt_s_f = Math.sqrt(s * f_s);
                const k0 = besselK0(sqrt_s_f);
                const k1 = besselK1(sqrt_s_f);
                const p_bar = k0 / (sqrt_s_f * k1);
                return p_bar / s;
            };
            return stehfest(laplaceFunc, tau, 10);
        }

        // Update plot based on current parameters
        function updatePlot(forceNewPlot = false) {
            const lambdaExponent = parseFloat(document.getElementById('slider-lambda').value);
            const omegaExponent = parseFloat(document.getElementById('slider-omega').value);
            const lambda = Math.pow(10, lambdaExponent);
            const omega = Math.pow(10, omegaExponent);

            // Update value displays
            document.getElementById('value-lambda').textContent = lambda.toExponential(5);
            document.getElementById('value-omega').textContent = omega.toFixed(4);

            // Determine plot type
            const isPressureDrop = document.getElementById('btn-pressure-drop').classList.contains('active');

            // Generate time values (τ)
            const tau = generateLogSpace(0, 10, 200);

            // Calculate y values
            const y = tau.map(t => {
                const p = calculatePressureDrop(t, lambda, omega);
                return isPressureDrop ? p : p - 0.5 * (Math.log(t) + 0.8091);
            });

            // Plot configuration
            const trace = {
                x: tau,
                y: y,
                mode: 'lines',
                line: { color: 'red', width: 2 }
            };

            const layout = isPressureDrop ? {
                title: "pressure drawdown at the wellbore",
                xaxis: { title: 'dimensionless time, τ', type: 'log', range: [0, 10], dtick: 2, gridcolor: '#ddd' },
                yaxis: { title: 'dimensionless pressure drop, ΔP', range: [0, 12], gridcolor: '#ddd' },
                margin: { l: 80, r: 50, b: 60, t: 60 },
                paper_bgcolor: '#fff',
                plot_bgcolor: '#fff'
            } : {
                title: "deviation from the asymptote",
                xaxis: { title: 'dimensionless time, τ', type: 'log', range: [0, 10], dtick: 2, gridcolor: '#ddd' },
                yaxis: { title: 'dimensionless difference, ΔP − 1/2 (ln(τ) + 0.8091)', range: [0, 3.5], gridcolor: '#ddd' },
                margin: { l: 80, r: 50, b: 60, t: 60 },
                paper_bgcolor: '#fff',
                plot_bgcolor: '#fff'
            };

            const plotContainer = document.getElementById('plot-container');
            if (forceNewPlot || !plotContainer.data || plotContainer.data.length === 0) {
                Plotly.newPlot(plotContainer, [trace], layout);
            } else {
                Plotly.react(plotContainer, [trace], layout);
            }
        }

        // Event listeners
        document.getElementById('slider-lambda').addEventListener('input', () => updatePlot(false));
        document.getElementById('slider-omega').addEventListener('input', () => updatePlot(false));

        document.getElementById('btn-pressure-drop').addEventListener('click', () => {
            if (!document.getElementById('btn-pressure-drop').classList.contains('active')) {
                document.getElementById('btn-pressure-drop').classList.add('active');
                document.getElementById('btn-difference').classList.remove('active');
                updatePlot(true);
            }
        });

        document.getElementById('btn-difference').addEventListener('click', () => {
