<body>
    <div id="left-column">
        <div id="canvas-container"></div>
        <div id="results-container">
            <p>rejuvenation bias <i>u</i> = <span id="results-u">22.7319</span></p>
            <p>site productivity <i>p</i> = <span id="results-p">58.2729</span></p>
        </div>
    </div>
    <div id="right-column">
        <table id="data-table">
            <thead>
                <tr>
                    <th>age</th>
                    <th>volume</th>
                </tr>
            </thead>
            <tbody id="data-table-body"></tbody>
        </table>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <script>
        let dataPoints = [
            { age: 30, volume: 289 },
            { age: 35, volume: 445 },
            { age: 40, volume: 565 },
            { age: 45, volume: 704 },
            { age: 50, volume: 833 },
            { age: 55, volume: 924 }
        ];
        let currentU = 22.7319;
        let currentP = 58.2729;
        let selectedPoint = null;
        let isDragging = false;

        function setup() {
            const canvasContainer = document.getElementById('canvas-container');
            const canvas = createCanvas(500, 500);
            canvas.parent(canvasContainer);
            recalculate();
        }

        function draw() {
            background('#FFFFE0');

            // Draw grid
            stroke(200);
            strokeWeight(1);
            for (let x = 20; x <= 80; x += 10) {
                const px = map(x, 20, 80, 0, width);
                line(px, 0, px, height);
            }
            for (let y = 0; y <= 1500; y += 200) {
                const py = map(y, 0, 1500, height, 0);
                line(0, py, width, py);
            }

            // Draw axes
            stroke(0);
            strokeWeight(2);
            line(0, height, width, height); // x-axis
            line(0, 0, 0, height); // y-axis

            // Draw labels
            fill(0);
            noStroke();
            textAlign(CENTER, BOTTOM);
            text("Forest Growth Curve", width/2, 20);
            textAlign(RIGHT, CENTER);
            text("Wood volume, m³ ha⁻¹", 0, height/2);
            textAlign(CENTER, TOP);
            text("Stand age, yr", width/2, height - 10);

            // Draw "4/5 law" label
            push();
            translate(width - 20, 50);
            rotate(-Math.PI/2);
            text("4/5 law", 0, 0);
            pop();

            // Draw data points
            fill('#0000CD');
            stroke(0);
            strokeWeight(1);
            for (let point of dataPoints) {
                const px = map(point.age, 20, 80, 0, width);
                const py = map(point.volume, 0, 1500, height, 0);
                ellipse(px, py, 10, 10);
            }

            // Draw fitted curve
            if (!isNaN(currentU) && !isNaN(currentP)) {
                noFill();
                stroke('#0000CD');
                strokeWeight(2);
                beginShape();
                const startAge = Math.max(20, currentU + 0.0001);
                for (let x = startAge; x <= 80; x += 0.5) {
                    const y = currentP * Math.pow(x - currentU, 4/5);
                    const px = map(x, 20, 80, 0, width);
                    const py = map(y, 0, 1500, height, 0);
                    vertex(px, py);
                }
                endShape();
            }
        }

        function mousePressed() {
            selectedPoint = null;
            isDragging = false;

            // Check if mouse is over any point
            for (let point of dataPoints) {
                const px = map(point.age, 20, 80, 0, width);
                const py = map(point.volume, 0, 1500, height, 0);
                if (dist(mouseX, mouseY, px, py) < 10) {
                    selectedPoint = point;
                    break;
                }
            }
        }

        function mouseDragged() {
            if (selectedPoint) {
                isDragging = true;
                const age = map(mouseX, 0, width, 20, 80);
                const volume = map(mouseY, height, 0, 0, 1500);
                selectedPoint.age = age;
                selectedPoint.volume = volume;
                recalculate();
            }
        }

        function mouseReleased() {
            if (selectedPoint) {
                selectedPoint = null;
            } else if (!isDragging) {
                // Add new point
                const age = map(mouseX, 0, width, 20, 80);
                const volume = map(mouseY, height, 0, 0, 1500);
                dataPoints.push({ age: age, volume: volume });
                recalculate();
            }
            isDragging = false;
        }

        function doubleClicked() {
            // Check if mouse is over a point
            for (let i = dataPoints.length - 1; i >= 0; i--) {
                const point = dataPoints[i];
                const px = map(point.age, 20, 80, 0, width);
                const py = map(point.volume, 0, 1500, height, 0);
                if (dist(mouseX, mouseY, px, py) < 10) {
                    dataPoints.splice(i, 1);
                    recalculate();
                    break;
                }
            }
        }

        function recalculate() {
            if (dataPoints.length === 0) {
                currentU = 22.7319;
                currentP = 58.2729;
                document.getElementById('results-u').textContent = currentU.toFixed(4);
                document.getElementById('results-p').textContent = currentP.toFixed(4);
                document.getElementById('data-table-body').innerHTML = '';
                return;
            }

            let p = 50;
            let u = 1;
            const learningRate = 0.0001;
            const iterations = 1000;

            for (let iter = 0; iter < iterations; iter++) {
                let sumGradientP = 0;
                let sumGradientU = 0;
                const ages = dataPoints.map(d => d.age);
                const minAge = Math.min(...ages);
                u = Math.min(u, minAge - 0.1);

                for (let point of dataPoints) {
                    const age = point.age;
                    const volume = point.volume;
                    const term = Math.pow(age - u, 4/5);
                    const prediction = p * term;
                    const error = volume - prediction;

                    sumGradientP += -2 * error * term;
                    sumGradientU += 2 * error * (4 * p / 5) * Math.pow(age - u, -1/5);
                }

                p -= learningRate * sumGradientP;
                u -= learningRate * sumGradientU;

                u = Math.min(u, Math.min(...dataPoints.map(d => d.age)) - 0.1);
            }

            currentU = u;
            currentP = p;

            document.getElementById('results-u').textContent = u.toFixed(4);
            document.getElementById('results-p').textContent = p.toFixed(4);

            const tbody = document.getElementById('data-table-body');
            tbody.innerHTML = '';
            dataPoints.forEach(point => {
                const row = document.createElement('tr');
                row.innerHTML = `<td>${point.age.toFixed(2)}</td><td>${point.volume.toFixed(2)}</td>`;
                tbody.appendChild(row);
            });

            redraw();
        }
    </script>
</body>
</html>
