            <button id="reset-button">+</button>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        // Polygon definitions
        const polygons = {
            outer: [[50, 50], [550, 50], [550, 550], [50, 550]],
            holes: [
                [[100, 100], [250, 100], [250, 150], [200, 150], [200, 200], [100, 200]],
                [[300, 100], [450, 100], [450, 250], [400, 250], [400, 200], [300, 200]],
                [[100, 350], [250, 350], [250, 500], [200, 500], [200, 450], [100, 450]],
                [[300, 400], [450, 400], [450, 500], [300, 500]],
                [[300, 300], [350, 300], [350, 350], [300, 350]]
            ]
        };

        let source = { x: 275, y: 275, dragging: false };
        let sliderValue = 3;
        let sortedVertices = [];
        let allEdges = [];
        let currentIntersection = null;

        function setup() {
            const canvas = createCanvas(600, 600);
            canvas.parent('canvas-container');

            // Collect all edges from polygons
            function collectEdges(polygons) {
                let edges = [];
                function addEdges(shape) {
                    for (let i = 0; i < shape.length; i++) {
                        let next = (i + 1) % shape.length;
                        edges.push([shape[i], shape[next]]);
                    }
                }
                addEdges(polygons.outer);
                polygons.holes.forEach(hole => addEdges(hole));
                return edges;
            }
            allEdges = collectEdges(polygons);

            // Event listeners
            document.getElementById('angle-slider').addEventListener('input', (e) => {
                sliderValue = parseInt(e.target.value);
                document.getElementById('angle-value').textContent = sliderValue;
                redraw();
            });

            document.getElementById('reset-button').addEventListener('click', () => {
                source.x = 275;
                source.y = 275;
                sliderValue = 3;
                document.getElementById('angle-slider').value = 3;
                document.getElementById('angle-value').textContent = '3';
                redraw();
            });

            noLoop();
        }

        function draw() {
            background(255);

            // Draw polygons
            fill(255);
            stroke(170);
            strokeWeight(1);
            beginShape();
            polygons.outer.forEach(pt => vertex(pt[0], pt[1]));
            polygons.holes.forEach(hole => {
                beginContour();
                hole.forEach(pt => vertex(pt[0], pt[1]));
                endContour();
            });
            endShape(CLOSE);

            // Draw vertices
            fill(255, 0, 0);
            noStroke();
            function drawVertices(shape) {
                shape.forEach(pt => ellipse(pt[0], pt[1], 4, 4));
            }
            drawVertices(polygons.outer);
            polygons.holes.forEach(hole => drawVertices(hole));

            // Sort vertices by angle
            const allVertices = [polygons.outer, ...polygons.holes].flat();
            sortedVertices = allVertices.map(v => ({
                x: v[0], y: v[1],
                angle: Math.atan2(v[1] - source.y, v[0] - source.x)
            })).sort((a, b) => a.angle - b.angle);

            // Draw visibility polygon
            if (sortedVertices.length > 0) {
                const processed = sliderValue;
                const maxVertices = 38;

                // Calculate visibility points
                const visibilityPoints = sortedVertices.slice(0, processed).map(v => {
                    const rayDir = createVector(v.x - source.x, v.y - source.y).normalize();
                    let closest = null;
                    let closestDist = Infinity;

                    allEdges.forEach(edge => {
                        const intersect = lineIntersect(
                            source.x, source.y,
                            source.x + rayDir.x * 1000, source.y + rayDir.y * 1000,
                            edge[0][0], edge[0][1],
                            edge[1][0], edge[1][1]
                        );
                        if (intersect) {
                            const d = dist(source.x, source.y, intersect.x, intersect.y);
                            if (d < closestDist) {
                                closestDist = d;
                                closest = intersect;
                                currentIntersection = { edge, point: intersect };
                            }
                        }
                    });
                    return closest || { x: v.x, y: v.y };
                });

                // Draw committed part
                if (processed > 0) {
                    fill(255, 255, 153, 150);
                    stroke(170, 170, 170, 128);
                    strokeWeight(1);
                    beginShape();
                    vertex(source.x, source.y);
                    visibilityPoints.slice(0, processed - 1).forEach(p => vertex(p.x, p.y));
                    endShape();
                }

                // Draw active triangle
                if (processed > 1 && processed <= maxVertices) {
                    fill(255, 204, 102, 150);
                    beginShape();
                    vertex(source.x, source.y);
                    vertex(visibilityPoints[processed - 2].x, visibilityPoints[processed - 2].y);
                    vertex(visibilityPoints[processed - 1].x, visibilityPoints[processed - 1].y);
                    endShape();
                }

                // Draw helper graphics if not complete
                if (processed < maxVertices && currentIntersection) {
                    // Draw ray
                    stroke(0, 0, 255);
                    strokeWeight(1);
                    line(source.x, source.y, sortedVertices[processed - 1].x, sortedVertices[processed - 1].y);

                    // Draw sweep arc
                    const startAngle = sortedVertices[0].angle;
                    const endAngle = sortedVertices[processed - 1].angle;
                    noFill();
                    stroke(0, 0, 255);
                    arc(source.x, source.y, 60, 60, startAngle, endAngle);

                    // Highlight intersected edge
                    stroke(0, 204, 0);
                    strokeWeight(3);
                    const edge = currentIntersection.edge;
                    line(edge[0][0], edge[0][1], edge[1][0], edge[1][1]);
                }
            }

            // Draw source point
            fill(0, 0, 255);
            stroke(0, 0, 255);
            strokeWeight(1);
            ellipse(source.x, source.y, 8, 8);
        }

        function mousePressed() {
            if (dist(mouseX, mouseY, source.x, source.y) < 10) {
                source.dragging = true;
            }
        }

        function mouseDragged() {
            if (source.dragging) {
                const newPos = { x: mouseX, y: mouseY };
                if (isPointInPolygon(newPos, polygons.outer) && 
                    !polygons.holes.some(hole => isPointInPolygon(newPos, hole))) {
                    source.x = mouseX;
                    source.y = mouseY;
                    redraw();
                }
            }
        }

        function mouseReleased() {
            source.dragging = false;
        }

        function lineIntersect(x1, y1, x2, y2, x3, y3, x4, y4) {
            const denominator = (x1 - x2) * (y3 - y4) - (y1 - y2) * (x3 - x4);
            if (denominator === 0) return null;

            const t = ((x1 - x3) * (y3 - y4) - (y1 - y3) * (x3 - x4)) / denominator;
            const u = -((x1 - x2) * (y1 - y3) - (y1 - y2) * (x1 - x3)) / denominator;

            if (t >= 0 && t <= 1 && u >= 0 && u <= 1) {
                return {
                    x: x1 + t * (x2 - x1),
                    y: y1 + t * (y2 - y1)
                };
            }
            return null;
        }

        function isPointInPolygon(point, polygon) {
            let inside = false;
            for (let i = 0, j = polygon.length - 1; i < polygon.length; j = i++) {
                const xi = polygon[i][0], yi = polygon[i][1];
                const xj = polygon[j][0], yj = polygon[j][1];
                const intersect = ((yi > point.y) !== (yj > point.y)) &&
                    (point.x < (xj - xi) * (point.y - yi) / (yj - yi) + xi);
                if (intersect) inside = !inside;
            }
            return inside;
        }
    </script>
</body>
</html>
