<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="slider-group">
                <label for="slider-order">expansion order</label>
                <input type="range" id="slider-order" min="1" max="5" step="1" value="5">
                <span id="value-order">5</span>
            </div>
            <div class="slider-group">
                <label for="slider-a">f(0)</label>
                <input type="range" id="slider-a" min="0.1" max="3.0" step="0.1" value="2.0">
                <span id="value-a">2.0</span>
            </div>
            <div class="slider-group">
                <label for="slider-b">f'(0)</label>
                <input type="range" id="slider-b" min="-2.0" max="2.0" step="0.1" value="0.0">
                <span id="value-b">0.0</span>
            </div>
        </div>
        <div id="visualization-area">
            <div id="plot-container"></div>
        </div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', function() {
            const sliderOrder = document.getElementById('slider-order');
            const sliderA = document.getElementById('slider-a');
            const sliderB = document.getElementById('slider-b');
            const valueOrder = document.getElementById('value-order');
            const valueA = document.getElementById('value-a');
            const valueB = document.getElementById('value-b');

            function updatePlot() {
                // Read inputs
                const order = parseInt(sliderOrder.value);
                const a = parseFloat(sliderA.value);
                const b = parseFloat(sliderB.value);

                // Update value displays
                valueOrder.textContent = order;
                valueA.textContent = a.toFixed(1);
                valueB.textContent = b.toFixed(1);

                // Calculate derivatives
                const fDerivs = [a, b];
                fDerivs[2] = -(fDerivs[1] + 1.5 * fDerivs[0]) / fDerivs[0];
                fDerivs[3] = -(
                    fDerivs[2] * fDerivs[1] + 
                    fDerivs[2] + 
                    1.5 * fDerivs[1]
                ) / fDerivs[0];
                fDerivs[4] = -(
                    2 * fDerivs[3] * fDerivs[1] + 
                    Math.pow(fDerivs[2], 2) + 
                    fDerivs[3] + 
                    1.5 * fDerivs[2]
                ) / fDerivs[0];
                fDerivs[5] = -(
                    3 * fDerivs[4] * fDerivs[1] + 
                    4 * fDerivs[3] * fDerivs[2] + 
                    fDerivs[4] + 
                    1.5 * fDerivs[3]
                ) / fDerivs[0];

                // Calculate coefficients
                const coefficients = fDerivs.map((val, k) => val / factorial(k));

                // Generate power series data
                const numPoints = 200;
                const xEnd = 1.5;
                const xValues = Array.from({ length: numPoints }, (_, i) => i * xEnd / (numPoints - 1));
                const fApprox = xValues.map(x => {
                    let sum = 0;
                    for (let k = 0; k <= order; k++) {
                        sum += coefficients[k] * Math.pow(x, k);
                    }
                    return sum;
                });

                // RK4 numerical solution
                const rkData = rk4(a, b, xEnd, numPoints);
                const { x: xRK, y: yRK } = rkData;

                // Determine agreement region
                let xLimit = xEnd;
                for (let i = 0; i < xValues.length; i++) {
                    if (Math.abs(fApprox[i] - yRK[i]) >= 0.01) {
                        xLimit = xValues[i];
                        break;
                    }
                }

                // Generate polynomial title
                const title = formatPolynomial(coefficients, order);

                // Update plot
                const traceRed = {
                    x: xValues,
                    y: fApprox,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'red', width: 2 },
                    name: 'Power Series'
                };
                const traceGreen = {
                    x: xRK,
                    y: yRK,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'green', width: 3 },
                    name: 'Numerical Solution'
                };
                const layout = {
                    title: title,
                    xaxis: { title: 'x', range: [0, 1.5], zeroline: false },
                    yaxis: { title: 'f(x)', range: [0, 4], zeroline: false },
                    margin: { l: 50, r: 20, t: 50, b: 40 },
                    shapes: [{
                        type: 'rect',
                        xref: 'x',
                        yref: 'paper',
                        x0: 0,
                        y0: 0,
                        x1: xLimit,
                        y1: 1,
                        fillcolor: 'lightblue',
                        opacity: 0.4,
                        layer: 'below',
                        line: { width: 0 }
                    }]
                };
                Plotly.react('plot-container', [traceRed, traceGreen], layout);
            }

            function factorial(n) {
                if (n === 0) return 1;
                let result = 1;
                for (let i = 1; i <= n; i++) result *= i;
                return result;
            }

            function rk4(a, b, xEnd, numPoints) {
                const steps = numPoints - 1;
                const h = xEnd / steps;
                let x = 0.0;
                let y1 = a;
                let y2 = b;
                const xValues = [x];
                const yValues = [y1];

                for (let i = 0; i < steps; i++) {
                    const k1y1 = h * y2;
                    const k1y2 = h * (-(y2 + 1.5 * y1) / y1);

                    const k2y1 = h * (y2 + k1y2 / 2);
                    const k2y2 = h * (-( (y2 + k1y2 / 2) + 1.5 * (y1 + k1y1 / 2) ) / (y1 + k1y1 / 2));

                    const k3y1 = h * (y2 + k2y2 / 2);
                    const k3y2 = h * (-( (y2 + k2y2 / 2) + 1.5 * (y1 + k2y1 / 2) ) / (y1 + k2y1 / 2));

                    const k4y1 = h * (y2 + k3y2);
                    const k4y2 = h * (-( (y2 + k3y2) + 1.5 * (y1 + k3y1) ) / (y1 + k3y1));

                    y1 += (k1y1 + 2 * k2y1 + 2 * k3y1 + k4y1) / 6;
                    y2 += (k1y2 + 2 * k2y2 + 2 * k3y2 + k4y2) / 6;
                    x += h;

                    xValues.push(x);
                    yValues.push(y1);
                }

                return { x: xValues, y: yValues };
            }

            function formatPolynomial(coeffs, order) {
                const parts = [];
                for (let k = 0; k <= order; k++) {
                    const c = coeffs[k];
                    if (c === 0) continue;
                    const rounded = c.toFixed(5);
                    if (parts.length === 0) {
                        parts.push(rounded);
                    } else {
                        const sign = c > 0 ? '+' : '-';
                        const absVal = Math.abs(rounded);
                        let term;
                        if (k === 1) {
                            term = `${absVal} x`;
                        } else {
                            term = `${absVal} x^${k}`;
                        }
                        parts.push(`${sign} ${term}`);
                    }
                }
                return `f(x) = ${parts.join(' ')}`;
            }

            // Event listeners
            sliderOrder.addEventListener('input', updatePlot);
            sliderA.addEventListener('input', updatePlot);
            sliderB.addEventListener('input', updatePlot);

            // Initial plot
            updatePlot();
        });
    </script>
</body>
</html>
