<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>Text Frequency Visualization</title>
    <style>
        body {
            margin: 0;
            font-family: Arial, sans-serif;
        }
        #main-container {
            display: flex;
            flex-direction: column;
            height: 100vh;
        }
        #controls {
            padding: 10px;
            background: #f0f0f0;
            display: flex;
            flex-wrap: wrap;
            gap: 15px;
            align-items: center;
        }
        #text-control, #view-controls {
            display: flex;
            align-items: center;
            gap: 8px;
        }
        button {
            padding: 5px 10px;
            cursor: pointer;
        }
        button.active {
            background: #007bff;
            color: white;
            border: none;
            border-radius: 4px;
        }
        #canvas-container {
            flex: 1;
            position: relative;
        }
        canvas {
            display: block;
            width: 100%;
            height: 100%;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="controls">
            <div id="text-control">
                <label for="select-text">text</label>
                <select id="select-text">
                    <option value="To Be Or Not To Be">To Be Or Not To Be</option>
                    <option value="The quick brown fox jumps over the lazy dog">The quick brown fox jumps over the lazy dog</option>
                    <option value="It was the best of times, it was the worst of times">It was the best of times, it was the worst of times</option>
                </select>
            </div>
            <div id="view-controls">
                <label>plot</label>
                <button id="btn-plot-2d" class="active">distribution 2D</button>
                <button id="btn-plot-3d">keyboard 3D</button>
                <label>keyboard</label>
                <button id="btn-keyboard-qwerty" class="active">QWERTY</button>
                <button id="btn-keyboard-dvorak">Dvorak</button>
            </div>
        </div>
        <div id="canvas-container">
            <canvas id="visualization-canvas"></canvas>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script>
        // State management
        let currentText = "To Be Or Not To Be";
        let currentView = "2d";
        let currentLayout = "qwerty";
        let frequencyMap = {};
        let scene, camera, renderer, axesGroup, visualizationGroup;

        // Keyboard layouts
        const layouts = {
            qwerty: {
                rows: [
                    'QWERTYUIOP',
                    'ASDFGHJKL',
                    'ZXCVBNM'
                ],
                colors: ['#8FBC8F', '#4682B4', '#8A2BE2']
            },
            dvorak: {
                rows: [
                    'PYFGCRL',
                    'AOEUIDHTNS',
                    'QJKXBMWVZ'
                ],
                colors: ['#8FBC8F', '#4682B4', '#8A2BE2']
            }
        };

        // Initialize
        document.addEventListener('DOMContentLoaded', () => {
            setupThreeJS();
            setupEventListeners();
            calculateFrequencies();
            updateVisualization();
        });

        function setupThreeJS() {
            const canvas = document.getElementById('visualization-canvas');
            renderer = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
            renderer.setSize(canvas.clientWidth, canvas.clientHeight);

            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xffffff);

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
            scene.add(ambientLight);
            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight.position.set(5, 10, 7.5);
            scene.add(directionalLight);

            // Camera
            camera = new THREE.OrthographicCamera(
                -window.innerWidth / 2, window.innerWidth / 2,
                window.innerHeight / 2, -window.innerHeight / 2,
                0.1, 1000
            );
            camera.position.set(0, 200, 300);
            camera.lookAt(0, 0, 0);

            // Groups
            axesGroup = new THREE.Group();
            visualizationGroup = new THREE.Group();
            scene.add(axesGroup);
            scene.add(visualizationGroup);

            window.addEventListener('resize', onWindowResize);
        }

        function setupEventListeners() {
            document.getElementById('select-text').addEventListener('change', (e) => {
                currentText = e.target.value;
                calculateFrequencies();
                updateVisualization();
            });

            document.getElementById('btn-plot-2d').addEventListener('click', () => {
                currentView = '2d';
                updateButtonStates();
                updateVisualization();
            });

            document.getElementById('btn-plot-3d').addEventListener('click', () => {
                currentView = '3d';
                updateButtonStates();
                updateVisualization();
            });

            document.getElementById('btn-keyboard-qwerty').addEventListener('click', () => {
                currentLayout = 'qwerty';
                updateButtonStates();
                if (currentView === '3d') updateVisualization();
            });

            document.getElementById('btn-keyboard-dvorak').addEventListener('click', () => {
                currentLayout = 'dvorak';
                updateButtonStates();
                if (currentView === '3d') updateVisualization();
            });
        }

        function updateButtonStates() {
            document.querySelectorAll('#view-controls button').forEach(btn => btn.classList.remove('active'));
            document.getElementById(`btn-plot-${currentView}`).classList.add('active');
            document.getElementById(`btn-keyboard-${currentLayout}`).classList.add('active');
        }

        function calculateFrequencies() {
            const text = currentText.toUpperCase();
            frequencyMap = {};
            for (let char of text) {
                if (char >= 'A' && char <= 'Z') {
                    frequencyMap[char] = (frequencyMap[char] || 0) + 1;
                }
            }
            // Initialize all letters
            for (let i = 65; i <= 90; i++) {
                const char = String.fromCharCode(i);
                frequencyMap[char] = frequencyMap[char] || 0;
            }
        }

        function updateVisualization() {
            // Clear existing visualization
            while (visualizationGroup.children.length) {
                visualizationGroup.remove(visualizationGroup.children[0]);
            }

            if (currentView === '2d') {
                create2DChart();
            } else {
                create3DKeyboard();
            }
            updateAxes();
            animate();
        }

        function create2DChart() {
            const maxFreq = Math.max(...Object.values(frequencyMap));
            const barWidth = 20;
            const spacing = 5;
            const totalWidth = (barWidth + spacing) * 26;

            Object.keys(frequencyMap).sort().forEach((char, index) => {
                const freq = frequencyMap[char];
                const height = (freq / maxFreq) * 150 || 1;

                const geometry = new THREE.BoxGeometry(barWidth, height, barWidth);
                const color = getColormapColor(freq / maxFreq);
                const material = new THREE.MeshStandardMaterial({ color: color });
                const bar = new THREE.Mesh(geometry, material);

                bar.position.x = -totalWidth/2 + index * (barWidth + spacing) + barWidth/2;
                bar.position.y = height / 2;
                visualizationGroup.add(bar);

                // Add text label
                const textSprite = createTextSprite(char, 12);
                textSprite.position.set(bar.position.x, height + 10, 0);
                visualizationGroup.add(textSprite);
            });
        }

        function create3DKeyboard() {
            const layout = layouts[currentLayout];
            const maxFreq = Math.max(...Object.values(frequencyMap));
            const keyHeight = 5;
            const keyRadius = 10;

            layout.rows.forEach((row, rowIndex) => {
                const rowColor = layout.colors[rowIndex];
                const zPos = -rowIndex * 30;

                for (let i = 0; i < row.length; i++) {
                    const char = row[i];
                    const freq = frequencyMap[char];
                    const height = (freq / maxFreq) * 50 || 1;

                    const geometry = new THREE.CylinderGeometry(keyRadius, keyRadius, height, 32);
                    const material = new THREE.MeshStandardMaterial({ 
                        color: rowColor,
                        roughness: 0.7,
                        metalness: 0.2
                    });
                    const key = new THREE.Mesh(geometry, material);

                    const xPos = (i - row.length/2) * 25;
                    key.position.set(xPos, height/2, zPos);
                    visualizationGroup.add(key);

                    // Add text label
                    const textSprite = createTextSprite(char, 14);
                    textSprite.position.set(xPos, height + 5, zPos);
                    visualizationGroup.add(textSprite);
                }
            });
        }

        function getColormapColor(value) {
            const r = Math.round(255 * value);
            const g = Math.round(255 * (1 - value));
            const b = 100;
            return new THREE.Color(`rgb(${r},${g},${b})`);
        }

        function createTextSprite(text, size) {
            const canvas = document.createElement('canvas');
            const context = canvas.getContext('2d');
            context.font = `${size}px Arial`;
            const textWidth = context.measureText(text).width;
            canvas.width = textWidth;
            canvas.height = size * 1.5;

            context.clearRect(0, 0, canvas.width, canvas.height);
            context.font = `${size}px Arial`;
            context.fillStyle = 'black';
            context.fillText(text, 0, size);

            const texture = new THREE.CanvasTexture(canvas);
            const spriteMaterial = new THREE.SpriteMaterial({ map: texture });
            const sprite = new THREE.Sprite(spriteMaterial);
            sprite.scale.set(10, 10, 1);
            return sprite;
        }

        function updateAxes() {
            // Clear existing axes
            while (axesGroup.children.length) {
                axesGroup.remove(axesGroup.children[0]);
            }

            const maxFreq = Math.max(...Object.values(frequencyMap));
            const maxY = Math.ceil(maxFreq / 50) * 50 || 50;

            // Y-axis labels
            for (let i = 0; i <= maxY; i += 50) {
                const label = createTextSprite(i.toString(), 12);
                label.position.set(-200, i, 0);
                axesGroup.add(label);
            }

            // Floor grid
            const gridSize = 300;
            const gridMaterial = new THREE.LineBasicMaterial({ color: 0x808080, opacity: 0.3, transparent: true });

            for (let i = -gridSize; i <= gridSize; i += 50) {
                // X lines
                const geometryX = new THREE.BufferGeometry().setFromPoints([
                    new THREE.Vector3(-gridSize, 0, i),
                    new THREE.Vector3(gridSize, 0, i)
                ]);
                axesGroup.add(new THREE.Line(geometryX, gridMaterial));

                // Z lines
                const geometryZ = new THREE.BufferGeometry().setFromPoints([
                    new THREE.Vector3(i, 0, -gridSize),
                    new THREE.Vector3(i, 0, gridSize)
                ]);
                axesGroup.add(new THREE.Line(geometryZ, gridMaterial));
            }
        }

        function onWindowResize() {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        }

        function animate() {
            requestAnimationFrame(animate);
            renderer.render(scene, camera);
        }
    </script>
</body>
</html>
