                </div>
            </div>
            <hr>
            <div class="control-section">
                <h3>shear wave</h3>
                <div>
                    <svg width="40" height="20"><path d="M0 10 C 5 0, 15 20, 20 10 S 25 0, 30 10 S 35 20, 40 10"/></svg>
                    <svg width="40" height="20"><path d="M0 10 H 10 C 15 10, 15 0, 20 0 S 25 10, 30 10 H 40"/></svg>
                </div>
                <div class="control-row">
                    <label for="slider-s-amp">amplitude</label>
                    <input type="range" id="slider-s-amp" min="0" max="0.4" step="0.001" value="0.1">
                    <span id="label-s-amp">0.1</span>
                </div>
                <div class="control-row">
                    <label for="slider-s-wl">wavelength</label>
                    <input type="range" id="slider-s-wl" min="0.5" max="5" step="0.01" value="1">
                    <span id="label-s-wl">1</span>
                </div>
            </div>
        </div>
        <div id="canvas-container">
            <canvas id="p5-canvas"></canvas>
        </div>
    </div>

    <script>
        let particles = [];
        let time = 0;
        let trailLength = 5;
        let numPoints = 50;
        let pAmp = 0.2;
        let pWl = 3;
        let sAmp = 0.1;
        let sWl = 1;
        let isPlaying = false;

        function setup() {
            const canvas = createCanvas(800, 500);
            canvas.parent('canvas-container');
            initializeParticles();

            // Slider event listeners
            document.getElementById('slider-time').addEventListener('input', (e) => {
                time = parseFloat(e.target.value);
                document.getElementById('label-time').textContent = time.toFixed(2);
                redraw();
            });
            document.getElementById('slider-trail').addEventListener('input', (e) => {
                trailLength = parseInt(e.target.value);
                document.getElementById('label-trail').textContent = trailLength;
                redraw();
            });
            document.getElementById('slider-points').addEventListener('input', (e) => {
                numPoints = parseInt(e.target.value);
                document.getElementById('label-points').textContent = numPoints;
                initializeParticles();
                redraw();
            });
            document.getElementById('slider-p-amp').addEventListener('input', (e) => {
                pAmp = parseFloat(e.target.value);
                document.getElementById('label-p-amp').textContent = pAmp.toFixed(3);
                redraw();
            });
            document.getElementById('slider-p-wl').addEventListener('input', (e) => {
                pWl = parseFloat(e.target.value);
                document.getElementById('label-p-wl').textContent = pWl.toFixed(2);
                redraw();
            });
            document.getElementById('slider-s-amp').addEventListener('input', (e) => {
                sAmp = parseFloat(e.target.value);
                document.getElementById('label-s-amp').textContent = sAmp.toFixed(3);
                redraw();
            });
            document.getElementById('slider-s-wl').addEventListener('input', (e) => {
                sWl = parseFloat(e.target.value);
                document.getElementById('label-s-wl').textContent = sWl.toFixed(2);
                redraw();
            });

            // Button event listeners
            document.getElementById('btn-play-pause').addEventListener('click', () => {
                isPlaying = !isPlaying;
                document.getElementById('btn-play-pause').textContent = isPlaying ? 'Pause' : 'Play';
            });
            document.getElementById('btn-reset').addEventListener('click', () => {
                isPlaying = false;
                document.getElementById('btn-play-pause').textContent = 'Play';
                time = 0;
                trailLength = 5;
                numPoints = 50;
                pAmp = 0.2;
                pWl = 3;
                sAmp = 0.1;
                sWl = 1;
                document.getElementById('slider-time').value = 0;
                document.getElementById('slider-trail').value = 5;
                document.getElementById('slider-points').value = 50;
                document.getElementById('slider-p-amp').value = 0.2;
                document.getElementById('slider-p-wl').value = 3;
                document.getElementById('slider-s-amp').value = 0.1;
                document.getElementById('slider-s-wl').value = 1;
                document.getElementById('label-time').textContent = '0.00';
                document.getElementById('label-trail').textContent = '5';
                document.getElementById('label-points').textContent = '50';
                document.getElementById('label-p-amp').textContent = '0.200';
                document.getElementById('label-p-wl').textContent = '3.00';
                document.getElementById('label-s-amp').textContent = '0.100';
                document.getElementById('label-s-wl').textContent = '1.00';
                initializeParticles();
                redraw();
            });
        }

        function initializeParticles() {
            particles = [];
            for (let i = 0; i < numPoints; i++) {
                const x0 = map(i, 0, numPoints-1, 0, 5);
                particles.push({
                    x0: x0,
                    history: []
                });
            }
        }

        function draw() {
            background(255);

            // Update time if playing
            if (isPlaying) {
                time += 0.01;
                if (time > 5) time = 0;
                document.getElementById('slider-time').value = time;
                document.getElementById('label-time').textContent = time.toFixed(2);
            }

            // Read current values
            pAmp = parseFloat(document.getElementById('slider-p-amp').value);
            pWl = parseFloat(document.getElementById('slider-p-wl').value);
            sAmp = parseFloat(document.getElementById('slider-s-amp').value);
            sWl = parseFloat(document.getElementById('slider-s-wl').value);
            trailLength = parseInt(document.getElementById('slider-trail').value);

            // Draw axes
            stroke(0);
            line(0, height-50, width, height-50); // X-axis
            line(width/2, 50, width/2, height-50); // Y-axis
            // Ticks
            for (let x = 0; x <= 5; x++) {
                const px = map(x, 0, 5, 0, width);
                line(px, height-50, px, height-45);
                text(x, px, height-35);
            }
            for (let y = -0.4; y <= 0.4; y += 0.2) {
                const py = map(y, -0.5, 0.5, height-50, 50);
                line(width/2-5, py, width/2, py);
                text(y.toFixed(1), width/2-30, py+5);
            }

            // Draw particles
            for (const p of particles) {
                const dx = pAmp * sin(2 * PI * (p.x0 / pWl - time));
                const dy = sAmp * sin(2 * PI * (p.x0 / sWl - time));
                const xFinal = p.x0 + dx;
                const yFinal = dy;

                // Add to history
                p.history.push({x: xFinal, y: yFinal});
                if (p.history.length > trailLength) p.history.shift();

                // Draw vertical line
                stroke(211, 211, 211);
                const px = map(xFinal, 0, 5, 0, width);
                const py = map(yFinal, -0.5, 0.5, height-50, 50);
                line(px, 50, px, height-50);

                // Draw trail
                if (trailLength > 0) {
                    noStroke();
                    fill(106, 90, 205, 100);
                    for (const h of p.history) {
                        const hx = map(h.x, 0, 5, 0, width);
                        const hy = map(h.y, -0.5, 0.5, height-50, 50);
                        ellipse(hx, hy, 4);
                    }
                }

                // Draw current position
                stroke(106, 90, 205);
                fill(106, 90, 205);
                ellipse(px, py, 8);
            }
        }
    </script>
</body>
</html>
