    <script>
        // Initialize state
        const state = {
            dip: 0,
            depth: 500,
            limit: 1000,
            component: 'Z',
            faultType: 'tensile'
        };

        // DOM elements
        const sliders = {
            dip: document.getElementById('slider-dip'),
            depth: document.getElementById('slider-depth'),
            limit: document.getElementById('slider-limit')
        };
        const valueDisplays = {
            dip: document.getElementById('value-dip'),
            depth: document.getElementById('value-depth'),
            limit: document.getElementById('value-limit')
        };
        const componentButtons = {
            x: document.getElementById('btn-component-x'),
            y: document.getElementById('btn-component-y'),
            z: document.getElementById('btn-component-z')
        };
        const faultTypeButtons = {
            tensile: document.getElementById('btn-fault-tensile'),
            strikeSlip: document.getElementById('btn-fault-strike-slip'),
            normal: document.getElementById('btn-fault-normal')
        };

        // Initialize UI
        function initUI() {
            // Set initial slider values
            sliders.dip.value = state.dip;
            sliders.depth.value = state.depth;
            sliders.limit.value = state.limit;
            // Update displays
            updateValueDisplays();
            // Add event listeners
            sliders.dip.addEventListener('input', handleSliderChange);
            sliders.depth.addEventListener('input', handleSliderChange);
            sliders.limit.addEventListener('input', handleSliderChange);
            // Button group handlers
            document.getElementById('btn-group-component').addEventListener('click', handleComponentClick);
            document.getElementById('btn-group-fault-type').addEventListener('click', handleFaultTypeClick);
        }

        function updateValueDisplays() {
            valueDisplays.dip.textContent = state.dip;
            valueDisplays.depth.textContent = state.depth;
            valueDisplays.limit.textContent = state.limit;
        }

        function handleSliderChange(event) {
            const { id, value } = event.target;
            state[id.replace('slider-', '')] = Number(value);
            updateValueDisplays();
            updatePlot();
        }

        function handleComponentClick(event) {
            if (event.target.tagName === 'BUTTON') {
                Object.values(componentButtons).forEach(btn => btn.classList.remove('active'));
                event.target.classList.add('active');
                state.component = event.target.id.replace('btn-component-', '').toUpperCase();
                updatePlot();
            }
        }

        function handleFaultTypeClick(event) {
            if (event.target.tagName === 'BUTTON') {
                Object.values(faultTypeButtons).forEach(btn => btn.classList.remove('active'));
                event.target.classList.add('active');
                state.faultType = event.target.id.replace('btn-fault-', '');
                updatePlot();
            }
        }

        // Displacement calculation
        function calculateDisplacements(x, y, dipRad, depth, faultType) {
            // Simplified displacement model for demonstration
            const nu = 0.25; // Poisson's ratio
            const b = 1.0; // Dislocation magnitude in meters
            const R = Math.sqrt(x*x + y*y + depth*depth);
            const cosDip = Math.cos(dipRad);
            const sinDip = Math.sin(dipRad);

            let ux = 0, uy = 0, uz = 0;

            // Basic displacement patterns based on fault type
            switch(faultType) {
                case 'tensile':
                    uz = (b * depth * (1 - 2*nu) * (1 - (x*x + y*y)/(R*R))) / (4 * Math.PI * (1 - nu) * R * (R + depth));
                    break;
                case 'strike-slip':
                    ux = (b * y * depth) / (4 * Math.PI * R * (R + depth));
                    uy = (b * x * depth) / (4 * Math.PI * R * (R + depth));
                    break;
                case 'normal':
                    uz = (b * x * depth) / (4 * Math.PI * R * (R + depth));
                    break;
            }

            return { ux, uy, uz };
        }

        // Plot initialization
        let plotInitialized = false;
        let plotData = [{
            type: 'surface',
            x: [],
            y: [],
            z: [],
            colorscale: [['0', '#FFA500'], ['1', '#FFD700']],
            showscale: false,
            contours: {
                x: { show: true, color: 'black', width: 0.5 },
                y: { show: true, color: 'black', width: 0.5 },
                z: { show: false }
            },
            lighting: {
                ambient: 0.8,
                diffuse: 0.8,
                specular: 0.2
            }
        }];

        const layout = {
            title: '',
            scene: {
                xaxis: { title: 'x (m)', range: [-state.limit, state.limit] },
                yaxis: { title: 'y (m)', range: [-state.limit, state.limit] },
                zaxis: { title: 'z (mm)', autorange: true },
                camera: { eye: { x: 1.8, y: 1.8, z: 0.8 } }
            },
            margin: { l: 20, r: 20, b: 20, t: 20 }
        };

        function updatePlot() {
            const dipRad = state.dip * Math.PI / 180;
            const gridSize = 50;
            const xCoords = Array.from({length: gridSize}, (_, i) => 
                state.limit * (-1 + 2 * i / (gridSize - 1)));
            const yCoords = [...xCoords];

            const zValues = xCoords.map(x => 
                yCoords.map(y => {
                    const { ux, uy, uz } = calculateDisplacements(x, y, dipRad, state.depth, state.faultType);
                    const displacement = { X: ux, Y: uy, Z: uz }[state.component];
                    return displacement * 1000; // Convert to mm
                })
            );

            if (!plotInitialized) {
                plotData[0].x = xCoords;
                plotData[0].y = yCoords;
                plotData[0].z = zValues;
                Plotly.newPlot('plot', plotData, layout);
                plotInitialized = true;
            } else {
                Plotly.react('plot', [{
                    ...plotData[0],
                    x: xCoords,
                    y: yCoords,
                    z: zValues,
                    scene: {
                        xaxis: { range: [-state.limit, state.limit] },
                        yaxis: { range: [-state.limit, state.limit] }
                    }
                }]);
            }
        }

        // Initialize
        initUI();
        updatePlot();
    </script>
</body>
</html>
