</head>
<body>
    <div id="app-container">
        <button id="btn-reset">Reset</button>
        <div id="top-controls">
            <div class="opacity-control">
                <label>opacity 1</label>
                <input type="range" id="slider-opacity1" min="0" max="1" step="0.01" value="0.7">
                <span style="margin-left: 5px;">+</span>
            </div>
            <div class="opacity-control">
                <label>opacity 2</label>
                <input type="range" id="slider-opacity2" min="0" max="1" step="0.01" value="0.7">
                <span style="margin-left: 5px;">+</span>
            </div>
            <div class="opacity-control">
                <label>opacity 3</label>
                <input type="range" id="slider-opacity3" min="0" max="1" step="0.01" value="0.7">
                <span style="margin-left: 5px;">+</span>
            </div>
            <div class="opacity-control">
                <label>opacity 4</label>
                <input type="range" id="slider-opacity4" min="0" max="1" step="0.01" value="0.7">
                <span style="margin-left: 5px;">+</span>
            </div>
            <div class="layering-control">
                <label>layering (bottom to top)</label>
                <span id="layering-display" style="margin: 0 5px;">1, 2, 3, 4</span>
                <div class="layering-buttons">
                    <button id="btn-layer-next">▴</button>
                    <button id="btn-layer-prev">▾</button>
                </div>
            </div>
        </div>
        <div id="main-content">
            <div id="color-controls">
                <div class="color-control">
                    <label>color 1</label>
                    <input type="color" id="color-picker1" value="#00ff00">
                </div>
                <div class="color-control">
                    <label>color 2</label>
                    <input type="color" id="color-picker2" value="#0000ff">
                </div>
                <div class="color-control">
                    <label>color 3</label>
                    <input type="color" id="color-picker3" value="#ff0000">
                </div>
                <div class="color-control">
                    <label>color 4</label>
                    <input type="color" id="color-picker4" value="#000000">
                </div>
            </div>
            <div id="canvas-container"></div>
        </div>
    </div>

    <script>
        const sketch = (p) => {
            let layeringOrderIndex = 0;
            const layeringPermutations = [
                [1, 2, 3, 4], [4, 3, 2, 1], [1, 3, 2, 4], [2, 4, 1, 3], [3, 1, 4, 2]
            ];
            const circles = {
                1: { pos: () => [p.width * 0.35, p.height * 0.4], size: () => p.width * 0.4 },
                2: { pos: () => [p.width * 0.65, p.height * 0.4], size: () => p.width * 0.4 },
                3: { pos: () => [p.width * 0.5, p.height * 0.65], size: () => p.width * 0.4 },
                4: { pos: () => [p.width * 0.5, p.height * 0.45], size: () => p.width * 0.3 }
            };

            p.setup = () => {
                const canvas = p.createCanvas(600, 400);
                canvas.parent('canvas-container');
                p.noLoop();
                updateLayerDisplay();
            };

            p.draw = () => {
                p.background('#f0f0f0');
                const order = layeringPermutations[layeringOrderIndex];

                order.forEach(num => {
                    const circle = circles[num];
                    const [x, y] = circle.pos();
                    const size = circle.size();
                    const colorPicker = document.getElementById(`color-picker${num}`);
                    const opacitySlider = document.getElementById(`slider-opacity${num}`);
                    const color = p.color(colorPicker.value);
                    color.setAlpha(p.map(opacitySlider.value, 0, 1, 0, 255));
                    p.fill(color);
                    p.noStroke();
                    p.ellipse(x, y, size);
                });

                for (let i = 1; i <= 4; i++) {
                    const [x, y] = circles[i].pos();
                    p.fill(0);
                    p.textSize(32);
                    p.textAlign(p.CENTER, p.CENTER);
                    p.text(i, x, y);
                }
            };

            window.updateLayerDisplay = () => {
                document.getElementById('layering-display').textContent = 
                    layeringPermutations[layeringOrderIndex].join(', ');
            };

            window.changeLayer = (direction) => {
                layeringOrderIndex = (layeringOrderIndex + direction + layeringPermutations.length) % layeringPermutations.length;
                updateLayerDisplay();
                p.redraw();
            };

            window.resetAll = () => {
                document.getElementById('slider-opacity1').value = 0.7;
                document.getElementById('slider-opacity2').value = 0.7;
                document.getElementById('slider-opacity3').value = 0.7;
                document.getElementById('slider-opacity4').value = 0.7;
                document.getElementById('color-picker1').value = '#00ff00';
                document.getElementById('color-picker2').value = '#0000ff';
                document.getElementById('color-picker3').value = '#ff0000';
                document.getElementById('color-picker4').value = '#000000';
                layeringOrderIndex = 0;
                updateLayerDisplay();
                p.redraw();
            };
        };

        new p5(sketch);

        document.getElementById('btn-layer-next').addEventListener('click', () => sketch.changeLayer(1));
        document.getElementById('btn-layer-prev').addEventListener('click', () => sketch.changeLayer(-1));
        document.getElementById('btn-reset').addEventListener('click', sketch.resetAll);

        document.querySelectorAll('input[type="range"], input[type="color"]').forEach(input => {
            input.addEventListener('input', () => sketch.redraw());
        });
    </script>
</body>
</html>
