<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>Equation of State Fugacity Calculator</title>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <style>
        #app-container {
            padding: 20px;
            font-family: Arial, sans-serif;
        }
        #control-panel {
            display: flex;
            flex-direction: column;
            gap: 15px;
            margin-bottom: 20px;
        }
        #control-panel > div {
            display: flex;
            align-items: center;
            gap: 10px;
        }
        button {
            padding: 5px 15px;
            cursor: pointer;
            border: 1px solid #ccc;
            border-radius: 4px;
        }
        .selected {
            border: 2px solid red;
        }
        #plot-container {
            width: 100%;
            height: 600px;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <div>
                <button id="btn-srk">SRK</button>
                <button id="btn-pr">PR</button>
            </div>
            <div>
                <input type="checkbox" id="checkbox-aspen" checked>
                <label for="checkbox-aspen">comparison with Aspen Plus</label>
            </div>
            <div>
                <label for="slider-pressure">pressure P</label>
                <input type="range" id="slider-pressure" min="1" max="20" step="1" value="10">
                <span id="display-pressure">10</span>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', function() {
            let selectedEoS = 'srk';
            let pressure = 10;
            let aspenChecked = true;

            // Initialize UI
            document.getElementById('btn-srk').classList.add('selected');
            document.getElementById('display-pressure').textContent = pressure;

            // Event Listeners
            document.getElementById('btn-srk').addEventListener('click', () => {
                selectedEoS = 'srk';
                updatePlot();
            });

            document.getElementById('btn-pr').addEventListener('click', () => {
                selectedEoS = 'pr';
                updatePlot();
            });

            document.getElementById('slider-pressure').addEventListener('input', function() {
                pressure = parseInt(this.value);
                document.getElementById('display-pressure').textContent = pressure;
                updatePlot();
            });

            document.getElementById('checkbox-aspen').addEventListener('change', function() {
                aspenChecked = this.checked;
                updatePlot();
            });

            function updatePlot() {
                const temperatures = Array.from({length: 21}, (_, i) => 300 + i * 10);
                const eosData = temperatures.map(T => calculatePhi(T, pressure, selectedEoS));

                const traces = [{
                    x: temperatures,
                    y: eosData,
                    type: 'scatter',
                    mode: 'lines',
                    line: { 
                        color: selectedEoS === 'srk' ? 'red' : 'blue', 
                        width: 2 
                    }
                }];

                if (pressure === 10 && aspenChecked) {
                    traces.push({
                        x: [300, 310, 320, 330, 340, 350, 360, 370, 380, 390, 400],
                        y: [0.868, 0.881, 0.891, 0.900, 0.909, 0.917, 0.924, 0.930, 0.937, 0.942, 0.946],
                        type: 'scatter',
                        mode: 'markers',
                        marker: { color: 'green', symbol: 'square', size: 8 }
                    });
                }

                Plotly.newPlot('plot-container', traces, {
                    xaxis: {
                        title: 'temperature (K)',
                        range: [295, 505],
                        showgrid: true,
                        gridcolor: 'white',
                        gridwidth: 1
                    },
                    yaxis: {
                        title: 'gas-phase fugacity coefficient',
                        range: [0.68, 1.02],
                        showgrid: true,
                        gridcolor: 'white',
                        gridwidth: 1
                    },
                    plot_bgcolor: 'lightgrey',
                    margin: { l: 60, r: 20, b: 60, t: 20 },
                    showlegend: false
                });
            }

            function calculatePhi(T, P, eos) {
                const Tc = 365.57, Pc = 46.65, omega = 0.144, R = 0.0831446;
                const Tr = T / Tc;
                let a, b, m, alpha, a_eff, A, B, Z;

                if (eos === 'srk') {
                    a = 0.42748 * R**2 * Tc**2 / Pc;
                    b = 0.08664 * R * Tc / Pc;
                    m = 0.480 + 1.574*omega - 0.176*omega**2;
                    alpha = (1 + m*(1 - Math.sqrt(Tr)))**2;
                    a_eff = a * alpha;
                    A = a_eff * P / (R * T)**2;
                    B = b * P / (R * T);

                    const roots = solveCubic(-1, A - B - B**2, -A*B);
                    Z = Math.max(...roots);

                    return Math.exp(Z - 1 - Math.log(Z - B) - (A/B) * Math.log(1 + B/Z));
                } else {
                    a = 0.45724 * R**2 * Tc**2 / Pc;
                    b = 0.07780 * R * Tc / Pc;
                    m = 0.37464 + 1.54226*omega - 0.26992*omega**2;
                    alpha = (1 + m*(1 - Math.sqrt(Tr)))**2;
                    a_eff = a * alpha;
                    A = a_eff * P / (R * T)**2;
                    B = b * P / (R * T);

                    const roots = solveCubic(-(1-B), A - 3*B**2 - 2*B, -(A*B - B**2 - B**3));
                    Z = Math.max(...roots);

                    return Math.exp(Z - 1 - Math.log(Z - B) - 
                        (A / (2*Math.sqrt(2)*B)) * Math.log((Z + (1+Math.sqrt(2))*B)/(Z + (1-Math.sqrt(2))*B)));
                }
            }

            function solveCubic(p, q, r) {
                const a = p, b = q, c = r;
                const a2 = a*a, Q = (3*b - a2)/9, R = (9*a*b - 27*c - 2*a2*a)/54, D = Q**3 + R**2;
                let roots = [];

                if (D < 0) {
                    const theta = Math.acos(R / Math.sqrt(-Q**3));
                    const sqrtQ = Math.sqrt(-Q);
                    roots = [
                        2*sqrtQ*Math.cos(theta/3) - a/3,
                        2*sqrtQ*Math.cos((theta + 2*Math.PI)/3) - a/3,
                        2*sqrtQ*Math.cos((theta + 4*Math.PI)/3) - a/3
                    ];
                } else if (D === 0) {
                    const root = R < 0 ? Math.pow(-R, 1/3) : -Math.pow(R, 1/3);
                    roots = [2*root - a/3, -root - a/3];
                } else {
                    const S = Math.sign(R) * Math.pow(Math.abs(R) + Math.sqrt(D), 1/3);
                    roots = [S - Q/S - a/3];
                }
                return roots.filter(x => !isNaN(x));
            }

            updatePlot();
        });
    </script>
</body>
</html>
