<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>Chemical Visualization Demo</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <style>
        body {
            margin: 0;
            font-family: Arial, sans-serif;
            display: flex;
            flex-direction: column;
            align-items: center;
        }
        #demo-container {
            width: 100%;
            max-width: 1200px;
            margin: 0 auto;
        }
        #mode-selector {
            display: flex;
            background: #f0f0f0;
            border-bottom: 2px solid #ccc;
        }
        .mode-button {
            padding: 12px 20px;
            cursor: pointer;
            border-bottom: 3px solid transparent;
            transition: all 0.3s;
        }
        .mode-button.active {
            border-bottom: 3px solid #007bff;
            font-weight: bold;
        }
        #control-panel {
            display: flex;
            flex-wrap: wrap;
            gap: 15px;
            padding: 10px;
            background: #fafafa;
            border-bottom: 1px solid #eee;
        }
        .control-group {
            display: flex;
            align-items: center;
            gap: 5px;
        }
        #canvas-container {
            width: 100%;
            height: 600px;
            position: relative;
        }
        canvas {
            display: block;
        }
        /* Hide controls by default */
        .control-group {
            display: none;
        }
        /* Show specific controls based on mode */
        .mode-da .control-group[data-control="focus"],
        .mode-da .control-group[data-control="reaction"],
        .mode-regio .control-group,
        .mode-stereo-phile .control-group[data-control="focus"],
        .mode-stereo-diene .control-group[data-control="focus"],
        .mode-homo .control-group[data-control="inverse"],
        .mode-homo .control-group[data-control="erg-ewg"] {
            display: flex;
        }
    </style>
</head>
<body>
    <div id="demo-container">
        <div id="mode-selector">
            <div id="btn-mode-da" class="mode-button active" data-mode="da">Diels–Alder reaction</div>
            <div id="btn-mode-regio" class="mode-button" data-mode="regio">regioselectivity</div>
            <div id="btn-mode-stereo-phile" class="mode-button" data-mode="stereo-phile">stereospecificity (dienophile)</div>
            <div id="btn-mode-stereo-diene" class="mode-button" data-mode="stereo-diene">stereospecificity (diene)</div>
            <div id="btn-mode-homo" class="mode-button" data-mode="homo">HOMO/LUMO interactions</div>
        </div>
        <div id="control-panel">
            <div class="control-group" id="inverse-container" data-control="inverse">
                <input type="checkbox" id="check-inverse">
                <label for="check-inverse">inverse</label>
            </div>
            <div class="control-group" id="focus-container" data-control="focus">
                <input type="checkbox" id="check-focus">
                <label for="check-focus">focus</label>
            </div>
            <div class="control-group" id="reaction-slider-container" data-control="reaction">
                <label for="slider-reaction">reaction evolution</label>
                <button id="btn-reaction-minus">-</button>
                <input type="range" id="slider-reaction" min="0" max="100" value="0">
                <button id="btn-reaction-plus">+</button>
            </div>
            <div class="control-group" id="erg-ewg-slider-container" data-control="erg-ewg">
                <label for="slider-erg-ewg">ERG/EWG effect intensity</label>
                <button id="btn-erg-ewg-minus">-</button>
                <input type="range" id="slider-erg-ewg" min="0" max="100" value="0">
                <button id="btn-erg-ewg-plus">+</button>
            </div>
        </div>
        <div id="canvas-container">
            <canvas id="viz-canvas"></canvas>
        </div>
    </div>

    <script>
        let state = {
            mode: 'da',
            inverse: false,
            focus: false,
            reaction: 0,
            ergEwg: 0
        };

        function setup() {
            const canvas = createCanvas(800, 600);
            canvas.parent('canvas-container');
            noLoop();
            drawVisualization();
        }

        function draw() {
            drawVisualization();
        }

        function drawVisualization() {
            background(255);
            switch (state.mode) {
                case 'da':
                    drawDielsAlder();
                    break;
                case 'regio':
                    drawRegioselectivity();
                    break;
                case 'stereo-phile':
                case 'stereo-diene':
                    drawStereospecificity();
                    break;
                case 'homo':
                    drawHOMO_LUMO();
                    break;
            }
        }

        function drawDielsAlder() {
            // Draw butadiene and ethylene reacting
            const progress = state.reaction / 100;

            // Butadiene (s-cis)
            const b1 = {x: 100, y: 300};
            const b2 = {x: 200, y: 300};
            const b3 = {x: 300, y: 300};
            const b4 = {x: 400, y: 300};

            // Ethylene
            const e1 = {x: 500 + (1 - progress)*200, y: 300};
            const e2 = {x: 600 + (1 - progress)*200, y: 300};

            // Reaction progress
            if (progress > 0.5) {
                // Transition to cyclohexene
                const p = progress - 0.5;
                b4.x = lerp(400, 500, p*2);
                b4.y = lerp(300, 350, p*2);
                e1.x = lerp(600, 400, p*2);
                e1.y = lerp(300, 350, p*2);
            }

            // Draw butadiene
            drawBond(b1, b2);
            drawBond(b2, b3);
            drawBond(b3, b4);

            // Draw ethylene
            drawBond(e1, e2);

            // Highlight bonds if focus is on
            if (state.focus) {
                if (progress < 0.5) {
                    drawHighlightedBond(b1, b2);
                    drawHighlightedBond(b3, b4);
                    drawHighlightedBond(e1, e2);
                } else {
                    drawHighlightedBond(b2, b3, false);
                    drawHighlightedBond(b4, e1);
                    drawHighlightedBond(b1, e2);
                }
            }
        }

        function drawRegioselectivity() {
            // Draw two reaction paths
            const progress = state.reaction / 100;
            const ergEwg = state.ergEwg / 100;

            // Reactants
            const diene = {x: 100, y: 200};
            const dienophile = {x: 100, y: 400};

            // Ortho product
            const ortho = {
                x: 400 + progress*100,
                y: 200 + (0.5 - ergEwg)*50
            };

            // Meta product
            const meta = {
                x: 400 + progress*100,
                y: 400 + (ergEwg - 0.5)*50
            };

            // Draw reactants
            drawText("1-substituted diene", diene.x, diene.y);
            drawText("substituted dienophile", dienophile.x, dienophile.y);

            // Draw products
            drawText("ortho product", ortho.x, ortho.y);
            drawText("meta product", meta.x, meta.y);

            // Highlight based on ergEwg
            if (state.focus) {
                fill(255, 0, 0, ergEwg*128);
                ellipse(ortho.x, ortho.y, 20);
                fill(255, 0, 0, (1-ergEwg)*128);
                ellipse(meta.x, meta.y, 20);
            }
        }

        function drawStereospecificity() {
            // Draw top/bottom sections
            const isDienophile = state.mode === 'stereo-phile';

            // Top section (cis/trans)
            drawText(isDienophile ? "cis-dienophile" : "trans,cis-diene", 200, 100);
            drawText("meso product", 200, 200);

            // Bottom section (trans)
            drawText(isDienophile ? "trans-dienophile" : "trans,trans-diene", 200, 400);
            drawText("racemic mixture", 200, 500);

            // Highlight bonds if focus is on
            if (state.focus) {
                stroke(255, 0, 0, 128);
                line(150, 100, 250, 100);
                line(150, 400, 250, 400);
            }
        }

        function drawHOMO_LUMO() {
            const ergEwg = state.ergEwg / 100;
            const inverse = state.inverse;

            // Energy levels
            const baseY = 300;
            const levelOffset = inverse ? -ergEwg*50 : ergEwg*50;

            // Diene levels
            drawEnergyLevel(100, baseY - levelOffset, "Diene HOMO");
            drawEnergyLevel(100, baseY - levelOffset - 30, "Diene LUMO");

            // Dienophile levels
            drawEnergyLevel(300, baseY + levelOffset, "Dienophile HOMO");
            drawEnergyLevel(300, baseY + levelOffset - 30, "Dienophile LUMO");

            // Interaction arrows
            const arrowProps = inverse ? {color: 'black', length: 80} : {color: 'red', length: 40};
            drawInteractionArrow(150, baseY - levelOffset - 15, 250, baseY + levelOffset - 45, arrowProps);

            const arrowProps2 = inverse ? {color: 'red', length: 40} : {color: 'black', length: 80};
            drawInteractionArrow(150, baseY - levelOffset - 45, 250, baseY + levelOffset - 15, arrowProps2);
        }

        function drawBond(p1, p2) {
            line(p1.x, p1.y, p2.x, p2.y);
            line(p1.x, p1.y+5, p2.x, p2.y+5);
        }

        function drawHighlightedBond(p1, p2, breaking = true) {
            stroke(255, 0, 0, breaking ? 128 : 255);
            line(p1.x, p1.y, p2.x, p2.y);
            line(p1.x, p1.y+5, p2.x, p2.y+5);
        }

        function drawEnergyLevel(x, y, label) {
            line(x, y, x+60, y);
            line(x, y-10, x, y+10);
            line(x+60, y-10, x+60, y+10);
            drawText(label, x+30, y-15);
        }

        function drawInteractionArrow(x1, y1, x2, y2, props) {
            stroke(props.color);
            strokeWeight(props.length/40);
            line(x1, y1, x2, y2);
            const angle = atan2(y2 - y1, x2 - x1);
            line(x2, y2, x2 - props.length*cos(angle), y2 - props.length*sin(angle));
            line(x2, y2, x2 - props.length*cos(angle - PI/6), y2 - props.length*sin(angle - PI/6));
        }

        function drawText(text, x, y) {
            fill(0);
            noStroke();
            textSize(14);
            textAlign(CENTER, CENTER);
            text(text, x, y);
        }

        // Event Listeners
        document.querySelectorAll('.mode-button').forEach(btn => {
            btn.addEventListener('click', () => {
                document.querySelectorAll('.mode-button').forEach(b => b.classList.remove('active'));
                btn.classList.add('active');
                state.mode = btn.dataset.mode;
                updateControls();
                redraw();
            });
        });

        document.getElementById('check-inverse').addEventListener('change', (e) => {
            state.inverse = e.target.checked;
            redraw();
        });

        document.getElementById('check-focus').addEventListener('change', (e) => {
            state.focus = e.target.checked;
            redraw();
        });

        function setupSlider(id, stateKey) {
            const slider = document.getElementById(id);
            const minus = document.getElementById(`btn-${id}-minus`);
            const plus = document.getElementById(`btn-${id}-plus`);

            slider.addEventListener('input', () => {
                state[stateKey] = parseInt(slider.value);
                redraw();
            });

            minus.addEventListener('click', () => {
                slider.value = Math.max(parseInt(slider.min), parseInt(slider.value) - 1);
                state[stateKey] = parseInt(slider.value);
                redraw();
            });

            plus.addEventListener('click', () => {
                slider.value = Math.min(parseInt(slider.max), parseInt(slider.value) + 1);
                state[stateKey] = parseInt(slider.value);
                redraw();
            });
        }

        setupSlider('slider-reaction', 'reaction');
        setupSlider('slider-erg-ewg', 'ergEwg');

        function updateControls() {
            const mode = state.mode;
            document.querySelectorAll('.control-group').forEach(group => {
                group.style.display = 'none';
            });

            if (mode === 'da') {
                document.querySelector('.control-group[data-control="focus"]').style.display = 'flex';
                document.querySelector('.control-group[data-control="reaction"]').style.display = 'flex';
            } else if (mode === 'regio') {
                document.querySelectorAll('.control-group').forEach(group => {
                    group.style.display = 'flex';
                });
            } else if (mode === 'stereo-phile' || mode === 'stereo-diene') {
                document.querySelector('.control-group[data-control="focus"]').style.display = 'flex';
            } else if (mode === 'homo') {
                document.querySelector('.control-group[data-control="inverse"]').style.display = 'flex';
                document.querySelector('.control-group[data-control="erg-ewg"]').style.display = 'flex';
            }
        }

        function redraw() {
            redraw();
        }
    </script>
</body>
</html>
